      setIsLoading(false);
    }
  };

  const mockUpdateProfile = async (updates: Partial<MockUser>) => {
    setIsLoading(true);
    setError(null);

    try {
      await new Promise(resolve => setTimeout(resolve, 100));
      setUser(prev => prev ? { ...prev, ...updates } : null);
    } catch (err: any) {
      setError(err.message);
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div data-testid="auth-container">
      {/* View Toggle Buttons */}
      {!user && (
        <div data-testid="view-toggle" className="view-toggle">
