            onClick={() => setCurrentView('login')}
            data-testid="show-login-button"
            className={currentView === 'login' ? 'active' : ''}
          >
            ç™»å½•
          </button>
          <button 
            onClick={() => setCurrentView('register')}
            data-testid="show-register-button"
            className={currentView === 'register' ? 'active' : ''}
          >
            æ³¨å†Œ
          </button>
        </div>
      )}

      {/* Auth Forms */}
      {!user && currentView === 'login' && (
        <MockLoginForm
          onLogin={mockSignIn}
          onSocialLogin={mockSocialLogin}
          onForgotPassword={mockForgotPassword}
          isLoading={isLoading}
          error={error}
        />
      )}

      {!user && currentView === 'register' && (
        <MockRegisterForm
          onRegister={mockSignUp}
          onSocialLogin={mockSocialLogin}
          isLoading={isLoading}
          error={error}
        />
      )}

      {/* User Profile */}
