"""
Expanded Utility Function Tests
Tests validation, string processing, and helper functions for increased coverage
"""
import pytest
import sys
import os
from datetime import datetime, date

# Add backend to path for imports
sys.path.insert(0, os.path.join(os.path.dirname(__file__), '..', '..'))

from app.services.fortune_service import FortuneService
from app.services.visitor_permission_service import VisitorPermissionService


class TestDataValidationUtilities:
    """Test data validation utility functions"""

    def setup_method(self):
        self.fortune_service = FortuneService()

    def test_birth_date_validation(self):
        """Test birth date validation utility"""
        # Valid dates
        valid_dates = [
            "1990-06-15",
            "2000-01-01", 
            "1975-12-31",
            "1985-02-28"
        ]

        for date_str in valid_dates:
            # Should not raise exception for valid dates
            try:
                year, month, day = map(int, date_str.split('-'))
                # Basic validation
                assert 1900 <= year <= 2100
                assert 1 <= month <= 12
                assert 1 <= day <= 31
            except ValueError:
                pytest.fail(f"Valid date {date_str} failed validation")

    def test_birth_time_validation(self):
        """Test birth time validation utility"""
        valid_times = [
            "00:00", "12:00", "23:59", "06:30", "18:45"
        ]

        invalid_times = [
            "24:00", "12:60", "25:30", "-01:00", "12:99"
        ]

        for time_str in valid_times:
            try:
                hour, minute = map(int, time_str.split(':'))
                assert 0 <= hour <= 23
                assert 0 <= minute <= 59
