/**
 * Visitor Permission Manager Tests
 * Tests for permission management and access control utilities
 */

import { VisitorPermissionManager } from '../visitorPermissionManager';

// Mock localStorage
const localStorageMock = (() => {
  let store: { [key: string]: string } = {};

  return {
    getItem: jest.fn((key: string) => store[key] || null),
    setItem: jest.fn((key: string, value: string) => {
      store[key] = value;
    }),
    removeItem: jest.fn((key: string) => {
      delete store[key];
    }),
    clear: jest.fn(() => {
      store = {};
    }),
  };
})();

Object.defineProperty(window, 'localStorage', {
  value: localStorageMock,
});

describe('VisitorPermissionManager', () => {
  let permissionManager: VisitorPermissionManager;

  beforeEach(() => {
    jest.clearAllMocks();
    localStorageMock.clear();
    permissionManager = new VisitorPermissionManager();
  });

  describe('Initialization', () => {
    it('creates instance successfully', () => {
      expect(permissionManager).toBeInstanceOf(VisitorPermissionManager);
    });

    it('has default permission levels', () => {
      const levels = permissionManager.getAvailablePermissionLevels();
      expect(levels).toContain('visitor');
      expect(levels).toContain('free');
      expect(levels).toContain('premium');
    });
  });

  describe('Permission Level Management', () => {
    it('gets current permission level', () => {
      const level = permissionManager.getCurrentPermissionLevel();
      expect(typeof level).toBe('string');
    });

    it('sets permission level', () => {
      permissionManager.setPermissionLevel('premium');
      const level = permissionManager.getCurrentPermissionLevel();
      expect(level).toBe('premium');
    });

    it('validates permission levels', () => {
      expect(permissionManager.isValidPermissionLevel('visitor')).toBe(true);
      expect(permissionManager.isValidPermissionLevel('free')).toBe(true);
      expect(permissionManager.isValidPermissionLevel('premium')).toBe(true);
      expect(permissionManager.isValidPermissionLevel('invalid')).toBe(false);
    });

    it('rejects invalid permission levels', () => {
      const initialLevel = permissionManager.getCurrentPermissionLevel();
      permissionManager.setPermissionLevel('invalid_level');
      const currentLevel = permissionManager.getCurrentPermissionLevel();
      expect(currentLevel).toBe(initialLevel); // Should not change
    });
  });

  describe('Feature Access Control', () => {
    it('checks feature access for visitor level', () => {
      permissionManager.setPermissionLevel('visitor');

      expect(permissionManager.canAccessFeature('basic_chart')).toBe(true);
      expect(permissionManager.canAccessFeature('ai_analysis')).toBe(false);
      expect(permissionManager.canAccessFeature('advanced_features')).toBe(false);
    });

    it('checks feature access for free level', () => {
      permissionManager.setPermissionLevel('free');

      expect(permissionManager.canAccessFeature('basic_chart')).toBe(true);
      expect(permissionManager.canAccessFeature('ai_analysis')).toBe(true);
      expect(permissionManager.canAccessFeature('advanced_features')).toBe(false);
    });

    it('checks feature access for premium level', () => {
      permissionManager.setPermissionLevel('premium');

      expect(permissionManager.canAccessFeature('basic_chart')).toBe(true);
      expect(permissionManager.canAccessFeature('ai_analysis')).toBe(true);
      expect(permissionManager.canAccessFeature('advanced_features')).toBe(true);
    });

    it('handles unknown features gracefully', () => {
      permissionManager.setPermissionLevel('premium');
      expect(permissionManager.canAccessFeature('unknown_feature')).toBe(false);
    });
  });

  describe('Usage Limits', () => {
    it('gets usage limits for visitor level', () => {
      permissionManager.setPermissionLevel('visitor');
      const limits = permissionManager.getUsageLimits();

      expect(limits.daily_charts).toBe(3);
      expect(limits.ai_analysis).toBe(0);
    });

    it('gets usage limits for free level', () => {
      permissionManager.setPermissionLevel('free');
      const limits = permissionManager.getUsageLimits();

      expect(limits.daily_charts).toBe(10);
      expect(limits.ai_analysis).toBe(2);
    });

    it('gets usage limits for premium level', () => {
      permissionManager.setPermissionLevel('premium');
      const limits = permissionManager.getUsageLimits();

      expect(limits.daily_charts).toBe(100);
      expect(limits.ai_analysis).toBe(50);
    });

    it('tracks usage correctly', () => {
      permissionManager.setPermissionLevel('visitor');

      expect(permissionManager.getCurrentUsage('daily_charts')).toBe(0);

      permissionManager.incrementUsage('daily_charts');
      expect(permissionManager.getCurrentUsage('daily_charts')).toBe(1);

      permissionManager.incrementUsage('daily_charts', 2);
      expect(permissionManager.getCurrentUsage('daily_charts')).toBe(3);
    });

    it('checks if usage limit is reached', () => {
      permissionManager.setPermissionLevel('visitor');

      expect(permissionManager.isUsageLimitReached('daily_charts')).toBe(false);

      // Use up the limit (3 for visitor)
      permissionManager.incrementUsage('daily_charts', 3);
      expect(permissionManager.isUsageLimitReached('daily_charts')).toBe(true);
    });

    it('prevents usage increment beyond limits', () => {
      permissionManager.setPermissionLevel('visitor');

      // Try to increment beyond limit
      permissionManager.incrementUsage('daily_charts', 5);
      expect(permissionManager.getCurrentUsage('daily_charts')).toBe(3); // Should cap at limit
    });
  });

  describe('Data Persistence', () => {
    it('persists permission level to localStorage', () => {
      permissionManager.setPermissionLevel('premium');

      expect(localStorageMock.setItem).toHaveBeenCalledWith(
        expect.stringContaining('permission'),
        expect.stringContaining('premium')
      );
    });

    it('persists usage data to localStorage', () => {
      permissionManager.incrementUsage('daily_charts');

      expect(localStorageMock.setItem).toHaveBeenCalledWith(
        expect.stringContaining('usage'),
        expect.any(String)
      );
    });

    it('loads data from localStorage on initialization', () => {
      // Pre-populate localStorage
      localStorageMock.setItem('visitor_permission_level', 'premium');
      localStorageMock.setItem('visitor_usage_data', JSON.stringify({
        daily_charts: 5,
        ai_analysis: 2
      }));

      // Create new instance (should load from localStorage)
      const newManager = new VisitorPermissionManager();

      expect(newManager.getCurrentPermissionLevel()).toBe('premium');
      expect(newManager.getCurrentUsage('daily_charts')).toBe(5);
      expect(newManager.getCurrentUsage('ai_analysis')).toBe(2);
    });
  });

  describe('Usage Reset', () => {
    it('resets daily usage', () => {
      permissionManager.incrementUsage('daily_charts', 5);
      permissionManager.incrementUsage('ai_analysis', 3);

      expect(permissionManager.getCurrentUsage('daily_charts')).toBe(5);
      expect(permissionManager.getCurrentUsage('ai_analysis')).toBe(3);

      permissionManager.resetDailyUsage();

      expect(permissionManager.getCurrentUsage('daily_charts')).toBe(0);
      expect(permissionManager.getCurrentUsage('ai_analysis')).toBe(0);
    });

    it('resets specific usage type', () => {
      permissionManager.incrementUsage('daily_charts', 5);
      permissionManager.incrementUsage('ai_analysis', 3);

      permissionManager.resetUsage('daily_charts');

      expect(permissionManager.getCurrentUsage('daily_charts')).toBe(0);
      expect(permissionManager.getCurrentUsage('ai_analysis')).toBe(3); // Should remain
    });
  });

  describe('Permission Hierarchy', () => {
    it('validates permission hierarchy correctly', () => {
      expect(permissionManager.hasPermissionLevel('visitor', 'visitor')).toBe(true);
      expect(permissionManager.hasPermissionLevel('free', 'visitor')).toBe(true);
      expect(permissionManager.hasPermissionLevel('premium', 'visitor')).toBe(true);
      expect(permissionManager.hasPermissionLevel('premium', 'free')).toBe(true);

      expect(permissionManager.hasPermissionLevel('visitor', 'free')).toBe(false);
      expect(permissionManager.hasPermissionLevel('visitor', 'premium')).toBe(false);
      expect(permissionManager.hasPermissionLevel('free', 'premium')).toBe(false);
    });
  });

  describe('Error Handling', () => {
    it('handles localStorage errors gracefully', () => {
      // Mock localStorage to throw error
      localStorageMock.setItem.mockImplementation(() => {
        throw new Error('Storage quota exceeded');
      });

      // Should not throw error
      expect(() => {
        permissionManager.setPermissionLevel('premium');
      }).not.toThrow();
    });

    it('handles corrupted localStorage data', () => {
      // Set corrupted data
      localStorageMock.setItem('visitor_usage_data', 'invalid json');

      // Should not throw error and use defaults
      const newManager = new VisitorPermissionManager();
      expect(newManager.getCurrentUsage('daily_charts')).toBe(0);
    });
  });

  describe('Utility Methods', () => {
    it('gets permission level display name', () => {
      expect(permissionManager.getPermissionLevelDisplayName('visitor')).toBe('è®¿å®¢');
      expect(permissionManager.getPermissionLevelDisplayName('free')).toBe('å…è´¹ç”¨æˆ·');
      expect(permissionManager.getPermissionLevelDisplayName('premium')).toBe('é«˜çº§ç”¨æˆ·');
      expect(permissionManager.getPermissionLevelDisplayName('unknown')).toBe('æœªçŸ¥');
    });

    it('gets feature description', () => {
      expect(permissionManager.getFeatureDescription('basic_chart')).toContain('åŸºæœ¬å‘½ç›˜');
      expect(permissionManager.getFeatureDescription('ai_analysis')).toContain('AIåˆ†æž');
      expect(permissionManager.getFeatureDescription('advanced_features')).toContain('é«˜çº§åŠŸèƒ½');
    });

    it('exports usage statistics', () => {
      permissionManager.incrementUsage('daily_charts', 5);
      permissionManager.incrementUsage('ai_analysis', 2);

      const stats = permissionManager.getUsageStatistics();

      expect(stats).toEqual({
        daily_charts: { used: 5, limit: 3, percentage: 100 },
        ai_analysis: { used: 2, limit: 0, percentage: 100 }
      });
    });
  });
});

