    overallFortune: { level: 'good', score: 75, description: 'æ•´é«”é‹å‹¢è‰¯å¥½' },
    majorStars: ['ç´«å¾®'],
    minorStars: ['æ–‡æ˜Œ'],
    getPalaceData: jest.fn(() => null),
    lifePalace: null,
    bodyPalace: null
  })
}));

jest.mock('../ZiWeiChart/hooks/usePalaceInteraction', () => ({
  usePalaceInteraction: () => ({
    selectedPalaceIndex: null,
    hoveredPalaceIndex: null,
    isSidebarOpen: false,
    handlePalaceClick: jest.fn(),
    handlePalaceHover: jest.fn(),
    closeSidebar: jest.fn()
  })
}));

import ZiWeiChart from '../ZiWeiChart';
import { ZiWeiChartData } from '../ZiWeiChart/types';

describe('ZiWeiChart Component', () => {
  const mockChartData: ZiWeiChartData = {
    palaces: [
      {
        name: 'å‘½å®«',
        majorStars: [
          { name: 'ç´«å¾®', brightness: 'æ—º', influence: 'positive', type: 'ä¸»æ˜Ÿ', transformations: [], strengthModifiers: [] }
