/**
 * Phase 3: ZiWeiChart Component Comprehensive Testing
 * Focus on chart rendering, interaction, and data visualization
 */

import React from 'react';
import { render, screen, fireEvent, waitFor } from '@testing-library/react';
import '@testing-library/jest-dom';

// Mock data structures for testing
interface MockPalace {
  name: string;
  position: number;
  majorStars: Array<{ name: string; brightness?: string }>;
  minorStars: Array<{ name: string }>;
  score: number;
  fortuneLevel: 'excellent' | 'good' | 'neutral' | 'bad' | 'terrible';
  branch?: string;
  element?: string;
}

interface MockChartData {
  palaces: MockPalace[];
  birthInfo: {
    name: string;
    solarDate: string;
    lunarDate: string;
    gender: string;
  };
  overallFortune: {
    level: string;
    score: number;
    description: string;
  };
}

// Mock ZiWeiChart component for testing
const MockZiWeiChart: React.FC<{
  chartData?: MockChartData;
  onPalaceClick?: (palace: MockPalace) => void;
  onStarClick?: (star: { name: string }, palace: MockPalace) => void;
  showDetails?: boolean;
  interactive?: boolean;
}> = ({ 
  chartData, 
  onPalaceClick, 
  onStarClick, 
  showDetails = true, 
  interactive = true 
}) => {
  const [selectedPalace, setSelectedPalace] = React.useState<MockPalace | null>(null);
  const [hoveredStar, setHoveredStar] = React.useState<string | null>(null);

  if (!chartData) {
    return (
      <div data-testid="chart-loading" className="chart-loading">
        æ­£åœ¨åŠ è½½å‘½ç›˜...
      </div>
    );
  }

  const handlePalaceClick = (palace: MockPalace) => {
    if (!interactive) return;

    setSelectedPalace(palace);
    onPalaceClick?.(palace);
  };

  const handleStarClick = (star: { name: string }, palace: MockPalace, event: React.MouseEvent) => {
    if (!interactive) return;

    event.stopPropagation();
    onStarClick?.(star, palace);
  };

  const getFortuneColor = (level: string) => {
    const colors = {
      'excellent': '#4ade80',
      'good': '#60a5fa',
      'neutral': '#fbbf24',
      'bad': '#f87171',
      'terrible': '#ef4444'
    };
    return colors[level as keyof typeof colors] || '#6b7280';
  };

  const getPalacePosition = (position: number) => {
    // Simplified position calculation for testing
    const angle = (position * 30) - 90; // 30 degrees per palace, start at top
    const radius = 150;
    const x = 200 + radius * Math.cos(angle * Math.PI / 180);
    const y = 200 + radius * Math.sin(angle * Math.PI / 180);
    return { x, y };
  };

  return (
    <div data-testid="ziwei-chart" className="ziwei-chart">
      {/* Chart Header */}
      <div data-testid="chart-header" className="chart-header">
        <h2>{chartData.birthInfo.name} çš„ç´«å¾®æ–—æ•°å‘½ç›˜</h2>
        <div data-testid="birth-info">
          <span>é˜³åŽ†ï¼š{chartData.birthInfo.solarDate}</span>
          <span>é˜´åŽ†ï¼š{chartData.birthInfo.lunarDate}</span>
          <span>æ€§åˆ«ï¼š{chartData.birthInfo.gender}</span>
        </div>
      </div>

      {/* Chart SVG */}
      <svg 
        data-testid="chart-svg" 
        width="400" 
        height="400" 
        className="chart-svg"
      >
        {/* Chart background */}
        <circle 
          cx="200" 
          cy="200" 
          r="180" 
          fill="none" 
          stroke="#e5e7eb" 
          strokeWidth="2"
          data-testid="chart-background"
        />

        {/* Palace sections */}
        {chartData.palaces.map((palace, index) => {
          const position = getPalacePosition(palace.position);
          const isSelected = selectedPalace?.position === palace.position;

          return (
            <g key={palace.position} data-testid={`palace-${palace.position}`}>
              {/* Palace circle */}
              <circle
                cx={position.x}
                cy={position.y}
                r="30"
                fill={getFortuneColor(palace.fortuneLevel)}
                stroke={isSelected ? "#1f2937" : "#6b7280"}
                strokeWidth={isSelected ? "3" : "1"}
                className={`palace-circle ${interactive ? 'cursor-pointer' : ''}`}
                onClick={() => handlePalaceClick(palace)}
                data-testid={`palace-circle-${palace.position}`}
              />

              {/* Palace name */}
              <text
                x={position.x}
                y={position.y - 35}
                textAnchor="middle"
                fontSize="12"
                fill="#374151"
                data-testid={`palace-name-${palace.position}`}
              >
                {palace.name}
              </text>

              {/* Palace score */}
              <text
                x={position.x}
                y={position.y}
                textAnchor="middle"
                fontSize="10"
                fill="#1f2937"
                fontWeight="bold"
                data-testid={`palace-score-${palace.position}`}
              >
                {palace.score}
              </text>

              {/* Major stars */}
              {palace.majorStars.map((star, starIndex) => (
                <text
                  key={`major-${starIndex}`}
                  x={position.x - 25 + starIndex * 12}
                  y={position.y + 15}
                  fontSize="8"
                  fill="#dc2626"
                  className={`major-star ${interactive ? 'cursor-pointer' : ''}`}
                  onClick={(e) => handleStarClick(star, palace, e)}
                  onMouseEnter={() => setHoveredStar(star.name)}
                  onMouseLeave={() => setHoveredStar(null)}
                  data-testid={`major-star-${palace.position}-${starIndex}`}
                >
                  {star.name}
                </text>
              ))}

              {/* Minor stars */}
              {palace.minorStars.map((star, starIndex) => (
                <text
                  key={`minor-${starIndex}`}
                  x={position.x - 25 + starIndex * 12}
                  y={position.y + 25}
                  fontSize="6"
                  fill="#059669"
                  className={`minor-star ${interactive ? 'cursor-pointer' : ''}`}
                  onClick={(e) => handleStarClick(star, palace, e)}
                  onMouseEnter={() => setHoveredStar(star.name)}
                  onMouseLeave={() => setHoveredStar(null)}
                  data-testid={`minor-star-${palace.position}-${starIndex}`}
                >
                  {star.name}
                </text>
              ))}
            </g>
          );
        })}
      </svg>

      {/* Palace Details Panel */}
      {showDetails && selectedPalace && (
        <div data-testid="palace-details" className="palace-details">
          <h3 data-testid="selected-palace-name">{selectedPalace.name}</h3>
          <p data-testid="selected-palace-score">åˆ†æ•°: {selectedPalace.score}</p>
          <p data-testid="selected-palace-level">ç­‰çº§: {selectedPalace.fortuneLevel}</p>

          {selectedPalace.branch && (
            <p data-testid="selected-palace-branch">åœ°æ”¯: {selectedPalace.branch}</p>
          )}

          {selectedPalace.element && (
            <p data-testid="selected-palace-element">äº”è¡Œ: {selectedPalace.element}</p>
          )}

          <div data-testid="selected-palace-stars">
            <h4>ä¸»æ˜Ÿ:</h4>
            <ul>
              {selectedPalace.majorStars.map((star, index) => (
                <li key={index} data-testid={`selected-major-star-${index}`}>
                  {star.name} {star.brightness && `(${star.brightness})`}
                </li>
              ))}
            </ul>

            <h4>è¾…æ˜Ÿ:</h4>
            <ul>
              {selectedPalace.minorStars.map((star, index) => (
                <li key={index} data-testid={`selected-minor-star-${index}`}>
                  {star.name}
                </li>
              ))}
            </ul>
          </div>
        </div>
      )}

      {/* Star Tooltip */}
      {hoveredStar && (
        <div data-testid="star-tooltip" className="star-tooltip">
          {hoveredStar} è¯¦ç»†ä¿¡æ¯
        </div>
      )}

      {/* Overall Fortune Summary */}
      <div data-testid="overall-fortune" className="overall-fortune">
        <h3>æ•´ä½“è¿åŠ¿</h3>
        <p data-testid="fortune-level">ç­‰çº§: {chartData.overallFortune.level}</p>
        <p data-testid="fortune-score">åˆ†æ•°: {chartData.overallFortune.score}</p>
        <p data-testid="fortune-description">{chartData.overallFortune.description}</p>
      </div>
    </div>
  );
};

