"""
API Functionality Tests
Tests API functionality without requiring Flask app
"""

import pytest
import json
from unittest.mock import patch, Mock, MagicMock
import sys
import os

# Add the parent directory to Python path for imports
sys.path.insert(0, os.path.dirname(os.path.dirname(os.path.abspath(__file__))))


class TestDataValidationAPI:
    """Test API data validation functions"""

    def test_birth_data_validation(self):
        """Test birth data validation logic"""
        def validate_birth_data(data):
            """Validate birth data for API"""
            errors = []

            # Required fields
            required_fields = ['name', 'birth_date', 'birth_time', 'gender']
            for field in required_fields:
                if field not in data or not data[field]:
                    errors.append(f"Missing required field: {field}")

            if errors:
                return False, errors

            # Validate gender
            if data['gender'] not in ['male', 'female']:
                errors.append("Gender must be 'male' or 'female'")

            # Validate date format
            try:
                date_parts = data['birth_date'].split('-')
                if len(date_parts) != 3:
                    errors.append("Invalid date format. Use YYYY-MM-DD")
                else:
                    year, month, day = int(date_parts[0]), int(date_parts[1]), int(date_parts[2])
                    if year < 1900 or year > 2100:
                        errors.append("Year must be between 1900 and 2100")
                    if month < 1 or month > 12:
                        errors.append("Month must be between 1 and 12")
                    if day < 1 or day > 31:
                        errors.append("Day must be between 1 and 31")
            except ValueError:
                errors.append("Invalid date values")

            # Validate time format
            try:
                time_parts = data['birth_time'].split(':')
                if len(time_parts) >= 2:
                    hour, minute = int(time_parts[0]), int(time_parts[1])
                    if hour < 0 or hour > 23:
                        errors.append("Hour must be between 0 and 23")
                    if minute < 0 or minute > 59:
                        errors.append("Minute must be between 0 and 59")
                else:
                    errors.append("Invalid time format. Use HH:MM")
            except ValueError:
                errors.append("Invalid time values")

            return len(errors) == 0, errors

        # Test valid data
        valid_data = {
            'name': 'å¼ ä¸‰',
            'birth_date': '1990-06-15',
            'birth_time': '12:30',
            'gender': 'male'
        }

        is_valid, errors = validate_birth_data(valid_data)
        assert is_valid == True
        assert len(errors) == 0

        # Test missing fields
        invalid_data1 = {
            'name': 'å¼ ä¸‰',
            'birth_date': '1990-06-15'
            # Missing birth_time and gender
        }

        is_valid, errors = validate_birth_data(invalid_data1)
        assert is_valid == False
        assert len(errors) >= 2
        assert any('birth_time' in error for error in errors)
        assert any('gender' in error for error in errors)

        # Test invalid gender
        invalid_data2 = {
            'name': 'å¼ ä¸‰',
            'birth_date': '1990-06-15',
            'birth_time': '12:30',
            'gender': 'invalid'
        }

        is_valid, errors = validate_birth_data(invalid_data2)
        assert is_valid == False
        assert any('Gender must be' in error for error in errors)

        # Test invalid date
        invalid_data3 = {
            'name': 'å¼ ä¸‰',
            'birth_date': '1990-13-45',  # Invalid month and day
            'birth_time': '12:30',
            'gender': 'male'
        }

        is_valid, errors = validate_birth_data(invalid_data3)
        assert is_valid == False
        assert len(errors) >= 1

    def test_api_response_formatting(self):
        """Test API response formatting"""
        def format_success_response(data, message="success"):
            """Format successful API response"""
            return {
                'status': 'success',
                'message': message,
                'data': data,
                'timestamp': '2023-01-01T00:00:00Z'  # Mock timestamp
            }

        def format_error_response(error_message, error_code=400):
            """Format error API response"""
            return {
                'status': 'error',
                'error': error_message,
                'error_code': error_code,
                'timestamp': '2023-01-01T00:00:00Z'  # Mock timestamp
            }

        # Test success response
        test_data = {'palaces': [], 'birth_info': {}}
        success_response = format_success_response(test_data)

        assert success_response['status'] == 'success'
        assert success_response['message'] == 'success'
        assert 'data' in success_response
        assert 'timestamp' in success_response

        # Test error response
        error_response = format_error_response("Invalid input data", 400)

        assert error_response['status'] == 'error'
        assert error_response['error'] == "Invalid input data"
        assert error_response['error_code'] == 400
        assert 'timestamp' in error_response


class TestCalculationAPI:
    """Test calculation API functions"""

    def test_chart_calculation_pipeline(self):
        """Test complete chart calculation pipeline"""
        def calculate_chart_api(birth_data):
            """Mock API chart calculation"""
            # Validate input
            if not birth_data.get('name'):
                return {'error': 'Name is required'}

            # Mock calculation process
            palaces = []
            palace_names = ['å‘½å®«', 'å…„å¼Ÿå®«', 'å¤«å¦»å®«', 'å­å¥³å®«', 'è´¢å¸›å®«', 'ç–¾åŽ„å®«',
                           'è¿ç§»å®«', 'å¥´ä»†å®«', 'å®˜ç¦„å®«', 'ç”°å®…å®«', 'ç¦å¾·å®«', 'çˆ¶æ¯å®«']

            for i, name in enumerate(palace_names):
