        assert is_valid == False
        assert any('Gender must be' in error for error in errors)

        # Test invalid date
        invalid_data3 = {
            'name': 'å¼ ä¸‰',
            'birth_date': '1990-13-45',  # Invalid month and day
            'birth_time': '12:30',
            'gender': 'male'
        }

        is_valid, errors = validate_birth_data(invalid_data3)
        assert is_valid == False
        assert len(errors) >= 1

