        assert 'overall_fortune' in api_data
        assert 'metadata' in api_data

        # Check palace transformation
        life_palace = api_data['palaces']['å‘½å®«']
        assert life_palace['name'] == 'å‘½å®«'
        assert life_palace['position'] == 0
        assert 'stars' in life_palace
        assert 'major' in life_palace['stars']
        assert 'minor' in life_palace['stars']
        assert 'analysis' in life_palace

        # Check metadata
        assert 'calculation_time' in api_data['metadata']
        assert 'version' in api_data['metadata']

    def test_input_sanitization(self):
        """Test input data sanitization"""
        def sanitize_api_input(data):
            """Sanitize API input data"""
            if not isinstance(data, dict):
                return {}

            sanitized = {}

            # Sanitize string fields
            string_fields = ['name', 'birth_date', 'birth_time', 'gender']
            for field in string_fields:
                if field in data:
                    value = str(data[field]).strip()
                    # Remove potentially dangerous characters
                    value = ''.join(c for c in value if c.isprintable())
                    # Limit length
                    if field == 'name':
                        value = value[:50]
                    elif field in ['birth_date', 'birth_time']:
                        value = value[:20]
                    elif field == 'gender':
                        value = value.lower()[:10]

                    sanitized[field] = value

            # Sanitize calendar type
            if 'calendar_type' in data:
                calendar_type = str(data['calendar_type']).lower().strip()
                if calendar_type in ['solar', 'lunar']:
                    sanitized['calendar_type'] = calendar_type
                else:
                    sanitized['calendar_type'] = 'solar'  # Default

            return sanitized

        # Test normal input
        input_data = {
            'name': 'å¼ ä¸‰',
            'birth_date': '1990-06-15',
            'birth_time': '12:30',
            'gender': 'MALE',
            'calendar_type': 'Solar'
        }

        sanitized = sanitize_api_input(input_data)

        assert sanitized['name'] == 'å¼ ä¸‰'
        assert sanitized['birth_date'] == '1990-06-15'
        assert sanitized['gender'] == 'male'  # Lowercase
        assert sanitized['calendar_type'] == 'solar'  # Lowercase

        # Test input with extra spaces and invalid characters
        messy_input = {
            'name': '  å¼ ä¸‰\t  ',
            'birth_date': ' 1990-06-15 ',
            'gender': ' Female ',
            'calendar_type': 'INVALID'
        }

        sanitized = sanitize_api_input(messy_input)

        assert sanitized['name'] == 'å¼ ä¸‰'
        assert sanitized['birth_date'] == '1990-06-15'
        assert sanitized['gender'] == 'female'
        assert sanitized['calendar_type'] == 'solar'  # Default for invalid

        # Test very long name
        long_input = {
            'name': 'A' * 100,  # Very long name
            'birth_date': '1990-06-15'
        }

        sanitized = sanitize_api_input(long_input)
        assert len(sanitized['name']) <= 50  # Should be truncated

