/**
 * Unified Auth Service Tests
 * Tests for the unified authentication service
 */

import { jest } from '@jest/globals';

// Mock Supabase client
const mockSupabaseClient = {
  auth: {
    signUp: jest.fn(),
    signInWithPassword: jest.fn(),
    signInWithOAuth: jest.fn(),
    signOut: jest.fn(),
    getSession: jest.fn(),
    getUser: jest.fn(),
    onAuthStateChange: jest.fn(),
  },
  from: jest.fn(() => ({
    select: jest.fn(() => ({
      eq: jest.fn(() => ({
        single: jest.fn(),
      })),
    })),
    insert: jest.fn(),
    update: jest.fn(() => ({
      eq: jest.fn(),
    })),
  })),
};

jest.mock('../supabaseClient', () => ({
  supabase: mockSupabaseClient,
}));

describe('UnifiedAuthService', () => {
  beforeEach(() => {
    jest.clearAllMocks();
  });

  describe('Authentication Functions', () => {
    it('handles sign up with email and password', async () => {
      const mockSignUpResponse = {
        data: {
          user: {
            id: 'test-user-id',
            email: 'test@example.com',
          },
          session: {
            access_token: 'test-token',
            user: { id: 'test-user-id' },
          },
        },
        error: null,
      };

      mockSupabaseClient.auth.signUp.mockResolvedValue(mockSignUpResponse);

      // Mock the auth service
      const signUpUser = async (email: string, password: string) => {
        const response = await mockSupabaseClient.auth.signUp({
          email,
          password,
        });

        if (response.error) {
          throw new Error(response.error.message);
        }

        return {
          user: response.data.user,
          session: response.data.session,
        };
      };

      const result = await signUpUser('test@example.com', 'password123');

      expect(mockSupabaseClient.auth.signUp).toHaveBeenCalledWith({
        email: 'test@example.com',
        password: 'password123',
      });

      expect(result.user.id).toBe('test-user-id');
      expect(result.user.email).toBe('test@example.com');
    });

    it('handles sign in with email and password', async () => {
      const mockSignInResponse = {
        data: {
          user: {
            id: 'test-user-id',
            email: 'test@example.com',
          },
          session: {
            access_token: 'test-token',
            user: { id: 'test-user-id' },
          },
        },
        error: null,
      };

      mockSupabaseClient.auth.signInWithPassword.mockResolvedValue(mockSignInResponse);

      const signInUser = async (email: string, password: string) => {
        const response = await mockSupabaseClient.auth.signInWithPassword({
          email,
          password,
        });

        if (response.error) {
          throw new Error(response.error.message);
        }

        return {
          user: response.data.user,
          session: response.data.session,
        };
      };

      const result = await signInUser('test@example.com', 'password123');

      expect(mockSupabaseClient.auth.signInWithPassword).toHaveBeenCalledWith({
        email: 'test@example.com',
        password: 'password123',
      });

      expect(result.user.id).toBe('test-user-id');
    });

    it('handles OAuth sign in', async () => {
      const mockOAuthResponse = {
        data: {
          url: 'https://oauth-provider.com/auth',
          provider: 'google',
        },
        error: null,
      };

      mockSupabaseClient.auth.signInWithOAuth.mockResolvedValue(mockOAuthResponse);

      const signInWithOAuth = async (provider: string) => {
        const response = await mockSupabaseClient.auth.signInWithOAuth({
          provider,
          options: {
            redirectTo: window.location.origin,
          },
        });

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data;
      };

      const result = await signInWithOAuth('google');

      expect(mockSupabaseClient.auth.signInWithOAuth).toHaveBeenCalledWith({
        provider: 'google',
        options: {
          redirectTo: window.location.origin,
        },
      });

      expect(result.provider).toBe('google');
    });

    it('handles sign out', async () => {
      const mockSignOutResponse = {
        error: null,
      };

      mockSupabaseClient.auth.signOut.mockResolvedValue(mockSignOutResponse);

      const signOutUser = async () => {
        const response = await mockSupabaseClient.auth.signOut();

        if (response.error) {
          throw new Error(response.error.message);
        }

        return true;
      };

      const result = await signOutUser();

      expect(mockSupabaseClient.auth.signOut).toHaveBeenCalled();
      expect(result).toBe(true);
    });
  });

  describe('Session Management', () => {
    it('gets current session', async () => {
      const mockSession = {
        access_token: 'test-token',
        user: {
          id: 'test-user-id',
          email: 'test@example.com',
        },
      };

      mockSupabaseClient.auth.getSession.mockResolvedValue({
        data: { session: mockSession },
        error: null,
      });

      const getCurrentSession = async () => {
        const response = await mockSupabaseClient.auth.getSession();

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data.session;
      };

      const session = await getCurrentSession();

      expect(mockSupabaseClient.auth.getSession).toHaveBeenCalled();
      expect(session.user.id).toBe('test-user-id');
    });

    it('gets current user', async () => {
      const mockUser = {
        id: 'test-user-id',
        email: 'test@example.com',
        user_metadata: {
          name: 'Test User',
        },
      };

      mockSupabaseClient.auth.getUser.mockResolvedValue({
        data: { user: mockUser },
        error: null,
      });

      const getCurrentUser = async () => {
        const response = await mockSupabaseClient.auth.getUser();

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data.user;
      };

      const user = await getCurrentUser();

      expect(mockSupabaseClient.auth.getUser).toHaveBeenCalled();
      expect(user.id).toBe('test-user-id');
      expect(user.email).toBe('test@example.com');
    });

    it('handles auth state changes', () => {
      const mockCallback = jest.fn();
      const mockUnsubscribe = jest.fn();

      mockSupabaseClient.auth.onAuthStateChange.mockReturnValue({
        data: { subscription: { unsubscribe: mockUnsubscribe } },
      });

      const subscribeToAuthChanges = (callback: Function) => {
        const { data } = mockSupabaseClient.auth.onAuthStateChange(callback);
        return data.subscription.unsubscribe;
      };

      const unsubscribe = subscribeToAuthChanges(mockCallback);

      expect(mockSupabaseClient.auth.onAuthStateChange).toHaveBeenCalledWith(mockCallback);
      expect(typeof unsubscribe).toBe('function');
    });
  });

  describe('User Profile Management', () => {
    it('creates user profile', async () => {
      const mockProfile = {
        id: 'test-user-id',
        email: 'test@example.com',
        name: 'Test User',
        tier: 'free',
        created_at: new Date().toISOString(),
      };

      const mockInsertResponse = {
        data: [mockProfile],
        error: null,
      };

      mockSupabaseClient.from.mockReturnValue({
        insert: jest.fn().mockResolvedValue(mockInsertResponse),
      });

      const createUserProfile = async (profileData: any) => {
        const response = await mockSupabaseClient
          .from('user_profiles')
          .insert(profileData);

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data[0];
      };

      const profileData = {
        id: 'test-user-id',
        email: 'test@example.com',
        name: 'Test User',
        tier: 'free',
      };

      const result = await createUserProfile(profileData);

      expect(result.id).toBe('test-user-id');
      expect(result.tier).toBe('free');
    });

    it('gets user profile', async () => {
      const mockProfile = {
        id: 'test-user-id',
        email: 'test@example.com',
        name: 'Test User',
        tier: 'premium',
        subscription_status: 'active',
      };

      const mockSelectResponse = {
        data: mockProfile,
        error: null,
      };

      mockSupabaseClient.from.mockReturnValue({
        select: jest.fn().mockReturnValue({
          eq: jest.fn().mockReturnValue({
            single: jest.fn().mockResolvedValue(mockSelectResponse),
          }),
        }),
      });

      const getUserProfile = async (userId: string) => {
        const response = await mockSupabaseClient
          .from('user_profiles')
          .select('*')
          .eq('id', userId)
          .single();

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data;
      };

      const profile = await getUserProfile('test-user-id');

      expect(profile.id).toBe('test-user-id');
      expect(profile.tier).toBe('premium');
      expect(profile.subscription_status).toBe('active');
    });

    it('updates user profile', async () => {
      const mockUpdateResponse = {
        data: [{ id: 'test-user-id', name: 'Updated Name' }],
        error: null,
      };

      mockSupabaseClient.from.mockReturnValue({
        update: jest.fn().mockReturnValue({
          eq: jest.fn().mockResolvedValue(mockUpdateResponse),
        }),
      });

      const updateUserProfile = async (userId: string, updates: any) => {
        const response = await mockSupabaseClient
          .from('user_profiles')
          .update(updates)
          .eq('id', userId);

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data[0];
      };

      const result = await updateUserProfile('test-user-id', { name: 'Updated Name' });

      expect(result.name).toBe('Updated Name');
    });
  });

  describe('Error Handling', () => {
    it('handles sign up errors', async () => {
      const mockError = {
        message: 'Email already exists',
        status: 400,
      };

      mockSupabaseClient.auth.signUp.mockResolvedValue({
        data: { user: null, session: null },
        error: mockError,
      });

      const signUpUser = async (email: string, password: string) => {
        const response = await mockSupabaseClient.auth.signUp({
          email,
          password,
        });

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data;
      };

      await expect(signUpUser('test@example.com', 'password123')).rejects.toThrow('Email already exists');
    });

    it('handles sign in errors', async () => {
      const mockError = {
        message: 'Invalid credentials',
        status: 401,
      };

      mockSupabaseClient.auth.signInWithPassword.mockResolvedValue({
        data: { user: null, session: null },
        error: mockError,
      });

      const signInUser = async (email: string, password: string) => {
        const response = await mockSupabaseClient.auth.signInWithPassword({
          email,
          password,
        });

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data;
      };

      await expect(signInUser('test@example.com', 'wrongpassword')).rejects.toThrow('Invalid credentials');
    });

    it('handles database errors', async () => {
      const mockError = {
        message: 'Database connection failed',
        code: 'PGRST301',
      };

      mockSupabaseClient.from.mockReturnValue({
        select: jest.fn().mockReturnValue({
          eq: jest.fn().mockReturnValue({
            single: jest.fn().mockResolvedValue({
              data: null,
              error: mockError,
            }),
          }),
        }),
      });

      const getUserProfile = async (userId: string) => {
        const response = await mockSupabaseClient
          .from('user_profiles')
          .select('*')
          .eq('id', userId)
          .single();

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data;
      };

      await expect(getUserProfile('test-user-id')).rejects.toThrow('Database connection failed');
    });
  });

  describe('Utility Functions', () => {
    it('validates email format', () => {
      const validateEmail = (email: string): boolean => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
        return emailRegex.test(email);
      };

      expect(validateEmail('test@example.com')).toBe(true);
      expect(validateEmail('user.name+tag@domain.co.uk')).toBe(true);
      expect(validateEmail('invalid.email')).toBe(false);
      expect(validateEmail('test@')).toBe(false);
      expect(validateEmail('@example.com')).toBe(false);
    });

    it('validates password strength', () => {
      const validatePassword = (password: string): { isValid: boolean; errors: string[] } => {
        const errors: string[] = [];

        if (password.length < 8) {
          errors.push('Password must be at least 8 characters long');
        }

        if (!/[A-Z]/.test(password)) {
          errors.push('Password must contain at least one uppercase letter');
        }

        if (!/[a-z]/.test(password)) {
          errors.push('Password must contain at least one lowercase letter');
        }

        if (!/\d/.test(password)) {
          errors.push('Password must contain at least one number');
        }

        return {
          isValid: errors.length === 0,
          errors,
        };
      };

      const strongPassword = validatePassword('StrongPass123');
      expect(strongPassword.isValid).toBe(true);
      expect(strongPassword.errors).toHaveLength(0);

      const weakPassword = validatePassword('weak');
      expect(weakPassword.isValid).toBe(false);
      expect(weakPassword.errors.length).toBeGreaterThan(0);
    });

    it('formats user display name', () => {
      const formatDisplayName = (user: any): string => {
        if (user.user_metadata?.name) {
          return user.user_metadata.name;
        }

        if (user.user_metadata?.full_name) {
          return user.user_metadata.full_name;
        }

        if (user.email) {
          return user.email.split('@')[0];
        }

        return 'User';
      };

      const userWithName = {
        email: 'test@example.com',
        user_metadata: { name: 'John Doe' },
      };
      expect(formatDisplayName(userWithName)).toBe('John Doe');

      const userWithFullName = {
        email: 'test@example.com',
        user_metadata: { full_name: 'Jane Smith' },
      };
      expect(formatDisplayName(userWithFullName)).toBe('Jane Smith');

      const userWithEmail = {
        email: 'test@example.com',
        user_metadata: {},
      };
      expect(formatDisplayName(userWithEmail)).toBe('test');

      const userMinimal = {
        user_metadata: {},
      };
      expect(formatDisplayName(userMinimal)).toBe('User');
    });
  });
});

