        email: 'test@example.com',
        name: 'Test User',
        tier: 'free',
      };

      const result = await createUserProfile(profileData);

      expect(result.id).toBe('test-user-id');
      expect(result.tier).toBe('free');
    });

    it('gets user profile', async () => {
      const mockProfile = {
        id: 'test-user-id',
        email: 'test@example.com',
        name: 'Test User',
        tier: 'premium',
        subscription_status: 'active',
      };

      const mockSelectResponse = {
        data: mockProfile,
        error: null,
      };

      mockSupabaseClient.from.mockReturnValue({
        select: jest.fn().mockReturnValue({
          eq: jest.fn().mockReturnValue({
            single: jest.fn().mockResolvedValue(mockSelectResponse),
          }),
        }),
      });

      const getUserProfile = async (userId: string) => {
        const response = await mockSupabaseClient
          .from('user_profiles')
          .select('*')
          .eq('id', userId)
          .single();

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data;
      };

      const profile = await getUserProfile('test-user-id');

      expect(profile.id).toBe('test-user-id');
      expect(profile.tier).toBe('premium');
      expect(profile.subscription_status).toBe('active');
    });

    it('updates user profile', async () => {
      const mockUpdateResponse = {
        data: [{ id: 'test-user-id', name: 'Updated Name' }],
        error: null,
      };

      mockSupabaseClient.from.mockReturnValue({
        update: jest.fn().mockReturnValue({
          eq: jest.fn().mockResolvedValue(mockUpdateResponse),
        }),
      });

      const updateUserProfile = async (userId: string, updates: any) => {
        const response = await mockSupabaseClient
          .from('user_profiles')
          .update(updates)
          .eq('id', userId);

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data[0];
      };

      const result = await updateUserProfile('test-user-id', { name: 'Updated Name' });

      expect(result.name).toBe('Updated Name');
    });
  });

  describe('Error Handling', () => {
    it('handles sign up errors', async () => {
      const mockError = {
        message: 'Email already exists',
        status: 400,
      };

      mockSupabaseClient.auth.signUp.mockResolvedValue({
        data: { user: null, session: null },
        error: mockError,
      });

      const signUpUser = async (email: string, password: string) => {
        const response = await mockSupabaseClient.auth.signUp({
          email,
          password,
        });

        if (response.error) {
          throw new Error(response.error.message);
        }

        return response.data;
      };

      await expect(signUpUser('test@example.com', 'password123')).rejects.toThrow('Email already exists');
