  describe('Error Handling', () => {
    it('handles network errors', async () => {
      (fetch as jest.MockedFunction<typeof fetch>).mockRejectedValueOnce(
        new Error('Network error')
      );

      const makeRequest = async () => {
        try {
          const response = await fetch('/api/test');
          return await response.json();
        } catch (error) {
          throw new Error(`Network error: ${error instanceof Error ? error.message : 'Unknown error'}`);
        }
      };

      await expect(makeRequest()).rejects.toThrow('Network error: Network error');
    });

    it('handles timeout errors', async () => {
      const timeoutController = new AbortController();
      setTimeout(() => timeoutController.abort(), 100);

      (fetch as jest.MockedFunction<typeof fetch>).mockRejectedValueOnce(
        new Error('The operation was aborted')
      );

      const makeRequestWithTimeout = async () => {
        try {
          const response = await fetch('/api/slow-endpoint', {
            signal: timeoutController.signal,
          });
          return await response.json();
        } catch (error) {
          if (error instanceof Error && error.name === 'AbortError') {
            throw new Error('Request timeout');
          }
          throw error;
        }
      };

      await expect(makeRequestWithTimeout()).rejects.toThrow();
    });

    it('handles malformed JSON responses', async () => {
      (fetch as jest.MockedFunction<typeof fetch>).mockResolvedValueOnce({
        ok: true,
        json: async () => {
          throw new Error('Unexpected token in JSON');
        },
      } as Response);

      const makeRequest = async () => {
        const response = await fetch('/api/test');

        if (!response.ok) {
          throw new Error(`HTTP error! status: ${response.status}`);
        }

        try {
          return await response.json();
        } catch (error) {
          throw new Error('Invalid JSON response from server');
        }
      };

      await expect(makeRequest()).rejects.toThrow('Invalid JSON response from server');
    });
  });

  describe('Request Configuration', () => {
    it('sets correct headers for JSON requests', async () => {
      (fetch as jest.MockedFunction<typeof fetch>).mockResolvedValueOnce({
        ok: true,
        json: async () => ({ success: true }),
      } as Response);

      const apiRequest = async (endpoint: string, data: any) => {
        const response = await fetch(endpoint, {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
            'Accept': 'application/json',
            'X-Requested-With': 'XMLHttpRequest',
          },
          body: JSON.stringify(data),
        });

        return await response.json();
      };

      await apiRequest('/api/test', { test: 'data' });

      expect(fetch).toHaveBeenCalledWith('/api/test', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Accept': 'application/json',
          'X-Requested-With': 'XMLHttpRequest',
        },
        body: JSON.stringify({ test: 'data' }),
      });
    });

    it('handles different HTTP methods', async () => {
      const methods = ['GET', 'POST', 'PUT', 'DELETE'];

      for (const method of methods) {
        (fetch as jest.MockedFunction<typeof fetch>).mockResolvedValueOnce({
          ok: true,
          json: async () => ({ method }),
        } as Response);

        const makeRequest = async (httpMethod: string) => {
          const options: RequestInit = { method: httpMethod };

          if (httpMethod !== 'GET') {
            options.headers = { 'Content-Type': 'application/json' };
            options.body = JSON.stringify({ test: 'data' });
          }

          const response = await fetch('/api/test', options);
          return await response.json();
        };

        const result = await makeRequest(method);
        expect(result.method).toBe(method);
      }

      expect(fetch).toHaveBeenCalledTimes(4);
    });
  });

  describe('Response Validation', () => {
    it('validates response structure', async () => {
      const mockResponse = {
        status: 'success',
        data: { test: 'value' },
        timestamp: '2023-01-01T00:00:00Z'
      };

      (fetch as jest.MockedFunction<typeof fetch>).mockResolvedValueOnce({
        ok: true,
        json: async () => mockResponse,
      } as Response);

      const validateResponse = (response: any) => {
        if (!response.status) {
          throw new Error('Response missing status field');
        }

        if (response.status === 'error' && !response.error) {
          throw new Error('Error response missing error field');
        }

        if (response.status === 'success' && !response.data) {
          throw new Error('Success response missing data field');
        }

        return true;
      };

      const makeValidatedRequest = async () => {
        const response = await fetch('/api/test');
        const data = await response.json();

        validateResponse(data);

        return data;
      };

      const result = await makeValidatedRequest();

      expect(result.status).toBe('success');
      expect(result.data).toBeDefined();
    });

    it('handles missing required fields', async () => {
      const mockInvalidResponse = {
        // Missing status field
        data: { test: 'value' }
      };

      (fetch as jest.MockedFunction<typeof fetch>).mockResolvedValueOnce({
        ok: true,
        json: async () => mockInvalidResponse,
      } as Response);

      const validateResponse = (response: any) => {
        if (!response.status) {
          throw new Error('Response missing status field');
        }
        return true;
      };

      const makeValidatedRequest = async () => {
        const response = await fetch('/api/test');
        const data = await response.json();

        validateResponse(data);

        return data;
      };

      await expect(makeValidatedRequest()).rejects.toThrow('Response missing status field');
    });
  });

  describe('API Utility Functions', () => {
    it('builds query strings correctly', () => {
      const buildQueryString = (params: Record<string, any>): string => {
        const searchParams = new URLSearchParams();

        Object.entries(params).forEach(([key, value]) => {
          if (value !== null && value !== undefined) {
            searchParams.append(key, String(value));
          }
        });

        return searchParams.toString();
      };

      const params = {
        page: 1,
        limit: 10,
        search: 'test',
        active: true,
        empty: null,
        undefined: undefined
      };

      const queryString = buildQueryString(params);

      expect(queryString).toContain('page=1');
      expect(queryString).toContain('limit=10');
      expect(queryString).toContain('search=test');
      expect(queryString).toContain('active=true');
      expect(queryString).not.toContain('empty');
      expect(queryString).not.toContain('undefined');
    });

    it('handles API base URL configuration', () => {
      const getApiUrl = (endpoint: string, baseUrl?: string): string => {
        const base = baseUrl || process.env.REACT_APP_API_URL || '/api';
        return `${base.replace(/\/$/, '')}/${endpoint.replace(/^\//, '')}`;
      };

      expect(getApiUrl('test')).toBe('/api/test');
      expect(getApiUrl('/test')).toBe('/api/test');
      expect(getApiUrl('test', 'https://api.example.com')).toBe('https://api.example.com/test');
      expect(getApiUrl('/test', 'https://api.example.com/')).toBe('https://api.example.com/test');
    });

    it('formats error messages consistently', () => {
      const formatApiError = (error: any): string => {
        if (typeof error === 'string') {
          return error;
        }

        if (error?.message) {
          return error.message;
        }

        if (error?.error) {
          return error.error;
        }

        if (error?.data?.error) {
          return error.data.error;
        }

        return 'An unexpected error occurred';
      };

      expect(formatApiError('Simple error')).toBe('Simple error');
      expect(formatApiError({ message: 'Error message' })).toBe('Error message');
      expect(formatApiError({ error: 'API error' })).toBe('API error');
      expect(formatApiError({ data: { error: 'Nested error' } })).toBe('Nested error');
      expect(formatApiError({})).toBe('An unexpected error occurred');
      expect(formatApiError(null)).toBe('An unexpected error occurred');
    });
  });
});
