import Database from 'duckdb'
import { readFile } from 'fs/promises'
import { join, dirname } from 'path'
import { fileURLToPath } from 'url'
import logger from '../utils/logger.js'

const __filename = fileURLToPath(import.meta.url)
const __dirname = dirname(__filename)

class DuckDBConnection {
  private db: Database.Database | null = null
  private connection: Database.Connection | null = null

  async connect(): Promise<void> {
    try {
      // Create DuckDB instance with file storage using absolute path
      const dbPath = join(__dirname, '../../../hesocial.duckdb')
      logger.info(`Connecting to DuckDB at: ${dbPath}`)

      // Force persistent mode by explicitly setting file path
      this.db = new Database.Database(dbPath)
      this.connection = this.db.connect()

      logger.info('Connected to DuckDB database')
    } catch (error) {
      logger.error('Failed to connect to DuckDB:', error)
      throw error
    }
  }

  async query(sql: string, params: any[] = []): Promise<any> {
    if (!this.connection) {
      throw new Error('Database not connected')
    }

    return new Promise((resolve, reject) => {
      this.connection!.all(sql, ...params, (err: Error | null, result: any) => {
        if (err) {
          logger.error('DuckDB query error:', { sql, params, error: err.message })
          reject(err)
        } else {
          resolve({ rows: result || [] })
        }
      })
    })
  }

  async close(): Promise<void> {
    try {
      if (this.connection) {
        this.connection = null
      }
      if (this.db) {
        this.db.close()
        this.db = null
      }
      logger.info('DuckDB connection closed')
    } catch (error) {
      logger.error('Error closing DuckDB connection:', error)
    }
  }

  async checkIfInitialized(): Promise<boolean> {
    try {
      // Check server_state table instead of users table
      logger.info('Checking if database is initialized...')
      const result = await this.query('SELECT COUNT(*) as count FROM server_state')
      const count = result.rows[0]?.count || 0
      logger.info(`Found ${count} server state records`)
      return count > 0
    } catch (error) {
      // If query fails (table doesn't exist), assume not initialized
      logger.info('Server state table does not exist, database not initialized')
      return false
    }
  }

  async runInit(): Promise<void> {
    try {
      logger.info('Running DuckDB initialization...')

      // Start explicit transaction
      await this.query('BEGIN TRANSACTION')

      const schemaPath = join(__dirname, '../../../database/duckdb-schema.sql')
      logger.info(`Reading schema from: ${schemaPath}`)
      const schemaSQL = await readFile(schemaPath, 'utf-8')
      logger.info(`Schema file length: ${schemaSQL.length} characters`)

      // Split by semicolon and execute each statement
      const rawStatements = schemaSQL.split(';')
      logger.info(`Raw statements after split: ${rawStatements.length}`)

      const trimmedStatements = rawStatements.map(stmt => stmt.trim())
      logger.info(`After trim: ${trimmedStatements.filter(s => s.length > 0).length} non-empty`)

      const statements = trimmedStatements.filter(stmt => {
        if (stmt.length === 0) return false

        // Remove comments and check if there's actual SQL content
        const sqlContent = stmt
          .split('\n')
          .map(line => line.trim())
          .filter(line => line.length > 0 && !line.startsWith('--'))
          .join('\n')
          .trim()

        return sqlContent.length > 0
      })

      logger.info(`After filtering comments: ${statements.length} statements`)

      // Debug: log first few statements
      statements.slice(0, 3).forEach((stmt, i) => {
        logger.info(`Statement ${i + 1}: ${stmt.substring(0, 100)}...`)
      })

      for (const statement of statements) {
        if (statement.trim()) {
          logger.info(`Executing: ${statement.substring(0, 50)}...`)
          await this.query(statement)
        }
      }

      // Commit transaction
      await this.query('COMMIT')

      // Verify tables were created
      const tables = await this.query("SELECT table_name FROM information_schema.tables WHERE table_schema = 'main'")
      logger.info(`Tables created: ${tables.rows.map((r: any) => r.table_name).join(', ')}`)

      logger.info(' DuckDB schema initialization completed successfully')
    } catch (error) {
      logger.error(' DuckDB initialization failed:', error)
      throw error
    }
  }

  async seedData(): Promise<void> {
    try {
      logger.info('Seeding DuckDB with sample data...')

      // Start explicit transaction
      await this.query('BEGIN TRANSACTION')

      const seedPath = join(__dirname, '../../../database/duckdb-seed.sql')
      const seedSQL = await readFile(seedPath, 'utf-8')

      // Split by semicolon and execute each statement
      const statements = seedSQL
        .split(';')
        .map(stmt => stmt.trim())
        .filter(stmt => stmt.length > 0 && !stmt.startsWith('--'))

      for (const statement of statements) {
        if (statement.trim() && statement.trim().length > 10) {
          try {
            logger.info(`Seeding: ${statement.substring(0, 50)}...`)
            await this.query(statement)
          } catch (error) {
            logger.error(`Failed to execute statement: ${statement.substring(0, 100)}...`, error)
            await this.query('ROLLBACK')
            throw error
          }
        }
      }

      // Commit transaction
      await this.query('COMMIT')

      logger.info(' DuckDB seeding completed successfully')
    } catch (error) {
      logger.error(' DuckDB seeding failed:', error)
      throw error
    }
  }

  async recordServerStart(): Promise<void> {
    try {
      const now = new Date().toISOString()

      // Get current state
      const result = await this.query('SELECT * FROM server_state WHERE id = 1')

      if (result.rows.length === 0) {
        // First time startup - create initial record
        await this.query(`
          INSERT INTO server_state (
            id, start_count, first_start_time, last_start_time, 
            last_stop_time, last_session_duration, total_lifetime
          ) VALUES (1, 1, ?, ?, NULL, 0, 0)
        `, [now, now])
        logger.info(' Server state: First startup recorded')
      } else {
        // Update existing record
        const state = result.rows[0]
        const newStartCount = (state.start_count || 0) + 1

        await this.query(`
          UPDATE server_state SET 
            start_count = ?,
            last_start_time = ?
          WHERE id = 1
        `, [newStartCount, now])

        logger.info(` Server state: Startup #${newStartCount} recorded`)
      }
    } catch (error) {
      logger.error('Failed to record server start:', error)
    }
  }

  async recordServerStop(): Promise<void> {
    try {
      const now = new Date().toISOString()

      // Get current state to calculate session duration
      const result = await this.query('SELECT * FROM server_state WHERE id = 1')

      if (result.rows.length > 0) {
        const state = result.rows[0]
        const lastStartTime = new Date(state.last_start_time)
        const stopTime = new Date(now)
        const sessionDuration = Math.floor((stopTime.getTime() - lastStartTime.getTime()) / 1000) // seconds
        const newTotalLifetime = (state.total_lifetime || 0) + sessionDuration

        await this.query(`
          UPDATE server_state SET 
            last_stop_time = ?,
            last_session_duration = ?,
            total_lifetime = ?
          WHERE id = 1
        `, [now, sessionDuration, newTotalLifetime])

        logger.info(` Server state: Shutdown recorded (session: ${sessionDuration}s, total: ${newTotalLifetime}s)`)
      }
    } catch (error) {
      logger.error('Failed to record server stop:', error)
    }
  }

  async getServerStats(): Promise<any> {
    try {
      const result = await this.query('SELECT * FROM server_state WHERE id = 1')
      return result.rows[0] || null
    } catch (error) {
      logger.error('Failed to get server stats:', error)
      return null
    }
  }
}

// Export singleton instance
export const duckdb = new DuckDBConnection()

// Mock Redis functions for development
export const redisClient = {
  connect: async () => logger.info('Redis mock: connected'),
  quit: async () => logger.info('Redis mock: disconnected'),
  on: (event: string, callback: Function) => logger.info(`Redis mock: listening to ${event}`)
}

export const connectDatabases = async (): Promise<void> => {
  try {
    await duckdb.connect()

    const isInitialized = await duckdb.checkIfInitialized()

    if (!isInitialized) {
      logger.info('Database not initialized, running setup...')
      await duckdb.runInit()
      await duckdb.seedData()
      logger.info(' DuckDB setup completed - Full functionality available')
    } else {
      logger.info(' DuckDB already initialized - Full functionality available')
    }

    // Record server startup
    await duckdb.recordServerStart()

    // Display server stats
    const stats = await duckdb.getServerStats()
    if (stats) {
      logger.info(` Server Stats: Startup #${stats.start_count}, Total uptime: ${Math.floor(stats.total_lifetime / 60)}min`)
    }

  } catch (error) {
    logger.error('Failed to connect to databases:', error)
    throw error
  }
}

export const closeDatabases = async (): Promise<void> => {
  try {
    // Record server stop before closing
    await duckdb.recordServerStop()
    await duckdb.close()
    logger.info('Database connections closed')
  } catch (error) {
    logger.error('Error closing database connections:', error)
    throw error
  }
}

import { useState } from 'react'
import { motion } from 'framer-motion'
import { Link, useNavigate } from 'react-router-dom'
import { Eye, EyeOff, Crown, Mail, Lock, User, Briefcase, DollarSign, Plus, X } from 'lucide-react'

const RegisterPage = () => {
  const navigate = useNavigate()
  const [currentStep, setCurrentStep] = useState(1)
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    confirmPassword: '',
    firstName: '',
    lastName: '',
    age: '',
    profession: '',
    annualIncome: '',
    netWorth: '',
    membershipTier: 'Platinum',
    bio: '',
    interests: [] as string[]
  })

  const [showPassword, setShowPassword] = useState(false)
  const [showConfirmPassword, setShowConfirmPassword] = useState(false)
  const [isLoading, setIsLoading] = useState(false)
  const [error, setError] = useState('')
  const [newInterest, setNewInterest] = useState('')

  const membershipTiers = [
    {
      value: 'Platinum',
      label: 'Platinum',
      price: 'NT$50,000/',
      description: ''
    },
    {
      value: 'Diamond',
      label: 'Diamond',
      price: 'NT$120,000/',
      description: 'VIP'
    },
    {
      value: 'Black Card',
      label: 'Black Card',
      price: '',
      description: ''
    }
  ]

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement | HTMLTextAreaElement>) => {
    setFormData(prev => ({
      ...prev,
      [e.target.name]: e.target.value
    }))
  }

  const addInterest = () => {
    if (newInterest.trim() && !formData.interests.includes(newInterest.trim()) && formData.interests.length < 10) {
      setFormData(prev => ({
        ...prev,
        interests: [...prev.interests, newInterest.trim()]
      }))
      setNewInterest('')
    }
  }

  const removeInterest = (interest: string) => {
    setFormData(prev => ({
      ...prev,
      interests: prev.interests.filter(i => i !== interest)
    }))
  }

  const validateStep1 = () => {
    if (!formData.email || !formData.password || !formData.confirmPassword) {
      setError('')
      return false
    }
    if (formData.password !== formData.confirmPassword) {
      setError('')
      return false
    }
    if (formData.password.length < 8) {
      setError('8')
      return false
    }
    return true
  }

  const validateStep2 = () => {
    if (!formData.firstName || !formData.lastName || !formData.age || !formData.profession) {
      setError('')
      return false
    }
    if (parseInt(formData.age) < 18 || parseInt(formData.age) > 100) {
      setError('18-100')
      return false
    }
    return true
  }

  const validateStep3 = () => {
    if (!formData.annualIncome || !formData.netWorth) {
      setError('')
      return false
    }
    if (parseInt(formData.annualIncome) < 5000000) {
      setError('500')
      return false
    }
    if (parseInt(formData.netWorth) < 30000000) {
      setError('3000')
      return false
    }
    if (formData.interests.length === 0) {
      setError('')
      return false
    }
    return true
  }

  const nextStep = () => {
    setError('')

    if (currentStep === 1 && !validateStep1()) return
    if (currentStep === 2 && !validateStep2()) return
    if (currentStep === 3 && !validateStep3()) return

    if (currentStep < 3) {
      setCurrentStep(currentStep + 1)
    } else {
      handleSubmit()
    }
  }

  const prevStep = () => {
    if (currentStep > 1) {
      setCurrentStep(currentStep - 1)
      setError('')
    }
  }

  const handleSubmit = async () => {
    setIsLoading(true)
    setError('')

    try {
      // TODO: Implement actual registration API call
      await new Promise(resolve => setTimeout(resolve, 2000)) // Simulate API call

      console.log('Registration data:', formData)
      navigate('/login', { 
        state: { message: '' }
      })
    } catch (err) {
      setError('')
    } finally {
      setIsLoading(false)
    }
  }

  const renderStep1 = () => (
    <div className="space-y-6">
      <div>
        <label htmlFor="email" className="block text-luxury-platinum text-sm font-medium mb-2">
           *
        </label>
        <div className="relative">
          <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-luxury-gold" />
          <input
            type="email"
            id="email"
            name="email"
            value={formData.email}
            onChange={handleChange}
            required
            className="w-full bg-luxury-midnight-black/50 border border-luxury-gold/30 rounded-lg px-10 py-3 text-luxury-platinum placeholder-luxury-platinum/50 focus:outline-none focus:border-luxury-gold transition-colors"
            placeholder=""
          />
        </div>
      </div>

      <div>
        <label htmlFor="password" className="block text-luxury-platinum text-sm font-medium mb-2">
           *
        </label>
        <div className="relative">
          <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-luxury-gold" />
          <input
            type={showPassword ? 'text' : 'password'}
            id="password"
            name="password"
            value={formData.password}
            onChange={handleChange}
            required
            className="w-full bg-luxury-midnight-black/50 border border-luxury-gold/30 rounded-lg px-10 py-3 pr-12 text-luxury-platinum placeholder-luxury-platinum/50 focus:outline-none focus:border-luxury-gold transition-colors"
            placeholder="8"
          />
          <button
            type="button"
            onClick={() => setShowPassword(!showPassword)}
            className="absolute right-3 top-1/2 transform -translate-y-1/2 text-luxury-gold hover:text-luxury-gold/80 transition-colors"
          >
            {showPassword ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
          </button>
        </div>
      </div>

      <div>
        <label htmlFor="confirmPassword" className="block text-luxury-platinum text-sm font-medium mb-2">
           *
        </label>
        <div className="relative">
          <Lock className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-luxury-gold" />
          <input
            type={showConfirmPassword ? 'text' : 'password'}
            id="confirmPassword"
            name="confirmPassword"
            value={formData.confirmPassword}
            onChange={handleChange}
            required
            className="w-full bg-luxury-midnight-black/50 border border-luxury-gold/30 rounded-lg px-10 py-3 pr-12 text-luxury-platinum placeholder-luxury-platinum/50 focus:outline-none focus:border-luxury-gold transition-colors"
            placeholder=""
          />
          <button
            type="button"
            onClick={() => setShowConfirmPassword(!showConfirmPassword)}
            className="absolute right-3 top-1/2 transform -translate-y-1/2 text-luxury-gold hover:text-luxury-gold/80 transition-colors"
          >
            {showConfirmPassword ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
          </button>
        </div>
      </div>
    </div>
  )

  const renderStep2 = () => (
    <div className="space-y-6">
      <div className="grid grid-cols-2 gap-4">
        <div>
          <label htmlFor="firstName" className="block text-luxury-platinum text-sm font-medium mb-2">
             *
          </label>
          <div className="relative">
            <User className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-luxury-gold" />
            <input
              type="text"
              id="firstName"
              name="firstName"
              value={formData.firstName}
              onChange={handleChange}
              required
              className="w-full bg-luxury-midnight-black/50 border border-luxury-gold/30 rounded-lg px-10 py-3 text-luxury-platinum placeholder-luxury-platinum/50 focus:outline-none focus:border-luxury-gold transition-colors"
              placeholder=""
            />
          </div>
        </div>

        <div>
          <label htmlFor="lastName" className="block text-luxury-platinum text-sm font-medium mb-2">
             *
          </label>
          <input
            type="text"
            id="lastName"
            name="lastName"
            value={formData.lastName}
            onChange={handleChange}
            required
            className="w-full bg-luxury-midnight-black/50 border border-luxury-gold/30 rounded-lg px-4 py-3 text-luxury-platinum placeholder-luxury-platinum/50 focus:outline-none focus:border-luxury-gold transition-colors"
            placeholder=""
          />
        </div>
      </div>

      <div>
        <label htmlFor="age" className="block text-luxury-platinum text-sm font-medium mb-2">
           *
        </label>
        <input
          type="number"
          id="age"
          name="age"
          value={formData.age}
          onChange={handleChange}
          required
          min="18"
          max="100"
          className="w-full bg-luxury-midnight-black/50 border border-luxury-gold/30 rounded-lg px-4 py-3 text-luxury-platinum placeholder-luxury-platinum/50 focus:outline-none focus:border-luxury-gold transition-colors"
          placeholder=""
        />
      </div>

      <div>
        <label htmlFor="profession" className="block text-luxury-platinum text-sm font-medium mb-2">
           *
        </label>
        <div className="relative">
          <Briefcase className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-luxury-gold" />
          <input
            type="text"
            id="profession"
            name="profession"
            value={formData.profession}
            onChange={handleChange}
            required
            className="w-full bg-luxury-midnight-black/50 border border-luxury-gold/30 rounded-lg px-10 py-3 text-luxury-platinum placeholder-luxury-platinum/50 focus:outline-none focus:border-luxury-gold transition-colors"
            placeholder=""
          />
        </div>
      </div>
    </div>
  )

  const renderStep3 = () => (
    <div className="space-y-6">
      <div>
        <label htmlFor="annualIncome" className="block text-luxury-platinum text-sm font-medium mb-2">
           () *
        </label>
        <div className="relative">
          <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-luxury-gold" />
          <input
            type="number"
            id="annualIncome"
            name="annualIncome"
            value={formData.annualIncome}
            onChange={handleChange}
            required
            min="5000000"
            className="w-full bg-luxury-midnight-black/50 border border-luxury-gold/30 rounded-lg px-10 py-3 text-luxury-platinum placeholder-luxury-platinum/50 focus:outline-none focus:border-luxury-gold transition-colors"
            placeholder="500"
          />
        </div>
        <p className="text-luxury-platinum/60 text-xs mt-1">500</p>
      </div>

      <div>
        <label htmlFor="netWorth" className="block text-luxury-platinum text-sm font-medium mb-2">
           () *
        </label>
        <div className="relative">
          <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 h-5 w-5 text-luxury-gold" />
          <input
            type="number"
            id="netWorth"
            name="netWorth"
            value={formData.netWorth}
            onChange={handleChange}
            required
            min="30000000"
            className="w-full bg-luxury-midnight-black/50 border border-luxury-gold/30 rounded-lg px-10 py-3 text-luxury-platinum placeholder-luxury-platinum/50 focus:outline-none focus:border-luxury-gold transition-colors"
            placeholder="3000"
          />
        </div>
        <p className="text-luxury-platinum/60 text-xs mt-1">3000</p>
      </div>

      <div>
        <label htmlFor="membershipTier" className="block text-luxury-platinum text-sm font-medium mb-2">
           *
        </label>
        <div className="space-y-3">
          {membershipTiers.map(tier => (
            <label key={tier.value} className="flex items-start space-x-3 cursor-pointer">
              <input
                type="radio"
                name="membershipTier"
                value={tier.value}
                checked={formData.membershipTier === tier.value}
                onChange={handleChange}
                className="mt-1 w-4 h-4 text-luxury-gold bg-luxury-midnight-black border-luxury-gold/30 focus:ring-luxury-gold focus:ring-2"
              />
              <div className="flex-1">
                <div className="flex items-center justify-between">
                  <span className="text-luxury-gold font-medium">{tier.label}</span>
                  <span className="text-luxury-platinum/80 text-sm">{tier.price}</span>
                </div>
                <p className="text-luxury-platinum/60 text-sm">{tier.description}</p>
              </div>
            </label>
          ))}
        </div>
      </div>

      <div>
        <label htmlFor="interests" className="block text-luxury-platinum text-sm font-medium mb-2">
           *
        </label>
        <div className="flex space-x-2 mb-3">
          <input
            type="text"
            value={newInterest}
            onChange={(e) => setNewInterest(e.target.value)}
            onKeyPress={(e) => e.key === 'Enter' && (e.preventDefault(), addInterest())}
            className="flex-1 bg-luxury-midnight-black/50 border border-luxury-gold/30 rounded-lg px-4 py-2 text-luxury-platinum placeholder-luxury-platinum/50 focus:outline-none focus:border-luxury-gold transition-colors"
            placeholder=""
            maxLength={50}
          />
          <button
            type="button"
            onClick={addInterest}
            className="px-4 py-2 bg-luxury-gold/20 text-luxury-gold rounded-lg hover:bg-luxury-gold/30 transition-colors"
          >
            <Plus className="h-4 w-4" />
          </button>
        </div>
        <div className="flex flex-wrap gap-2">
          {formData.interests.map((interest, index) => (
            <span
              key={index}
              className="inline-flex items-center px-3 py-1 bg-luxury-gold/20 text-luxury-gold text-sm rounded-full"
            >
              {interest}
              <button
                type="button"
                onClick={() => removeInterest(interest)}
                className="ml-2 hover:text-luxury-gold/80"
              >
                <X className="h-3 w-3" />
              </button>
            </span>
          ))}
        </div>
        <p className="text-luxury-platinum/60 text-xs mt-1">110</p>
      </div>

      <div>
        <label htmlFor="bio" className="block text-luxury-platinum text-sm font-medium mb-2">
           ()
        </label>
        <textarea
          id="bio"
          name="bio"
          value={formData.bio}
          onChange={handleChange}
          rows={3}
          maxLength={500}
          className="w-full bg-luxury-midnight-black/50 border border-luxury-gold/30 rounded-lg px-4 py-3 text-luxury-platinum placeholder-luxury-platinum/50 focus:outline-none focus:border-luxury-gold transition-colors resize-none"
          placeholder="500"
        />
        <p className="text-luxury-platinum/60 text-xs mt-1">{formData.bio.length}/500</p>
      </div>
    </div>
  )

  return (
    <div className="min-h-screen bg-luxury-midnight-black py-8 px-4">
      <div className="max-w-2xl mx-auto">
        <motion.div
          initial={{ opacity: 0, y: 30 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
          className="luxury-glass p-8 rounded-2xl"
        >
          <div className="text-center mb-8">
            <div className="flex items-center justify-center mb-4">
              <Crown className="h-12 w-12 text-luxury-gold" />
            </div>
            <h1 className="text-3xl font-luxury font-bold text-luxury-gold mb-2">
               HeSocial
            </h1>
            <p className="text-luxury-platinum/80">

            </p>
          </div>

          {/* Progress Steps */}
          <div className="flex items-center justify-center mb-8">
            {[1, 2, 3].map((step) => (
              <div key={step} className="flex items-center">
                <div className={`w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium ${
                  step <= currentStep 
                    ? 'bg-luxury-gold text-luxury-midnight-black' 
                    : 'bg-luxury-gold/20 text-luxury-gold'
                }`}>
                  {step}
                </div>
                {step < 3 && (
                  <div className={`w-12 h-0.5 mx-2 ${
                    step < currentStep ? 'bg-luxury-gold' : 'bg-luxury-gold/20'
                  }`}></div>
                )}
              </div>
            ))}
          </div>

          <div className="text-center mb-6">
            <h2 className="text-xl font-luxury text-luxury-gold">
              {currentStep === 1 && ' 1: '}
              {currentStep === 2 && ' 2: '}
              {currentStep === 3 && ' 3: '}
            </h2>
          </div>

          {error && (
            <motion.div
              initial={{ opacity: 0, y: -10 }}
              animate={{ opacity: 1, y: 0 }}
              className="mb-6 p-4 bg-red-500/20 border border-red-500/50 rounded-lg text-red-400 text-sm"
            >
              {error}
            </motion.div>
          )}

          <form onSubmit={(e) => e.preventDefault()}>
            {currentStep === 1 && renderStep1()}
            {currentStep === 2 && renderStep2()}
            {currentStep === 3 && renderStep3()}

            <div className="flex justify-between mt-8">
              <button
                type="button"
                onClick={prevStep}
                disabled={currentStep === 1}
                className="px-6 py-3 border border-luxury-gold/30 text-luxury-gold rounded-lg hover:bg-luxury-gold/10 transition-colors disabled:opacity-50 disabled:cursor-not-allowed"
              >

              </button>

              <motion.button
                type="button"
                onClick={nextStep}
                disabled={isLoading}
                className="px-6 py-3 luxury-button disabled:opacity-50 disabled:cursor-not-allowed"
                whileHover={{ scale: isLoading ? 1 : 1.02 }}
                whileTap={{ scale: isLoading ? 1 : 0.98 }}
              >
                {isLoading ? (
                  <div className="flex items-center">
                    <div className="w-5 h-5 border-2 border-luxury-midnight-black border-t-transparent rounded-full animate-spin mr-2"></div>
                    ...
                  </div>
                ) : currentStep === 3 ? (
                  ''
                ) : (
                  ''
                )}
              </motion.button>
            </div>
          </form>

          <div className="mt-8 text-center">
            <p className="text-luxury-platinum/60 text-sm">

              <Link
                to="/login"
                className="text-luxury-gold hover:text-luxury-gold/80 font-medium ml-1 transition-colors"
              >

              </Link>
            </p>
          </div>
        </motion.div>
      </div>
    </div>
  )
}

export default RegisterPage

import winston from 'winston'
import config from './config.js'

const logger = winston.createLogger({
  level: config.nodeEnv === 'production' ? 'info' : 'debug',
  format: winston.format.combine(
    winston.format.timestamp(),
    winston.format.errors({ stack: true }),
    winston.format.json()
  ),
  defaultMeta: { service: 'hesocial-backend' },
  transports: [
    new winston.transports.File({ 
      filename: 'logs/error.log', 
      level: 'error',
      maxsize: 5242880, // 5MB
      maxFiles: 5
    }),
    new winston.transports.File({ 
      filename: 'logs/combined.log',
      maxsize: 5242880, // 5MB
      maxFiles: 5
    })
  ]
})

if (config.nodeEnv !== 'production') {
  logger.add(new winston.transports.Console({
    format: winston.format.combine(
      winston.format.colorize(),
      winston.format.simple()
    )
  }))
}

