import React, { createContext, useContext, useEffect, useState, ReactNode } from 'react'
import { authService, User, LoginCredentials, RegisterData } from '../services/authService'

interface AuthContextType {
  user: User | null
  isLoading: boolean
  isAuthenticated: boolean
  login: (credentials: LoginCredentials) => Promise<{ success: boolean; error?: string }>
  register: (userData: RegisterData) => Promise<{ success: boolean; error?: string }>
  logout: () => void
  updateProfile: (updates: Partial<User>) => Promise<{ success: boolean; error?: string }>
  refreshToken: () => Promise<void>
  loginWithGoogle: () => void
}

const AuthContext = createContext<AuthContextType | undefined>(undefined)

interface AuthProviderProps {
  children: ReactNode
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [user, setUser] = useState<User | null>(null)
  const [isLoading, setIsLoading] = useState(true)

  const isAuthenticated = !!user

  // Initialize auth state on app load
  useEffect(() => {
    initializeAuth()
  }, [])

  // Handle OAuth callback tokens
  useEffect(() => {
    const token = authService.handleOAuthCallback()
    if (token) {
      // OAuth login successful, fetch user profile
      loadUserProfile()
    }
  }, [])

  const initializeAuth = async () => {
    try {
      setIsLoading(true)

      if (authService.isAuthenticated()) {
        // Validate existing token and load user data
        const result = await authService.validateToken()

        if (result.success && result.data) {
          setUser(result.data.user)
        } else {
          // Invalid token, clear it
          authService.logout()
          setUser(null)
        }
      }
    } catch (error) {
      console.error('Auth initialization error:', error)
      authService.logout()
      setUser(null)
    } finally {
      setIsLoading(false)
    }
  }

  const loadUserProfile = async () => {
    try {
      const result = await authService.getProfile()

      if (result.success && result.data) {
        setUser(result.data.user)
      }
    } catch (error) {
      console.error('Load user profile error:', error)
    }
  }

  const login = async (credentials: LoginCredentials): Promise<{ success: boolean; error?: string }> => {
    try {
      setIsLoading(true)

      const result = await authService.login(credentials)

      if (result.success && result.data) {
        setUser(result.data.user)
        return { success: true }
      } else {
        return { success: false, error: result.error }
      }
    } catch (error) {
      console.error('Login error:', error)
      return { success: false, error: 'Login failed' }
    } finally {
      setIsLoading(false)
    }
  }

  const register = async (userData: RegisterData): Promise<{ success: boolean; error?: string }> => {
    try {
      setIsLoading(true)

      const result = await authService.register(userData)

      if (result.success && result.data) {
        setUser(result.data.user)
        return { success: true }
      } else {
        return { success: false, error: result.error }
      }
    } catch (error) {
      console.error('Registration error:', error)
      return { success: false, error: 'Registration failed' }
    } finally {
      setIsLoading(false)
    }
  }

  const logout = () => {
    authService.logout()
    setUser(null)
  }

  const updateProfile = async (updates: Partial<User>): Promise<{ success: boolean; error?: string }> => {
    try {
      const result = await authService.updateProfile(updates)

      if (result.success && result.data) {
        setUser(result.data.user)
        return { success: true }
      } else {
        return { success: false, error: result.error }
      }
    } catch (error) {
      console.error('Update profile error:', error)
      return { success: false, error: 'Failed to update profile' }
    }
  }

  const refreshToken = async () => {
    try {
      const result = await authService.refreshToken()

      if (!result.success) {
        // Refresh failed, logout user
        logout()
      }
    } catch (error) {
      console.error('Refresh token error:', error)
      logout()
    }
  }

  const loginWithGoogle = () => {
    authService.initiateGoogleLogin()
  }

  const value: AuthContextType = {
    user,
    isLoading,
    isAuthenticated,
    login,
    register,
    logout,
    updateProfile,
    refreshToken,
    loginWithGoogle
  }

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  )
}

export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext)
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider')
  }
  return context
}

import { Router } from 'express'
import jwt from 'jsonwebtoken'
import passport from '../config/passport.js'
import {
  register,
  login,
  getProfile,
  updateProfile,
  refreshToken
} from '../controllers/authController.js'
import { authenticateToken } from '../middleware/auth.js'
import { User } from '../types/index.js'
import config from '../utils/config.js'
import logger from '../utils/logger.js'

const router = Router()

// Initialize passport
router.use(passport.initialize())

// Regular authentication routes
router.post('/register', register)
router.post('/login', login)
router.get('/profile', authenticateToken, getProfile)
router.put('/profile', authenticateToken, updateProfile)
router.post('/refresh', authenticateToken, refreshToken)

// Google OAuth routes
router.get('/google',
  passport.authenticate('google', {
    scope: ['profile', 'email']
  })
)

router.get('/google/callback',
  passport.authenticate('google', { session: false }),
  async (req, res) => {
    try {
      const user = req.user as User

      if (!user) {
        logger.error('Google OAuth callback: No user data')
        return res.redirect(`${config.corsOrigins[0]}/login?error=oauth_failed`)
      }

      // Generate JWT token for the authenticated user
      const tokenPayload = {
        userId: user.id,
        email: user.email,
        membershipTier: user.membershipTier
      }
      const token = jwt.sign(tokenPayload, config.jwtSecret, { expiresIn: config.jwtExpiresIn } as jwt.SignOptions)

      logger.info(`Google OAuth successful for user: ${user.email}`)

      // Check if user needs to complete profile (for new Google users)
      const needsProfileCompletion = !user.age || !user.profession || !user.annualIncome || !user.netWorth

      // Redirect to frontend with token and status
      const redirectUrl = needsProfileCompletion 
        ? `${config.corsOrigins[0]}/complete-profile?token=${token}`
        : `${config.corsOrigins[0]}/dashboard?token=${token}`

      res.redirect(redirectUrl)
    } catch (error) {
      logger.error('Google OAuth callback error:', error)
      res.redirect(`${config.corsOrigins[0]}/login?error=oauth_error`)
    }
  }
)

// LinkedIn OAuth routes (placeholder for future implementation)
router.get('/linkedin', (req, res) => {
  res.status(501).json({
    success: false,
    error: 'LinkedIn OAuth not implemented yet',
    message: 'LinkedIn OAuth will be added in next phase'
  })
})

router.get('/linkedin/callback', (req, res) => {
  res.status(501).json({
    success: false,
    error: 'LinkedIn OAuth not implemented yet',
    message: 'LinkedIn OAuth will be added in next phase'
  })
})

// Logout route (mainly for clearing any server-side sessions if needed)
router.post('/logout', authenticateToken, (req, res) => {
  try {
    // In a JWT-based system, logout is typically handled client-side
    // by removing the token from storage. This endpoint is for any
    // server-side cleanup if needed in the future.

    logger.info(`User logged out: ${(req as any).user?.email}`)

    res.json({
      success: true,
      message: 'Logged out successfully'
    })
  } catch (error) {
    logger.error('Logout error:', error)
    res.status(500).json({
      success: false,
      error: 'Logout failed'
    })
  }
})

// Validate token endpoint
router.get('/validate', authenticateToken, (req, res) => {
  res.json({
    success: true,
    data: {
      user: (req as any).user,
      valid: true
    },
    message: 'Token is valid'
  })
})

export default router

