@tailwind base;
@tailwind components;
@tailwind utilities;

@layer base {
  html {
    font-family: 'Inter', system-ui, sans-serif;
  }

  h1, h2, h3, h4, h5, h6 {
    font-family: 'Playfair Display', serif;
  }
}

@layer components {
  .luxury-gradient {
    @apply bg-gradient-to-br from-luxury-gold/20 via-luxury-platinum/10 to-luxury-champagne/30;
  }

  .luxury-glass {
    @apply bg-white/10 backdrop-blur-luxury border border-white/20;
  }

  .luxury-button {
    @apply px-8 py-3 bg-luxury-gold text-luxury-midnight-black font-medium rounded-lg 
           hover:bg-luxury-gold/90 transition-all duration-300 
           shadow-lg hover:shadow-xl transform hover:-translate-y-0.5;
  }

  .luxury-button-outline {
    @apply px-8 py-3 border-2 border-luxury-gold text-luxury-gold font-medium rounded-lg
           hover:bg-luxury-gold hover:text-luxury-midnight-black transition-all duration-300
           shadow-lg hover:shadow-xl transform hover:-translate-y-0.5;
  }
}

/** @type {import('tailwindcss').Config} */
export default {
  content: [
    "./index.html",
    "./src/**/*.{js,ts,jsx,tsx}",
  ],
  theme: {
    extend: {
      colors: {
        luxury: {
          gold: '#D4AF37',
          'deep-blue': '#1B2951',
          platinum: '#E5E4E2',
          champagne: '#F7E7CE',
          'midnight-black': '#0C0C0C',
        },
      },
      fontFamily: {
        'luxury': ['Playfair Display', 'serif'],
        'sans': ['Inter', 'system-ui', 'sans-serif'],
      },
      animation: {
        'luxury-fade': 'luxuryFade 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94)',
        'luxury-slide': 'luxurySlide 0.6s cubic-bezier(0.25, 0.46, 0.45, 0.94)',
      },
      keyframes: {
        luxuryFade: {
          '0%': { opacity: '0', transform: 'translateY(10px)' },
          '100%': { opacity: '1', transform: 'translateY(0)' },
        },
        luxurySlide: {
          '0%': { transform: 'translateX(-20px)', opacity: '0' },
          '100%': { transform: 'translateX(0)', opacity: '1' },
        },
      },
      backdropBlur: {
        'luxury': '20px',
      },
    },
  },
  plugins: [],
}

import { defineConfig } from 'vite'
import react from '@vitejs/plugin-react-swc'
import path from 'path'

export default defineConfig({
  plugins: [react()],
  resolve: {
    alias: {
      '@': path.resolve(__dirname, './src'),
    },
  },
  server: {
    port: 3000,
    proxy: {
      '/api': {
        target: 'http://localhost:5000',
        changeOrigin: true,
      },
    },
  },
  build: {
    outDir: 'dist',
    sourcemap: true,
  },
})

