import { useState } from 'react'
import { motion } from 'framer-motion'
import { Link } from 'react-router-dom'
import { Calendar, MapPin, Users, Star, Filter, Search } from 'lucide-react'

const EventsPage = () => {
  const [searchTerm, setSearchTerm] = useState('')
  const [selectedCategory, setSelectedCategory] = useState('all')
  const [selectedLevel, setSelectedLevel] = useState('all')

  const categories = [
    { id: 'all', name: '全部活動' },
    { id: 'dinner', name: '私人晚宴' },
    { id: 'yacht', name: '遊艇派對' },
    { id: 'art', name: '藝術沙龍' },
    { id: 'business', name: '商務社交' },
    { id: 'wine', name: '品酒會' }
  ]

  const exclusivityLevels = [
    { id: 'all', name: '全部等級' },
    { id: 'VIP', name: 'VIP' },
    { id: 'VVIP', name: 'VVIP' },
    { id: 'invitation', name: '僅限邀請' }
  ]

  const mockEvents = [
    {
      id: '1',
      name: '星空下的法式晚宴',
      description: '與米其林三星主廚共享精緻法式料理，在台北101頂樓欣賞城市夜景',
      dateTime: '2024-07-27T19:00:00',
      venue: {
        name: '台北文華東方酒店',
        address: '台北市松山區敦化北路166號',
        rating: 5
      },
      category: '私人晚宴',
      exclusivityLevel: 'VVIP',
      pricing: {
        vvip: 15000,
        vip: 10000,
        currency: 'TWD'
      },
      currentAttendees: 12,
      capacity: 20,
      dressCode: 5,
      images: ['/api/placeholder/400/300'],
      tags: ['米其林', '頂樓', '限量']
    },
    {
      id: '2',
      name: '私人遊艇品酒之夜',
      description: '在豪華遊艇上品嚐世界頂級香檳，與成功企業家建立深度連結',
      dateTime: '2024-08-15T18:30:00',
      venue: {
        name: '基隆港VIP碼頭',
        address: '基隆市中正區中正路1號',
        rating: 5
      },
      category: '遊艇派對',
      exclusivityLevel: 'VIP',
      pricing: {
        vip: 8000,
        currency: 'TWD'
      },
      currentAttendees: 8,
      capacity: 16,
      dressCode: 4,
      images: ['/api/placeholder/400/300'],
      tags: ['遊艇', '香檳', '夕陽']
    },
    {
      id: '3',
      name: '當代藝術收藏家沙龍',
      description: '與知名藝術策展人探討當代藝術趨勢，欣賞私人收藏珍品',
      dateTime: '2024-09-08T15:00:00',
      venue: {
        name: '台北當代藝術館',
        address: '台北市大同區長安西路39號',
        rating: 4
      },
      category: '藝術沙龍',
      exclusivityLevel: '僅限邀請',
      pricing: {
        vvip: 12000,
        currency: 'TWD'
      },
      currentAttendees: 6,
