    const counts: any = {}

    for (const table of tables) {
      try {
        const result = await pool.query('SELECT COUNT(*) as count FROM ' + table)
        counts[table] = Number(result.rows[0]?.count) || 0
      } catch (error) {
        counts[table] = 'Error: ' + (error instanceof Error ? error.message : 'Unknown')
      }
    }

    res.json({
      success: true,
