    }

    if (paymentStatus) {
      whereClause += ' AND r.payment_status = ?'
      params.push(paymentStatus)
    }

    if (search) {
      whereClause += ' AND (e.name ILIKE ? OR u.first_name ILIKE ? OR u.last_name ILIKE ? OR u.email ILIKE ?)'
      const searchTerm = `%${search}%`
      params.push(searchTerm, searchTerm, searchTerm, searchTerm)
    }

    const offset = (Number(page) - 1) * Number(limit)

    // Get total count
    const countQuery = `
      SELECT COUNT(*) as total 
      FROM registrations r
      LEFT JOIN events e ON r.event_id = e.id
      LEFT JOIN users u ON r.user_id = u.id
      WHERE ${whereClause}
    `
    const countResult = await pool.query(countQuery, params)
    const total = Number(countResult.rows[0]?.total) || 0

    // Get registrations with detailed information
    const registrationsQuery = `
      SELECT 
        r.*,
        e.name as event_name,
        e.date_time as event_date_time,
        e.pricing,
        u.first_name,
        u.last_name,
        u.email,
        u.membership_tier,
        v.name as venue_name,
        ec.name as category_name
      FROM registrations r
      LEFT JOIN events e ON r.event_id = e.id
      LEFT JOIN users u ON r.user_id = u.id
      LEFT JOIN venues v ON e.venue_id = v.id
      LEFT JOIN event_categories ec ON e.category_id = ec.id
      WHERE ${whereClause}
      ORDER BY r.created_at DESC
      LIMIT ? OFFSET ?
    `

    const result = await pool.query(registrationsQuery, [...params, limit, offset])

    const response: ApiResponse<Registration[]> = {
      success: true,
      data: result.rows,
      pagination: {
        page: Number(page),
        limit: Number(limit),
        total,
        totalPages: Math.ceil(total / Number(limit))
      }
    }

    return res.json(response)
  } catch (error) {
    logger.error('Error fetching all registrations:', error)
    return res.status(500).json({
      success: false,
      error: 'Failed to fetch registrations'
    })
  }
}

export const approveRegistration = async (req: Request, res: Response): Promise<Response> => {
  try {
    const { id } = req.params
    const { approved, notes } = req.body

    const status = approved ? 'approved' : 'rejected'

    // Update registration status
    const updateQuery = `
      UPDATE registrations 
      SET status = ?, updated_at = ?
      WHERE id = ?
    `

    const result = await pool.query(updateQuery, [
      status,
      new Date().toISOString(),
      id
    ])

    if (result.rowCount === 0) {
      return res.status(404).json({
        success: false,
        error: 'Registration not found'
      })
    }

    logger.info(`Registration ${id} ${status} by admin`)

    return res.json({
      success: true,
      message: `Registration ${status} successfully`
    })
  } catch (error) {
    logger.error('Error approving registration:', error)
    return res.status(500).json({
      success: false,
      error: 'Failed to update registration status'
    })
  }
}

export const updatePaymentStatus = async (req: Request, res: Response): Promise<Response> => {
  try {
    const { id } = req.params
    const { paymentStatus, paymentIntentId } = req.body

    // First get the registration details to update participant access
    const registrationQuery = `
      SELECT user_id, event_id 
      FROM registrations 
      WHERE id = ?
    `
    const registrationResult = await pool.query(registrationQuery, [id])

    if (registrationResult.rows.length === 0) {
      return res.status(404).json({
        success: false,
        error: 'Registration not found'
      })
    }

    const registration = registrationResult.rows[0]

    const updateQuery = `
      UPDATE registrations 
      SET payment_status = ?, payment_intent_id = ?, updated_at = ?
      WHERE id = ?
    `

    const result = await pool.query(updateQuery, [
      paymentStatus,
      paymentIntentId || null,
      new Date().toISOString(),
      id
    ])

    if (result.rowCount === 0) {
      return res.status(404).json({
        success: false,
        error: 'Registration not found'
      })
    }

    // Update participant access when payment status changes
    try {
      const { getDb } = await import('../config/database.js')
      const ParticipantAccessService = (await import('../services/participantAccessService.js')).default
      const participantService = new ParticipantAccessService(getDb())

      participantService.updateParticipantAccess(
        registration.user_id,
        registration.event_id,
        paymentStatus,
        id
      )
    } catch (accessError) {
      logger.error('Error updating participant access:', accessError)
      // Don't fail the main operation if participant access update fails
    }

    logger.info(`Registration ${id} payment status updated to ${paymentStatus}`)

    return res.json({
      success: true,
      message: 'Payment status updated successfully'
    })
  } catch (error) {
    logger.error('Error updating payment status:', error)
    return res.status(500).json({
      success: false,
      error: 'Failed to update payment status'
    })
  }
}

