  const fetchSettings = async () => {
    if (!eventId) return

    try {
      setLoading(true)
      const response = await participantService.getPrivacySettings(eventId)

      if (response.success) {
        setSettings(response.data)
      } else {
        setError(response.error || 'Failed to fetch privacy settings')
      }
