   150‚Üí          .join('\n')
   151‚Üí          .trim()
   152‚Üí          
   153‚Üí        return sqlContent.length > 0
   154‚Üí      })
   155‚Üí      
   156‚Üí      logger.info(`After filtering comments: ${statements.length} statements`)
   157‚Üí      
   158‚Üí      // Debug: log first few statements
   159‚Üí      statements.slice(0, 3).forEach((stmt, i) => {
   160‚Üí        logger.info(`Statement ${i + 1}: ${stmt.substring(0, 100)}...`)
   161‚Üí      })
   162‚Üí
   163‚Üí      for (const statement of statements) {
