async userHasPermission(adminId: string, permission: string): Promise<boolean> {
  const user = await this.getAdminUser(adminId)

  if (!user || user.status !== 'active') {
    return false
  }

  // å¾æ¬Šé™å­—ç¬¦ä¸²ä¸­æå–åŠŸèƒ½åç¨±ï¼ˆä¾‹å¦‚ 'events:view' -> 'events'ï¼‰
  const functionName = permission.split(':')[0]

  // æª¢æŸ¥ç›´æ¥æ¬Šé™
  if (user.permissions) {
    // è¶…ç´šç®¡ç†å“¡æ¬Šé™æª¢æŸ¥
    if (user.permissions.includes('*')) return true

    // åŠŸèƒ½ç´šåˆ¥æ¬Šé™æª¢æŸ¥ - å¦‚æœæœ‰è©²åŠŸèƒ½çš„ä»»ä½•æ¬Šé™ï¼Œå‰‡æˆäºˆè©²åŠŸèƒ½çš„æ‰€æœ‰æ“ä½œæ¬Šé™
