// Admin Authentication Hook
// Manages admin authentication state separate from user authentication
import { useEffect, useCallback } from 'react'
import { create } from 'zustand'
import { persist } from 'zustand/middleware'
import { API_CONFIG, API_ENDPOINTS } from '../config/api'

export interface AdminProfile {
  adminId: string
  username: string
  email: string
  roleId: string
  type: 'super_admin' | 'system_admin' | 'operation_admin' | 'premium_admin'
  level: 1 | 2
  permissions: string[]
  profile: {
    realName: string
    employeeId: string
    lastLogin: Date
  }
  status: 'active' | 'suspended' | 'inactive'
}

interface AdminAuthState {
  admin: AdminProfile | null
  accessToken: string | null
  refreshToken: string | null
  isAuthenticated: boolean
  loading: boolean

  // Actions
  login: (email: string, password: string) => Promise<void>
  logout: () => void
  refreshAccessToken: () => Promise<boolean>
  hasPermission: (permission: string) => boolean

  // Level-based utility functions (incremental improvement)
  isTopLevelAdmin: () => boolean
  isLevel2Admin: () => boolean
  getAdminLevelName: () => string
}

const API_BASE_URL = API_CONFIG.ADMIN_BASE_URL

export const useAdminAuthStore = create<AdminAuthState>()(
  persist(
    (set, get) => ({
      admin: null,
      accessToken: null,
      refreshToken: null,
      isAuthenticated: false,
      loading: false,

      login: async (email: string, password: string) => {
        set({ loading: true })

        try {
          console.log('ğŸ” Admin login attempt:', { email, url: `${API_BASE_URL}${API_ENDPOINTS.ADMIN.AUTH.LOGIN}` })

          const response = await fetch(`${API_BASE_URL}${API_ENDPOINTS.ADMIN.AUTH.LOGIN}`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email, password })
          })

          console.log('ğŸ“¡ Response status:', response.status)
          const data = await response.json()
          console.log('ğŸ“¦ Response data:', data)

          if (!response.ok) {
            console.error('âŒ Login failed:', data.error)
            throw new Error(data.error || 'ç™»å…¥å¤±æ•—')
          }

          if (data.success) {
            // Ensure permissions is always an array and level is properly set
            const adminData = {
              ...data.data.admin,
              permissions: data.data.admin.permissions || [],
              level: data.data.admin.level || 2 // Default to level 2 if not provided
            }

            console.log('ğŸ”§ Setting admin data in auth store:', {
              adminId: adminData.adminId,
              type: adminData.type,
              level: adminData.level,
              permissions: adminData.permissions
            })

            set({
              admin: adminData,
              accessToken: data.data.accessToken,
              refreshToken: data.data.refreshToken,
              isAuthenticated: true,
              loading: false
            })
          } else {
            throw new Error(data.error || 'ç™»å…¥å¤±æ•—')
          }
                } catch (error: unknown) {
          set({ loading: false })
          throw error
        }
      },

      logout: () => {
        // Call logout endpoint to invalidate token on server
        const { accessToken } = get()
        if (accessToken) {
                    fetch(`${API_BASE_URL}${API_ENDPOINTS.ADMIN.AUTH.LOGOUT}`, {
            method: 'POST',
            headers: {
              'Authorization': `Bearer ${accessToken}`,
              'Content-Type': 'application/json'
            }
          }).catch(() => {
            // Ignore errors on logout
          })
        }

        set({
          admin: null,
          accessToken: null,
          refreshToken: null,
          isAuthenticated: false
        })
      },

      refreshAccessToken: async () => {
        const { refreshToken } = get()
        if (!refreshToken) return false

        try {
                    const response = await fetch(`${API_BASE_URL}${API_ENDPOINTS.ADMIN.AUTH.REFRESH}`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ refreshToken })
          })

          const data = await response.json()

          if (response.ok && data.success) {
            set({
              accessToken: data.data.accessToken,
              isAuthenticated: true
            })
            return true
          } else {
            // Refresh failed, logout
            get().logout()
            return false
          }
        } catch (error) {
          get().logout()
          return false
        }
      },

      hasPermission: (permission: string) => {
        const { admin } = get()

        // Debug logging (disabled to reduce console noise)
        // if (process.env.NODE_ENV === 'development') {
        //   console.log('hasPermission called with:', permission)
        //   console.log('admin object:', admin)
        //   console.log('admin.permissions:', admin?.permissions)
        // }

        if (!admin || admin.status !== 'active') return false

        // Level 1 admins bypass ALL permission checks (incremental improvement)
        if (admin.level === 1) {
          console.log('ğŸ”“ Level 1 admin bypassing permission check for:', permission)
          return true
        }

        // Check if permissions array exists
        if (!admin.permissions || !Array.isArray(admin.permissions)) {
          console.warn('Admin permissions is not an array:', admin.permissions)
          return false
        }

        // Super admin has all permissions
        if (admin.permissions.includes('*')) return true

        // å¾æ¬Šé™å­—ç¬¦ä¸²ä¸­æå–åŠŸèƒ½åç¨±ï¼ˆä¾‹å¦‚ 'events:view' -> 'events'ï¼‰
        const functionName = permission.split(':')[0]

        // åŠŸèƒ½ç´šåˆ¥æ¬Šé™æª¢æŸ¥ - å¦‚æœæœ‰è©²åŠŸèƒ½çš„ä»»ä½•æ¬Šé™ï¼Œå‰‡æˆäºˆè©²åŠŸèƒ½çš„æ‰€æœ‰æ“ä½œæ¬Šé™
        if (admin.permissions.includes(functionName) || 
            admin.permissions.some(p => p.startsWith(functionName + ':'))) {
          return true
        }

        return false
      },

      // New level-based utility functions (incremental improvement)
      isTopLevelAdmin: () => {
        const { admin } = get()
        return admin?.level === 1
      },

      isLevel2Admin: () => {
        const { admin } = get()
        return admin?.level === 2
      },

      getAdminLevelName: () => {
        const { admin } = get()
        if (!admin) return 'æœªçŸ¥'
        return admin.level === 1 ? 'é ‚ç´šç®¡ç†å“¡' : 'æ¬Šé™ç®¡ç†å“¡'
      }
    }),
    {
      name: 'admin-auth-storage',
      partialize: (state) => ({
        admin: state.admin,
        accessToken: state.accessToken,
        refreshToken: state.refreshToken,
        isAuthenticated: state.isAuthenticated
      }),
      onRehydrateStorage: () => (state) => {
        // Ensure loading is false after hydration
        if (state) {
          state.loading = false

          // Ensure admin.permissions is always an array
          if (state.admin && (!state.admin.permissions || !Array.isArray(state.admin.permissions))) {
            console.warn('Fixing admin.permissions from localStorage:', state.admin.permissions)
            state.admin.permissions = []
          }
        }
      }
    }
  )
)

// Main hook for components
export const useAdminAuth = () => {
  const {
    admin,
    accessToken,
    isAuthenticated,
    loading,
    login,
    logout,
    refreshAccessToken,
    hasPermission,
    isTopLevelAdmin,
    isLevel2Admin,
    getAdminLevelName
  } = useAdminAuthStore()

  // API helper for authenticated requests - moved before useEffect to maintain hook order
  const apiCall = useCallback(async (endpoint: string, options: RequestInit = {}) => {
    const token = useAdminAuthStore.getState().accessToken

    if (!token) {
      throw new Error('æœªæˆæ¬Šçš„è«‹æ±‚')
    }

    const response = await fetch(`${API_BASE_URL}${endpoint}`, {
      ...options,
      headers: {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json',
        ...options.headers
      }
    })

    // Handle token expiry
    if (response.status === 401) {
      const refreshSuccess = await refreshAccessToken()
      if (refreshSuccess) {
        // Retry with new token
        const newToken = useAdminAuthStore.getState().accessToken
        return fetch(`${API_BASE_URL}${endpoint}`, {
          ...options,
          headers: {
            'Authorization': `Bearer ${newToken}`,
            'Content-Type': 'application/json',
            ...options.headers
          }
        })
      } else {
        logout()
        throw new Error('èªè­‰å·²éæœŸï¼Œè«‹é‡æ–°ç™»å…¥')
      }
    }

    if (!response.ok) {
      const errorData = await response.json().catch(() => ({}))
      throw new Error(errorData.error || `HTTP ${response.status}`)
    }

    return response
  }, [refreshAccessToken, logout])

  // Auto-refresh token on mount and periodically
  useEffect(() => {
    if (accessToken && !loading) {
      // Try to refresh token on mount
      refreshAccessToken()

      // Set up periodic token refresh (every 7 hours)
      const interval = setInterval(() => {
        refreshAccessToken()
      }, 7 * 60 * 60 * 1000) // 7 hours

      return () => clearInterval(interval)
    }
  }, [accessToken, loading, refreshAccessToken])

  return {
    admin,
    isAuthenticated,
    loading,
    login,
    logout,
    hasPermission,
    isTopLevelAdmin,
    isLevel2Admin,
    getAdminLevelName,
    apiCall
  }
}

// Admin API service helpers - to be used within components that have access to useAdminAuth
export const createAdminAPI = (apiCall: (url: string, options?: Record<string, unknown>) => Promise<Response>) => ({
  // Permission atoms
  getPermissionAtoms: () => 
    apiCall('/permissions/atoms'),

  getGroupedPermissions: () => 
    apiCall('/permissions/atoms/grouped'),

  createPermissionAtom: (atomData: Record<string, unknown>) => 
    apiCall('/permissions/atoms', {
      method: 'POST',
      body: JSON.stringify(atomData)
    }),

  // Roles
  getRoles: (department?: string) => 
    apiCall(`/roles${department ? `?department=${department}` : ''}`),

  createRole: (roleData: Record<string, unknown>) => 
    apiCall('/roles', {
      method: 'POST',
      body: JSON.stringify(roleData)
    }),

  updateRole: (roleId: string, updates: Record<string, unknown>) => 
    apiCall(`/roles/${roleId}`, {
      method: 'PUT',
      body: JSON.stringify(updates)
    }),

  getRoleCapabilities: (roleId: string) => 
    apiCall(`/roles/${roleId}/capabilities`),

  // Permission validation
  validatePermissions: (permissions: string[]) => 
    apiCall('/permissions/validate', {
      method: 'POST',
      body: JSON.stringify({ permissions })
    }),

  // Users
  createAdminUser: (userData: Record<string, unknown>) => 
    apiCall('/users', {
      method: 'POST',
      body: JSON.stringify(userData)
    }),

  updateAdminUser: (adminId: string, updates: Record<string, unknown>) => 
    apiCall(`/users/${adminId}`, {
      method: 'PUT',
      body: JSON.stringify(updates)
    }),

  // Audit logs
    getAuditLogs: (filters?: Record<string, string>) => {
    const params = new URLSearchParams(filters || {})
    return apiCall(`/audit/logs?${params}`)
  }
})

// Deprecated: Use createAdminAPI inside components instead
export const adminAPI = createAdminAPI(() => { 
  throw new Error('adminAPI is deprecated. Use createAdminAPI(apiCall) inside components with useAdminAuth hook.')
})

