// SheSocial Database Types
// Matches NeDB structure for seamless sync

export interface BaseDocument {
  _id?: string
  createdAt: Date
  updatedAt: Date
  lastSync?: Date | null
}

// User Profile Types
export interface UserProfile extends BaseDocument {
  email: string
  profile: {
    name: string
    age: number
    bio: string
    interests: string[]
    location: string
    avatar?: string
    videos: VideoProfile[]
    interviewStatus: {
      completed: boolean
      duration: number
      interviewer?: string
      notes?: string
      scheduledAt?: Date
    }
  }
  membership: {
    type: 'regular' | 'vip' | 'premium_1300' | 'premium_2500'
    joinDate: Date
    payments: PaymentRecord[]
    vouchers?: VoucherBalance
    permissions: {
      viewParticipants: boolean
      priorityBooking: boolean
    }
  }
}

export interface VideoProfile {
  type: 'introduction' | 'interests' | 'profession' | 'lifestyle' | 'closing'
  url: string
  duration: number
  approved: boolean
  uploadedAt: Date
}

export interface PaymentRecord {
  amount: number
  date: Date
  method: 'LINE Pay' | 'ECPay' | 'Apple Pay' | 'Google Pay'
  status: 'pending' | 'completed' | 'failed' | 'refunded'
  transactionId?: string
}

export interface VoucherBalance {
  total_100: number
  total_200: number
  used_100: number
  used_200: number
  expiry: Date
  validFor: string[]
}

// Event Types
export interface EventData extends BaseDocument {
  name: string
  metadata: {
    date: Date
    location: string
    category: string
    type: '1day_trip' | '4hour_dining' | '2day_trip'
    pricing: {
      male: number
      female: number
      voucherDiscount: {
        '100': number
        '200': number
      }
    }
    requirements: {
      ageMin: number
      ageMax: number
      maritalStatus: 'single' | 'divorced' | 'any'
    }
    schedule: {
      frequency: 'biweekly'
      cycle: '3months'
      totalEvents: number
      twoDayTrips: number
    }
  }
  participants: EventParticipant[]
  participantVisibility: {
    premium_2500: boolean
    vip: boolean
    regular: boolean
  }
  notifications: {
    sent: boolean
    sentAt?: Date
    recipients: string[]
  }
  maxParticipants: number
  status: 'draft' | 'published' | 'full' | 'completed' | 'cancelled'
}

export interface EventParticipant {
  userId: string
  status: 'pending' | 'confirmed' | 'cancelled' | 'waitlist'
  paid: boolean
  voucherUsed?: {
    type: '100' | '200'
    amount: number
  }
  joinedAt: Date
}

// Booking Types
export interface BookingData extends BaseDocument {
  userId: string
  eventId: string
  status: 'pending' | 'confirmed' | 'cancelled' | 'completed'
  paymentStatus: 'pending' | 'paid' | 'refunded'
  paymentMethod?: string
  voucherApplied?: {
    type: '100' | '200'
    amount: number
    voucherId: string
  }
  specialRequests?: string
  emergencyContact?: {
    name: string
    phone: string
    relationship: string
  }
}

// Sync Queue Types
export interface SyncQueueItem {
  id?: number
  collection: 'users' | 'events' | 'bookings'
  operation: 'insert' | 'update' | 'delete'
  data: any
  timestamp: number
  retries: number
  lastError?: string
  priority: 'high' | 'medium' | 'low'
}

// API Response Types
export interface ApiResponse<T> {
  success: boolean
  data?: T
  error?: string
  timestamp: number
}

export interface SyncResponse {
  changes: any[]
  timestamp: number
  conflicts?: ConflictItem[]
}

export interface ConflictItem {
  collection: string
  localData: any
  serverData: any
  resolution: 'server_wins' | 'client_wins' | 'merge_required'
}

// Database Collections
export interface DatabaseCollections {
  users: UserProfile
  events: EventData
  bookings: BookingData
  syncQueue: SyncQueueItem
}

// Utility Types
export type CollectionName = keyof DatabaseCollections
export type DocumentType<T extends CollectionName> = DatabaseCollections[T]

// Search and Filter Types
export interface EventFilters {
  location?: string
  dateRange?: {
    start: Date
    end: Date
  }
  type?: EventData['metadata']['type']
  ageRange?: {
    min: number
    max: number
  }
  priceRange?: {
    min: number
    max: number
  }
}

export interface UserSearchFilters {
  location?: string
  ageRange?: {
    min: number
    max: number
  }
  interests?: string[]
  membershipType?: UserProfile['membership']['type']
}

// Authentication Store with Zustand
// Manages user authentication state, tokens, and membership permissions
import { create } from 'zustand'
import { persist, createJSONStorage } from 'zustand/middleware'
import { UserProfile } from '../types/database'

export interface AuthState {
  // User state
  user: UserProfile | null
  accessToken: string | null
  refreshToken: string | null
  isAuthenticated: boolean
  isLoading: boolean

  // Actions
  login: (credentials: LoginCredentials) => Promise<AuthResponse>
  register: (userData: RegisterData) => Promise<AuthResponse>
  logout: () => void
  refreshAccessToken: () => Promise<boolean>
  getCurrentUser: () => Promise<void>
  changePassword: (passwordData: ChangePasswordData) => Promise<boolean>

  // Membership helpers
  hasPermission: (permission: keyof UserProfile['membership']['permissions']) => boolean
  getMembershipLevel: () => UserProfile['membership']['type'] | null
  canViewParticipants: () => boolean
  hasPriorityBooking: () => boolean
  isPremiumMember: () => boolean
}

export interface LoginCredentials {
  email: string
  password: string
  rememberMe?: boolean
}

export interface RegisterData {
  email: string
  password: string
  profile: {
    name: string
    age?: number
    bio?: string
    interests?: string[]
    location?: string
  }
  membershipType?: UserProfile['membership']['type']
}

export interface ChangePasswordData {
  currentPassword: string
  newPassword: string
}

export interface AuthResponse {
  success: boolean
  data?: {
    user: UserProfile
    accessToken: string
    refreshToken: string
    expiresIn: string
  }
  error?: string
  message?: string
}

const API_BASE_URL = 'http://localhost:3001/api'

export const useAuthStore = create<AuthState>()(
  persist(
    (set, get) => ({
      // Initial state
      user: null,
      accessToken: null,
      refreshToken: null,
      isAuthenticated: false,
      isLoading: false,

      // Login action
      login: async (credentials: LoginCredentials): Promise<AuthResponse> => {
        set({ isLoading: true })

        try {
          const response = await fetch(`${API_BASE_URL}/auth/login`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(credentials),
          })

          const result = await response.json()

          if (result.success && result.data) {
            set({
              user: result.data.user,
              accessToken: result.data.accessToken,
              refreshToken: result.data.refreshToken,
              isAuthenticated: true,
              isLoading: false,
            })

            return result
          } else {
            set({ isLoading: false })
            return result
          }
        } catch (error) {
          set({ isLoading: false })
          return {
            success: false,
            error: 'ç¶²è·¯é€£æ¥å¤±æ•—',
            message: 'Network connection failed'
          }
        }
      },

      // Register action
      register: async (userData: RegisterData): Promise<AuthResponse> => {
        set({ isLoading: true })

        try {
          const response = await fetch(`${API_BASE_URL}/auth/register`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify(userData),
          })

          const result = await response.json()

          if (result.success && result.data) {
            set({
              user: result.data.user,
              accessToken: result.data.accessToken,
              refreshToken: result.data.refreshToken,
              isAuthenticated: true,
              isLoading: false,
            })

            return result
          } else {
            set({ isLoading: false })
            return result
          }
        } catch (error) {
          set({ isLoading: false })
          return {
            success: false,
            error: 'ç¶²è·¯é€£æ¥å¤±æ•—',
            message: 'Network connection failed'
          }
        }
      },

      // Logout action
      logout: () => {
        // Call logout endpoint if we have a token
        const { accessToken } = get()
        if (accessToken) {
          fetch(`${API_BASE_URL}/auth/logout`, {
            method: 'POST',
            headers: {
              'Authorization': `Bearer ${accessToken}`,
            },
          }).catch(() => {
            // Ignore logout errors
          })
        }

        set({
          user: null,
          accessToken: null,
          refreshToken: null,
          isAuthenticated: false,
          isLoading: false,
        })
      },

      // Refresh access token
      refreshAccessToken: async (): Promise<boolean> => {
        const { refreshToken } = get()

        if (!refreshToken) {
          return false
        }

        try {
          const response = await fetch(`${API_BASE_URL}/auth/refresh`, {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            body: JSON.stringify({ refreshToken }),
          })

          const result = await response.json()

          if (result.success && result.data) {
            set({
              accessToken: result.data.accessToken,
            })
            return true
          } else {
            // Refresh failed, logout user
            get().logout()
            return false
          }
        } catch (error) {
          get().logout()
          return false
        }
      },

      // Get current user info
      getCurrentUser: async (): Promise<void> => {
        const { accessToken } = get()

        if (!accessToken) {
          return
        }

        try {
          const response = await fetch(`${API_BASE_URL}/auth/me`, {
            headers: {
              'Authorization': `Bearer ${accessToken}`,
            },
          })

          const result = await response.json()

          if (result.success && result.data) {
            set({
              user: result.data,
              isAuthenticated: true,
            })
          } else {
            // Token might be expired, try to refresh
            const refreshed = await get().refreshAccessToken()
            if (refreshed) {
              await get().getCurrentUser()
            }
          }
        } catch (error) {
          console.error('Failed to get current user:', error)
        }
      },

      // Change password
      changePassword: async (passwordData: ChangePasswordData): Promise<boolean> => {
        const { accessToken } = get()

        if (!accessToken) {
          return false
        }

        try {
          const response = await fetch(`${API_BASE_URL}/auth/change-password`, {
            method: 'PUT',
            headers: {
              'Content-Type': 'application/json',
              'Authorization': `Bearer ${accessToken}`,
            },
            body: JSON.stringify(passwordData),
          })

          const result = await response.json()
          return result.success
        } catch (error) {
          return false
        }
      },

      // Membership helper functions
      hasPermission: (permission: keyof UserProfile['membership']['permissions']): boolean => {
        const { user } = get()
        return user?.membership.permissions[permission] || false
      },

      getMembershipLevel: (): UserProfile['membership']['type'] | null => {
        const { user } = get()
        return user?.membership.type || null
      },

      canViewParticipants: (): boolean => {
        return get().hasPermission('viewParticipants')
      },

      hasPriorityBooking: (): boolean => {
        return get().hasPermission('priorityBooking')
      },

      isPremiumMember: (): boolean => {
        const membershipType = get().getMembershipLevel()
        return membershipType === 'premium_1300' || membershipType === 'premium_2500'
      },
    }),
    {
      name: 'shesocial-auth-storage',
      storage: createJSONStorage(() => localStorage),
      partialize: (state) => ({
        user: state.user,
        accessToken: state.accessToken,
        refreshToken: state.refreshToken,
        isAuthenticated: state.isAuthenticated,
      }),
    }
  )
)

// Auto-refresh token on store initialization
if (typeof window !== 'undefined') {
  const store = useAuthStore.getState()
  if (store.refreshToken && store.isAuthenticated) {
    store.getCurrentUser()
  }
}

export default useAuthStore

