// Centralized Authentication Context - eliminates prop drilling
import React, { createContext, useContext, useState, ReactNode } from 'react'
import { AuthModal } from '../components/auth/AuthModal'

interface AuthContextType {
  openLogin: () => void
  openRegister: () => void
  closeAuth: () => void
  isAuthModalOpen: boolean
  authMode: 'login' | 'register'
}

const AuthContext = createContext<AuthContextType | undefined>(undefined)

interface AuthProviderProps {
  children: ReactNode
}

export const AuthProvider: React.FC<AuthProviderProps> = ({ children }) => {
  const [isAuthModalOpen, setIsAuthModalOpen] = useState(false)
  const [authMode, setAuthMode] = useState<'login' | 'register'>('login')

  const openLogin = () => {
    setAuthMode('login')
    setIsAuthModalOpen(true)
  }

  const openRegister = () => {
    setAuthMode('register')
    setIsAuthModalOpen(true)
  }

  const closeAuth = () => {
    setIsAuthModalOpen(false)
  }

  const toggleMode = () => {
    setAuthMode(authMode === 'login' ? 'register' : 'login')
  }

  return (
    <AuthContext.Provider value={{
      openLogin,
      openRegister,
      closeAuth,
      isAuthModalOpen,
      authMode
    }}>
      {children}

      {/* Global Auth Modal - rendered once for entire app */}
      {isAuthModalOpen && (
        <AuthModal
          mode={authMode}
          onClose={closeAuth}
          onToggleMode={toggleMode}
        />
      )}
    </AuthContext.Provider>
  )
}

// Custom hook for using auth context
export const useAuth = (): AuthContextType => {
  const context = useContext(AuthContext)
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider')
  }
  return context
}

// Reusable Modal Component - eliminates modal duplication
import React, { ReactNode } from 'react'

interface ModalProps {
  isOpen: boolean
  onClose: () => void
  children: ReactNode
  size?: 'sm' | 'md' | 'lg' | 'xl'
  className?: string
}

export const Modal: React.FC<ModalProps> = ({ 
  isOpen, 
  onClose, 
  children, 
  size = 'md',
  className = ''
}) => {
  if (!isOpen) return null

  const sizeClasses = {
    sm: 'max-w-md',
    md: 'max-w-2xl', 
    lg: 'max-w-4xl',
    xl: 'max-w-6xl'
  }

  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget) {
      onClose()
    }
  }

  return (
    <div 
      className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center z-50 p-4"
      onClick={handleBackdropClick}
    >
      <div className={`bg-white rounded-2xl shadow-2xl ${sizeClasses[size]} w-full max-h-[90vh] overflow-y-auto relative ${className}`}>
        {/* Close Button */}
        <button
          onClick={onClose}
          className="absolute top-4 right-4 text-gray-400 hover:text-gray-600 transition-colors z-10"
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
          </svg>
        </button>

        {/* Content */}
        <div className="p-8">
          {children}
        </div>
      </div>
    </div>
  )
}

// Reusable Call-to-Action Section Component
import React, { ReactNode } from 'react'

interface CTAAction {
  label: string
  onClick: () => void
  variant: 'primary' | 'secondary'
}

interface CTASectionProps {
  title: string
  description: string
  actions: CTAAction[]
  icon?: ReactNode
  className?: string
}

export const CTASection: React.FC<CTASectionProps> = ({
  title,
