  const { getEventById, bookEvent, cancelBooking, getEventParticipants } = useEvents()

  // Booking form state
  const [bookingForm, setBookingForm] = useState<Partial<BookingData>>({
    specialRequests: '',
    emergencyContact: {
      name: '',
      phone: '',
      relationship: ''
    }
  })

  useEffect(() => {
    loadEventDetails()
