// Event Card Component - Luxury design for Taiwan social events
import React from 'react'
// Define EventData interface directly here to avoid Vite cache issues
interface EventData {
  _id?: string
  name: string
  metadata: {
    date: Date
    location: string
    category: string
    type: '1day_trip' | '4hour_dining' | '2day_trip'
  }
