      setError(err instanceof Error ? err.message : 'Failed to create user')
      throw err
    } finally {
      setLoading(false)
    }
  }, [])

  const updateUser = useCallback(async (userId: string, updates: Partial<UserProfile>) => {
    setLoading(true)
    setError(null)

    try {
      await offlineDB.updateUser(userId, updates)

      // Update local state
      setUsers(prev => prev.map(user => 
        user._id === userId ? { ...user, ...updates } : user
      ))
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to update user')
      throw err
    } finally {
      setLoading(false)
    }
  }, [])

  const searchUsers = useCallback(async (query: string, currentUserId: string) => {
    setLoading(true)
    setError(null)

    try {
      const results = await offlineDB.searchUsers(query, currentUserId)
      return results
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Failed to search users')
      return []
    } finally {
      setLoading(false)
    }
  }, [])

  return {
    users,
    loading,
    error,
    loadUsers,
    createUser,
    updateUser,
    searchUsers
  }
}

