    const skip = (page - 1) * limit

    return new Promise((resolve) => {
      this.db.find({
        'metadata.date': { $gte: now },
        status: { $in: ['published', 'draft'] }
      })
        .sort({ 'metadata.date': 1 })
        .skip(skip)
        .limit(limit)
        .exec((err, docs) => {
          if (err) {
            resolve({
              success: false,
              error: err.message,
              timestamp: Date.now()
            })
          } else {
            resolve({
              success: true,
              data: docs,
              timestamp: Date.now()
            })
          }
        })
    })
  }

