            success: false,
            error: err.message,
            timestamp: Date.now()
          })
          return
        }

        if (!event) {
          resolve({
            success: false,
            error: 'Event not found',
            timestamp: Date.now()
          })
          return
        }

        // Check if user already registered
        const existingParticipant = event.participants.find(p => p.userId === userId)
        if (existingParticipant) {
          resolve({
            success: false,
