    return new Promise((resolve) => {
      this.db.count({}, (err, count) => {
        if (err) {
          resolve({
            success: false,
            error: err.message,
            timestamp: Date.now()
          })
        } else {
          resolve({
            success: true,
            data: count,
            timestamp: Date.now()
          })
        }
      })
    })
  }

