// User Controller - API endpoints for user operations
import { Request, Response } from 'express'
import { UserModel } from '../models/User'
import { UserSearchFilters } from '../types/database'

export class UserController {
  private userModel: UserModel

  constructor(userModel: UserModel) {
    this.userModel = userModel
  }

  // GET /api/users
  async getUsers(req: Request, res: Response): Promise<void> {
    try {
      const page = parseInt(req.query.page as string) || 1
      const limit = parseInt(req.query.limit as string) || 10

      const result = await this.userModel.findAll(page, limit)

      if (result.success) {
        res.json(result)
      } else {
        res.status(400).json(result)
      }
    } catch (error) {
      res.status(500).json({
        success: false,
        error: 'Internal server error',
        timestamp: Date.now()
      })
    }
  }

  // GET /api/users/:id
  async getUserById(req: Request, res: Response): Promise<void> {
    try {
      const { id } = req.params
      const result = await this.userModel.findById(id)

      if (result.success) {
        res.json(result)
      } else {
        res.status(404).json(result)
      }
    } catch (error) {
      res.status(500).json({
        success: false,
        error: 'Internal server error',
        timestamp: Date.now()
      })
    }
  }

  // GET /api/users/email/:email
  async getUserByEmail(req: Request, res: Response): Promise<void> {
    try {
      const { email } = req.params
      const result = await this.userModel.findByEmail(email)

      if (result.success) {
        res.json(result)
      } else {
        res.status(404).json(result)
      }
    } catch (error) {
      res.status(500).json({
        success: false,
        error: 'Internal server error',
        timestamp: Date.now()
      })
    }
  }

  // POST /api/users
  async createUser(req: Request, res: Response): Promise<void> {
    try {
      const userData = req.body

      // Basic validation
      if (!userData.email || !userData.profile?.name) {
