// SheSocial Backend Server
// Taiwan Luxury Social Platform API

import express from 'express'
import helmet from 'helmet'
import path from 'path'
import fs from 'fs'

// Middleware imports
import corsMiddleware from './middleware/cors'
import { developmentFormat, productionFormat, errorFormat } from './middleware/logger'

// Routes
import apiRoutes from './routes/api'

const app = express()
const PORT = process.env.PORT || 3001
const NODE_ENV = process.env.NODE_ENV || 'development'

// Ensure data directory exists
const dataDir = path.join(__dirname, '../data')
if (!fs.existsSync(dataDir)) {
  fs.mkdirSync(dataDir, { recursive: true })
  console.log(`ğŸ“ Created data directory: ${dataDir}`)
}

// Security middleware
app.use(helmet({
  contentSecurityPolicy: {
    directives: {
      defaultSrc: ["'self'"],
      styleSrc: ["'self'", "'unsafe-inline'"],
      scriptSrc: ["'self'"],
      imgSrc: ["'self'", "data:", "https:"],
      connectSrc: ["'self'"],
      fontSrc: ["'self'"],
      objectSrc: ["'none'"],
      mediaSrc: ["'self'"],
      frameSrc: ["'none'"],
    },
  },
  crossOriginEmbedderPolicy: false
}))

// CORS middleware
app.use(corsMiddleware)

// Request logging
if (NODE_ENV === 'production') {
  app.use(productionFormat)
  app.use(errorFormat)
} else {
  app.use(developmentFormat)
}

// Body parsing middleware
app.use(express.json({ limit: '50mb' }))
app.use(express.urlencoded({ extended: true, limit: '50mb' }))

// API routes
app.use('/api', apiRoutes)

// Health check endpoint (outside API prefix for load balancers)
app.get('/health', (req, res) => {
  res.json({
    success: true,
    message: 'SheSocial Backend is healthy',
    timestamp: Date.now(),
    environment: NODE_ENV,
    version: '1.0.0',
    uptime: process.uptime()
  })
