// JWT Authentication Middleware for SheSocial
import jwt from 'jsonwebtoken'
import { Request, Response, NextFunction } from 'express'
import { UserProfile } from '../types/database'

// Extend Express Request interface to include user
export interface AuthenticatedRequest extends Request {
  user?: UserProfile
  userId?: string
}

// JWT Secret from environment or default for development
const JWT_SECRET = process.env.JWT_SECRET || 'shesocial-taiwan-luxury-social-platform-secret-key-2025'
const JWT_EXPIRES_IN = process.env.JWT_EXPIRES_IN || '7d'

// Generate JWT token for user
export const generateToken = (user: UserProfile): string => {
  const payload = {
    userId: user._id,
    email: user.email,
    membershipType: user.membership.type,
    permissions: user.membership.permissions
  }

  return jwt.sign(payload, JWT_SECRET, { 
    expiresIn: JWT_EXPIRES_IN,
    issuer: 'shesocial.tw',
    audience: 'shesocial-users'
  })
}

// Verify JWT token and extract user info
export const verifyToken = (token: string): any => {
  try {
    return jwt.verify(token, JWT_SECRET, {
      issuer: 'shesocial.tw',
      audience: 'shesocial-users'
    })
  } catch (error) {
    throw new Error('Invalid token')
  }
}

// Middleware to authenticate requests
export const authenticateToken = async (
  req: AuthenticatedRequest, 
  res: Response, 
  next: NextFunction
): Promise<void> => {
  try {
    const authHeader = req.headers.authorization
    const token = authHeader && authHeader.split(' ')[1] // Bearer TOKEN

    if (!token) {
      res.status(401).json({
        success: false,
        error: 'éœ€è¦èªè­‰ä»¤ç‰Œ',
        message: 'Access token required',
        timestamp: Date.now()
      })
      return
    }

    const decoded = verifyToken(token)

    // Add user info to request object
    req.userId = decoded.userId
    req.user = {
      _id: decoded.userId,
      email: decoded.email,
      membership: {
        type: decoded.membershipType,
        permissions: decoded.permissions
      }
    } as UserProfile

    next()
  } catch (error) {
    res.status(403).json({
      success: false,
      error: 'ç„¡æ•ˆçš„èªè­‰ä»¤ç‰Œ',
      message: 'Invalid or expired token',
      timestamp: Date.now()
    })
  }
}

// Middleware to check specific membership levels
export const requireMembership = (...allowedTypes: UserProfile['membership']['type'][]) => {
  return (req: AuthenticatedRequest, res: Response, next: NextFunction): void => {
    if (!req.user) {
      res.status(401).json({
        success: false,
        error: 'éœ€è¦ç™»å…¥',
        message: 'Authentication required',
        timestamp: Date.now()
      })
      return
    }

