  // PUT /api/auth/change-password
  async changePassword(req: AuthenticatedRequest, res: Response): Promise<void> {
    try {
      const { currentPassword, newPassword } = req.body

      if (!currentPassword || !newPassword) {
        res.status(400).json({
          success: false,
          error: 'ç•¶å‰å¯†ç¢¼å’Œæ–°å¯†ç¢¼ç‚ºå¿…å¡«é …ç›®',
          message: 'Current password and new password are required',
          timestamp: Date.now()
        })
        return
      }

      if (newPassword.length < 8) {
        res.status(400).json({
          success: false,
          error: 'æ–°å¯†ç¢¼é•·åº¦å¿…é ˆè‡³å°‘8å€‹å­—ç¬¦',
          message: 'New password must be at least 8 characters long',
          timestamp: Date.now()
        })
        return
      }

      // Get current user
      const userResult = await this.userModel.findById(req.userId!)
      if (!userResult.success || !userResult.data) {
        res.status(404).json({
          success: false,
          error: 'ç”¨æˆ¶ä¸å­˜åœ¨',
          message: 'User not found',
          timestamp: Date.now()
        })
        return
      }

      // Verify current password
      const passwordMatch = await bcrypt.compare(currentPassword, userResult.data.password!)
      if (!passwordMatch) {
        res.status(401).json({
          success: false,
          error: 'ç•¶å‰å¯†ç¢¼éŒ¯èª¤',
          message: 'Current password is incorrect',
          timestamp: Date.now()
        })
        return
      }

      // Hash new password
      const saltRounds = 12
      const hashedNewPassword = await bcrypt.hash(newPassword, saltRounds)

      // Update password
      const updateResult = await this.userModel.update(req.userId!, {
        password: hashedNewPassword,
        updatedAt: new Date()
      })

      if (updateResult.success) {
        res.json({
          success: true,
          message: 'å¯†ç¢¼æ›´æ–°æˆåŠŸ',
          timestamp: Date.now()
        })
      } else {
        res.status(400).json(updateResult)
      }
    } catch (error) {
      res.status(500).json({
        success: false,
        error: 'å¯†ç¢¼æ›´æ–°å¤±æ•—',
        message: 'Password change failed',
        timestamp: Date.now()
      })
    }
  }

  // Helper method to get membership permissions
  private getMembershipPermissions(membershipType: UserProfile['membership']['type']): UserProfile['membership']['permissions'] {
    switch (membershipType) {
