        success: false,
        error: 'Collection and timestamp are required',
        timestamp: Date.now()
      })
      return
    }

    let result
    switch (collection) {
      case 'users':
        result = await userModel.getModifiedSince(timestamp)
        break
      case 'events':
        result = await eventModel.getModifiedSince(timestamp)
        break
      case 'bookings':
