      .limit(50) // Process in batches
      .toArray()

    if (pendingItems.length === 0) {
      console.log('ğŸ“¤ No local changes to push')
      return
    }

    console.log(`ğŸ“¤ Pushing ${pendingItems.length} local changes...`)

    for (const item of pendingItems) {
      try {
