// Main sync function - called by Service Worker or periodic sync
async syncToServer(): Promise<boolean> {
  if (this.syncInProgress || !navigator.onLine) {
    return false
  }

  // Check authentication status
  const authStore = useAuthStore.getState()
  if (!authStore.isAuthenticated) {
    console.log('âš ï¸ Skipping sync - user not authenticated')
    return false
  }

  this.syncInProgress = true
  console.log('ğŸ”„ Starting sync to server...')

  try {
    // Step 1: Push local changes to server
    await this.pushLocalChanges()

    // Step 2: Pull server changes to local
    await this.pullServerChanges()

    console.log('âœ… Sync completed successfully')
    return true
  } catch (error) {
    console.error('âŒ Sync failed:', error)

    // Handle authentication errors
    if (error instanceof Error && error.message.includes('401')) {
      console.log('ğŸ”‘ Authentication error - attempting token refresh')
      await authStore.refreshToken()
      return false
    }

    return false
  } finally {
    this.syncInProgress = false
  }
}

