// Initialize sync service with enhanced background sync integration
export const initializeSyncService = (): void => {
  console.log('ğŸ”„ Sync service initialized')

  // Register service worker message handler for background sync
  if ('serviceWorker' in navigator) {
    navigator.serviceWorker.addEventListener('message', (event) => {
      const { data } = event

      switch (data?.type) {
        case 'BACKGROUND_SYNC':
          console.log('ğŸ“± Background sync triggered by Service Worker')
          syncService.syncToServer()
          break

        case 'NETWORK_CHANGED':
          console.log(`ğŸŒ Network status changed: ${data.isOnline ? 'online' : 'offline'}`)
          if (data.isOnline) {
            syncService.syncToServer()
          }
          break

        case 'SYNC_REQUEST':
          console.log(`ğŸ”„ Manual sync requested: ${data.collection || 'all'}`)
          if (data.collection) {
            syncService.syncSpecificCollection(data.collection)
          } else {
            syncService.syncToServer()
          }
          break
      }
    })

    // Register for background sync if supported
    navigator.serviceWorker.ready.then(registration => {
      if ('sync' in registration) {
        console.log('âœ… Background Sync API supported')
        syncService.registerBackgroundSync()
      } else {
        console.warn('âš ï¸ Background Sync API not supported - using periodic sync only')
      }
    })
  }

  // Listen for authentication changes to trigger sync
  const authStore = useAuthStore.getState()
  authStore.subscribe((state) => {
    if (state.isAuthenticated && navigator.onLine) {
      console.log('ğŸ”‘ User authenticated - triggering sync')
      syncService.syncToServer()
    }
  })
}

