   297â†’                  <span>Get Started</span>
   298â†’                </button>
   299â†’              </div>
   300â†’            )}
   301â†’          </div>
   302â†’        </div>
   303â†’      )}
   304â†’
   305â†’      {/* Page Title Bar (Mobile) */}
   306â†’      <div className="md:hidden bg-gray-50 border-t border-gray-200 px-4 py-2">
   307â†’        <h1 className="text-lg font-semibold text-gray-900">{getViewTitle()}</h1>
   308â†’        {currentUser && (
   309â†’          <p className="text-sm text-gray-600">
   310â†’            Welcome back, {currentUser.name.split(' ')[0]}
   311â†’          </p>
   312â†’        )}
   313â†’      </div>
   314â†’    </nav>
   315â†’  );
   316â†’};
   317â†’
   318â†’export default Navigation;

