import React, { useState } from 'react';
import { getCurrentUser, clearAuth } from '../services/api';
import { 
  Menu, 
  X, 
  Home, 
  Target, 
  BarChart3, 
  User, 
  LogOut, 
  Bell,
  Search,
  ChevronDown
} from 'lucide-react';

interface NavigationProps {
  onNavigate: (view: string) => void;
  currentView: string;
}

const Navigation: React.FC<NavigationProps> = ({ onNavigate, currentView }) => {
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [isProfileDropdownOpen, setIsProfileDropdownOpen] = useState(false);
  const [isNotificationsOpen, setIsNotificationsOpen] = useState(false);

  const currentUser = getCurrentUser();
  const isInvestor = currentUser?.user_type === 'investor';

  const handleLogout = () => {
    clearAuth();
    onNavigate('login');
  };

  const navItems = [
    { id: 'home', label: 'Browse DROs', icon: Home, show: true },
    { id: 'dashboard', label: isInvestor ? 'My Portfolio' : 'My DROs', icon: BarChart3, show: !!currentUser },
    { id: 'opportunities', label: 'Opportunities', icon: Target, show: isInvestor },
  ];

  const getInitials = (name: string) => {
    return name?.split(' ')
      .map(word => word[0])
      .join('')
      .toUpperCase()
      .slice(0, 2) || 'U';
  };

  const getViewTitle = () => {
    switch (currentView) {
      case 'home': return 'Browse DROs';
      case 'dashboard': return isInvestor ? 'My Portfolio' : 'My DROs';
      case 'opportunities': return 'Investment Opportunities';
      default: return 'SIREX';
    }
  };

  return (
    <nav className="bg-white shadow-sm border-b border-gray-200 sticky top-0 z-40">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="flex justify-between items-center h-16">
          {/* Logo and Brand */}
          <div className="flex items-center">
            <button
              onClick={() => onNavigate('home')}
              className="flex items-center space-x-3"
            >
              <div className="text-2xl font-bold sirex-text-gradient">SIREX</div>
              <div className="hidden md:block text-sm text-gray-500">Sand in River EX</div>
            </button>
          </div>

          {/* Desktop Navigation */}
          <div className="hidden md:flex items-center space-x-8">
            {navItems.map((item) => {
              if (!item.show) return null;
              const Icon = item.icon;
              return (
                <button
                  key={item.id}
                  onClick={() => onNavigate(item.id)}
                  className={`flex items-center space-x-2 px-3 py-2 rounded-md text-sm font-medium transition-colors ${
                    currentView === item.id
                      ? 'text-sirex-blue bg-sirex-blue bg-opacity-10'
                      : 'text-gray-700 hover:text-sirex-blue hover:bg-gray-50'
                  }`}
                >
                  <Icon className="w-4 h-4" />
                  <span>{item.label}</span>
                </button>
              );
            })}
          </div>

          {/* Right Side - User Actions */}
          <div className="flex items-center space-x-4">
            {currentUser ? (
              <>
                {/* Search (Desktop) */}
                <div className="hidden lg:block">
                  <div className="relative">
                    <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
                    <input
                      type="text"
                      placeholder="Search DROs..."
                      className="pl-10 pr-4 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-sirex-blue focus:border-transparent w-64"
                    />
                  </div>
                </div>

                {/* Notifications */}
                <div className="relative">
                  <button
                    onClick={() => setIsNotificationsOpen(!isNotificationsOpen)}
                    className="p-2 text-gray-400 hover:text-gray-600 relative"
                  >
                    <Bell className="w-5 h-5" />
                    <span className="absolute top-1 right-1 w-2 h-2 bg-red-500 rounded-full"></span>
                  </button>

                  {/* Notifications Dropdown */}
                  {isNotificationsOpen && (
                    <>
                      <div 
                        className="fixed inset-0 z-10" 
                        onClick={() => setIsNotificationsOpen(false)}
                      ></div>
                      <div className="absolute right-0 mt-2 w-80 bg-white rounded-md shadow-lg ring-1 ring-black ring-opacity-5 z-20">
                        <div className="p-4 border-b border-gray-200">
                          <h3 className="text-sm font-medium text-gray-900">Notifications</h3>
                        </div>
                        <div className="max-h-64 overflow-y-auto">
                          <div className="p-4 text-center text-gray-500">
                            <Bell className="w-8 h-8 mx-auto mb-2 text-gray-300" />
                            <p className="text-sm">No new notifications</p>
                          </div>
                        </div>
                      </div>
                    </>
                  )}
                </div>

                {/* Profile Dropdown */}
                <div className="relative">
                  <button
                    onClick={() => setIsProfileDropdownOpen(!isProfileDropdownOpen)}
                    className="flex items-center space-x-2 p-2 rounded-md hover:bg-gray-50"
                  >
                    <div className="w-8 h-8 bg-sirex-blue text-white rounded-full flex items-center justify-center text-sm font-medium">
                      {getInitials(currentUser.name)}
                    </div>
                    <div className="hidden md:block text-left">
                      <div className="text-sm font-medium text-gray-900">{currentUser.name}</div>
                      <div className="text-xs text-gray-500 capitalize">{currentUser.user_type}</div>
                    </div>
                    <ChevronDown className="w-4 h-4 text-gray-400" />
                  </button>

                  {/* Profile Dropdown Menu */}
                  {isProfileDropdownOpen && (
                    <>
                      <div 
                        className="fixed inset-0 z-10" 
                        onClick={() => setIsProfileDropdownOpen(false)}
                      ></div>
                      <div className="absolute right-0 mt-2 w-56 bg-white rounded-md shadow-lg ring-1 ring-black ring-opacity-5 z-20">
                        <div className="p-4 border-b border-gray-200">
                          <div className="flex items-center space-x-3">
                            <div className="w-10 h-10 bg-sirex-blue text-white rounded-full flex items-center justify-center text-sm font-medium">
                              {getInitials(currentUser.name)}
                            </div>
                            <div>
                              <div className="text-sm font-medium text-gray-900">{currentUser.name}</div>
                              <div className="text-xs text-gray-500">{currentUser.email}</div>
                              <div className="text-xs text-sirex-blue capitalize">{currentUser.user_type} Account</div>
                            </div>
                          </div>
                        </div>
                        <div className="py-1">
                          <button
                            onClick={() => {
                              onNavigate('profile');
                              setIsProfileDropdownOpen(false);
                            }}
                            className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 w-full"
                          >
                            <User className="w-4 h-4 mr-3" />
                            Profile Settings
                          </button>
                          <button
                            onClick={handleLogout}
                            className="flex items-center px-4 py-2 text-sm text-gray-700 hover:bg-gray-100 w-full"
                          >
                            <LogOut className="w-4 h-4 mr-3" />
                            Sign Out
                          </button>
                        </div>
                      </div>
                    </>
                  )}
                </div>
              </>
            ) : (
              <div className="flex items-center space-x-4">
                <button
                  onClick={() => onNavigate('login')}
                  className="text-gray-700 hover:text-sirex-blue font-medium"
                >
                  Sign In
                </button>
                <button
                  onClick={() => onNavigate('register')}
                  className="btn-primary text-sm"
                >
                  Get Started
                </button>
              </div>
            )}

            {/* Mobile menu button */}
            <div className="md:hidden">
              <button
                onClick={() => setIsMobileMenuOpen(!isMobileMenuOpen)}
                className="p-2 text-gray-400 hover:text-gray-600"
              >
                {isMobileMenuOpen ? (
                  <X className="w-6 h-6" />
                ) : (
                  <Menu className="w-6 h-6" />
                )}
              </button>
            </div>
          </div>
        </div>

        {/* Mobile Search Bar */}
        {currentUser && (
          <div className="lg:hidden pb-3">
            <div className="relative">
              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
              <input
                type="text"
                placeholder="Search DROs..."
                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-md text-sm focus:outline-none focus:ring-2 focus:ring-sirex-blue focus:border-transparent"
              />
            </div>
          </div>
        )}
      </div>

      {/* Mobile Navigation Menu */}
      {isMobileMenuOpen && (
        <div className="md:hidden border-t border-gray-200 bg-white">
          <div className="px-2 pt-2 pb-3 space-y-1">
            {navItems.map((item) => {
              if (!item.show) return null;
              const Icon = item.icon;
              return (
                <button
                  key={item.id}
                  onClick={() => {
                    onNavigate(item.id);
                    setIsMobileMenuOpen(false);
                  }}
                  className={`flex items-center space-x-3 px-3 py-2 rounded-md text-base font-medium w-full ${
                    currentView === item.id
                      ? 'text-sirex-blue bg-sirex-blue bg-opacity-10'
                      : 'text-gray-700 hover:text-sirex-blue hover:bg-gray-50'
                  }`}
                >
                  <Icon className="w-5 h-5" />
                  <span>{item.label}</span>
                </button>
              );
            })}

            {/* Mobile Auth Buttons */}
            {!currentUser && (
              <div className="border-t border-gray-200 pt-4 mt-4 space-y-1">
                <button
                  onClick={() => {
                    onNavigate('login');
                    setIsMobileMenuOpen(false);
                  }}
                  className="flex items-center space-x-3 px-3 py-2 rounded-md text-base font-medium w-full text-gray-700 hover:text-sirex-blue hover:bg-gray-50"
                >
                  <User className="w-5 h-5" />
                  <span>Sign In</span>
                </button>
                <button
                  onClick={() => {
                    onNavigate('register');
                    setIsMobileMenuOpen(false);
                  }}
                  className="flex items-center space-x-3 px-3 py-2 rounded-md text-base font-medium w-full text-white bg-sirex-blue hover:bg-sirex-light-blue"
                >
                  <Target className="w-5 h-5" />
                  <span>Get Started</span>
                </button>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Page Title Bar (Mobile) */}
      <div className="md:hidden bg-gray-50 border-t border-gray-200 px-4 py-2">
        <h1 className="text-lg font-semibold text-gray-900">{getViewTitle()}</h1>
        {currentUser && (
          <p className="text-sm text-gray-600">
            Welcome back, {currentUser.name.split(' ')[0]}
          </p>
        )}
      </div>
    </nav>
  );
};

export default Navigation;

