   233â†’        </div>
   234â†’
   235â†’        {/* Mobile Search Bar */}
   236â†’        {currentUser && (
   237â†’          <div className="lg:hidden pb-3">
   238â†’            <div className="relative">
   239â†’              <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-4 h-4" />
