import React, { useState, useEffect } from 'react';
import type { DRO } from '../services/api';
import { droAPI, revenueAPI } from '../services/api';
import { X, DollarSign, TrendingUp, Calculator, AlertTriangle, CheckCircle } from 'lucide-react';

interface InvestmentModalProps {
  dro: DRO;
  onClose: () => void;
  onSuccess: () => void;
}

const InvestmentModal: React.FC<InvestmentModalProps> = ({ dro, onClose, onSuccess }) => {
  const [investmentAmount, setInvestmentAmount] = useState('');
  const [projectedReturns, setProjectedReturns] = useState<any>(null);
  const [loading, setLoading] = useState(false);
  const [calculating, setCalculating] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [success, setSuccess] = useState(false);

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatPercentage = (value: number) => {
    return `${value.toFixed(2)}%`;
  };

  // Calculate projected returns when investment amount changes
  useEffect(() => {
    const calculateReturns = async () => {
      if (!investmentAmount || parseFloat(investmentAmount) <= 0) {
        setProjectedReturns(null);
        return;
      }

      try {
        setCalculating(true);
        const response = await revenueAPI.calculateReturns({
          dro_id: dro.id,
          investment_amount: parseFloat(investmentAmount),
          projected_monthly_revenue: 50000, // Default assumption
          duration_months: dro.duration_months,
        });
        setProjectedReturns(response.projected_returns);
      } catch (err) {
        console.error('Error calculating returns:', err);
        setProjectedReturns(null);
      } finally {
        setCalculating(false);
      }
    };

    const debounceTimer = setTimeout(calculateReturns, 500);
    return () => clearTimeout(debounceTimer);
  }, [investmentAmount, dro.id, dro.duration_months]);

  const handleInvest = async () => {
    if (!investmentAmount || parseFloat(investmentAmount) <= 0) {
      setError('Please enter a valid investment amount');
      return;
    }

    if (parseFloat(investmentAmount) < dro.minimum_investment) {
      setError(`Minimum investment is ${formatCurrency(dro.minimum_investment)}`);
      return;
    }

    const remainingTarget = dro.target_amount - (dro.raised_amount || 0);
    if (parseFloat(investmentAmount) > remainingTarget) {
      setError(`Investment amount exceeds remaining target of ${formatCurrency(remainingTarget)}`);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      await droAPI.investInDRO(dro.id, parseFloat(investmentAmount));
      setSuccess(true);

      // Show success message for 2 seconds, then close
      setTimeout(() => {
        onSuccess();
      }, 2000);

    } catch (err: any) {
      console.error('Investment error:', err);
      const errorMessage = err.response?.data?.error || 'Investment failed. Please try again.';
      setError(errorMessage);
    } finally {
      setLoading(false);
    }
  };

  const validationErrors = [];
  if (investmentAmount && parseFloat(investmentAmount) < dro.minimum_investment) {
    validationErrors.push(`Minimum investment: ${formatCurrency(dro.minimum_investment)}`);
  }
  if (investmentAmount && parseFloat(investmentAmount) > (dro.target_amount - (dro.raised_amount || 0))) {
    validationErrors.push(`Exceeds remaining target: ${formatCurrency(dro.target_amount - (dro.raised_amount || 0))}`);
  }

  if (success) {
    return (
      <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
        <div className="bg-white rounded-lg max-w-md w-full p-6 text-center">
          <CheckCircle className="w-16 h-16 text-green-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Investment Successful!</h2>
          <p className="text-gray-600 mb-4">
            You've successfully invested {formatCurrency(parseFloat(investmentAmount))} in {dro.title}.
          </p>
          <p className="text-sm text-gray-500">
            You'll start receiving revenue distributions once the MSME reports their daily revenue.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div>
            <h2 className="text-xl font-semibold text-gray-900">Invest in DRO</h2>
            <p className="text-sm text-gray-600">{dro.title}</p>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600"
            disabled={loading}
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="p-6">
          {/* DRO Summary */}
          <div className="bg-gray-50 rounded-lg p-4 mb-6">
            <div className="grid grid-cols-2 gap-4">
              <div>
                <div className="text-sm text-gray-600">Revenue Share</div>
                <div className="font-semibold text-sirex-blue">
                  {formatPercentage(dro.revenue_percentage)}
                </div>
              </div>
              <div>
                <div className="text-sm text-gray-600">Duration</div>
                <div className="font-semibold">{dro.duration_months} months</div>
              </div>
              <div>
                <div className="text-sm text-gray-600">Min Investment</div>
                <div className="font-semibold">{formatCurrency(dro.minimum_investment)}</div>
              </div>
              <div>
                <div className="text-sm text-gray-600">Remaining Target</div>
                <div className="font-semibold">
                  {formatCurrency(dro.target_amount - (dro.raised_amount || 0))}
                </div>
              </div>
            </div>
          </div>

          {/* Investment Amount Input */}
          <div className="mb-6">
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Investment Amount
            </label>
            <div className="relative">
              <DollarSign className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
              <input
                type="number"
                value={investmentAmount}
                onChange={(e) => setInvestmentAmount(e.target.value)}
                placeholder={`Min: ${dro.minimum_investment.toLocaleString()}`}
                className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-sirex-blue focus:border-transparent text-lg"
                disabled={loading}
              />
            </div>

            {/* Quick Amount Buttons */}
            <div className="mt-3 flex flex-wrap gap-2">
              {[dro.minimum_investment, 5000, 10000, 25000].map((amount) => (
                <button
                  key={amount}
                  onClick={() => setInvestmentAmount(amount.toString())}
                  className="px-3 py-1 text-sm border border-gray-300 rounded hover:bg-gray-50"
                  disabled={loading || amount > (dro.target_amount - (dro.raised_amount || 0))}
                >
                  {formatCurrency(amount)}
                </button>
              ))}
            </div>

            {/* Validation Errors */}
            {validationErrors.length > 0 && (
              <div className="mt-2 flex items-start text-red-600 text-sm">
                <AlertTriangle className="w-4 h-4 mr-1 mt-0.5 flex-shrink-0" />
                <ul>
                  {validationErrors.map((error, index) => (
                    <li key={index}>{error}</li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          {/* Projected Returns */}
          {investmentAmount && parseFloat(investmentAmount) > 0 && (
            <div className="mb-6">
              <div className="flex items-center mb-3">
                <Calculator className="w-5 h-5 text-gray-400 mr-2" />
                <h3 className="text-lg font-medium text-gray-900">Projected Returns</h3>
                {calculating && (
                  <div className="ml-2 text-sm text-gray-500">Calculating...</div>
                )}
              </div>

              {projectedReturns && !calculating ? (
                <div className="bg-sirex-blue bg-opacity-5 border border-sirex-blue border-opacity-20 rounded-lg p-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <div className="text-sm text-gray-600">Ownership Percentage</div>
                      <div className="text-lg font-semibold text-sirex-blue">
                        {formatPercentage(projectedReturns.ownership_percentage)}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Annual Return Rate</div>
                      <div className="text-lg font-semibold text-sirex-blue">
                        {formatPercentage(projectedReturns.annual_return_rate)}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Monthly Average</div>
                      <div className="text-lg font-semibold">
                        {formatCurrency(projectedReturns.monthly_average_return)}
                      </div>
                    </div>
                    <div>
                      <div className="text-sm text-gray-600">Total Potential Return</div>
                      <div className="text-lg font-semibold text-green-600">
                        {formatCurrency(projectedReturns.total_potential_return)}
                      </div>
                    </div>
                  </div>

                  <div className="mt-3 text-xs text-gray-500">
                    * Projections based on estimated monthly revenue. Actual returns may vary.
                  </div>
                </div>
              ) : calculating ? (
                <div className="bg-gray-50 rounded-lg p-4 animate-pulse">
                  <div className="h-4 bg-gray-200 rounded mb-2"></div>
                  <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                </div>
              ) : null}
            </div>
          )}

          {/* Risk Disclosure */}
          <div className="mb-6 bg-yellow-50 border border-yellow-200 rounded-lg p-4">
            <div className="flex items-start">
              <AlertTriangle className="w-5 h-5 text-yellow-600 mr-2 mt-0.5 flex-shrink-0" />
              <div className="text-sm">
                <div className="font-medium text-yellow-800 mb-1">Investment Risk Disclosure</div>
                <ul className="text-yellow-700 space-y-1">
                  <li>â€¢ Revenue-based investments carry risk and returns are not guaranteed</li>
                  <li>â€¢ Actual returns depend on the business's daily revenue performance</li>
                  <li>â€¢ You may lose some or all of your investment</li>
                  <li>â€¢ Please consider your risk tolerance before investing</li>
                </ul>
              </div>
            </div>
          </div>

          {/* Error Message */}
          {error && (
            <div className="mb-4 bg-red-50 border border-red-200 rounded-lg p-4">
              <div className="flex items-start">
                <AlertTriangle className="w-5 h-5 text-red-600 mr-2 mt-0.5 flex-shrink-0" />
                <div className="text-sm text-red-700">{error}</div>
              </div>
            </div>
          )}

          {/* Action Buttons */}
          <div className="flex space-x-4">
            <button
              onClick={onClose}
              className="flex-1 btn-secondary"
              disabled={loading}
            >
              Cancel
            </button>
            <button
              onClick={handleInvest}
              disabled={
                loading || 
                !investmentAmount || 
                parseFloat(investmentAmount) <= 0 || 
                validationErrors.length > 0
              }
              className="flex-1 btn-primary flex items-center justify-center"
            >
              {loading ? (
                <>
                  <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white mr-2"></div>
                  Processing...
                </>
              ) : (
                <>
                  <TrendingUp className="w-4 h-4 mr-2" />
                  Invest {investmentAmount ? formatCurrency(parseFloat(investmentAmount)) : ''}
                </>
              )}
            </button>
          </div>
        </div>
      </div>
    </div>
  );
};

export default InvestmentModal;

