import React, { useState, useEffect } from 'react';
import type { DRO, Investment } from '../services/api';
import { droAPI } from '../services/api';
import { 
  X, 
  Building2, 
  TrendingUp, 
  Users, 
  Calendar, 
  DollarSign, 
  Target,
  BarChart3,
  Info
} from 'lucide-react';

interface DRODetailModalProps {
  dro: DRO;
  onClose: () => void;
  onInvest: (dro: DRO) => void;
}

const DRODetailModal: React.FC<DRODetailModalProps> = ({ dro, onClose, onInvest }) => {
  const [detailedDRO, setDetailedDRO] = useState<DRO | null>(null);
  const [investments, setInvestments] = useState<Investment[]>([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchDRODetails = async () => {
      try {
        const response = await droAPI.getDRO(dro.id);
        setDetailedDRO(response.dro);
        setInvestments(response.investments);
      } catch (error) {
        console.error('Error fetching DRO details:', error);
        // Fallback to basic DRO data
        setDetailedDRO(dro);
        setInvestments([]);
      } finally {
        setLoading(false);
      }
    };

    fetchDRODetails();
  }, [dro.id]);

  const displayDRO = detailedDRO || dro;
  const fundingPercentage = displayDRO.target_amount > 0 ? 
    ((displayDRO.raised_amount || 0) / displayDRO.target_amount) * 100 : 0;

  const formatCurrency = (amount: number) => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: 'USD',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    return new Date(dateString).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
    });
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'active': return 'bg-green-100 text-green-800';
      case 'pending': return 'bg-yellow-100 text-yellow-800';
      case 'completed': return 'bg-blue-100 text-blue-800';
      case 'cancelled': return 'bg-red-100 text-red-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  const getIndustryIcon = (industry: string) => {
    const industryLower = industry.toLowerCase();
    if (industryLower.includes('tech') || industryLower.includes('software')) {
      return 'ğŸ’»';
    } else if (industryLower.includes('food') || industryLower.includes('restaurant')) {
      return 'ğŸ½ï¸';
    } else if (industryLower.includes('retail') || industryLower.includes('shop')) {
      return 'ğŸ›ï¸';
    } else if (industryLower.includes('health') || industryLower.includes('medical')) {
      return 'ğŸ¥';
    } else if (industryLower.includes('education')) {
      return 'ğŸ“š';
    } else {
      return 'ğŸ¢';
    }
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
      <div className="bg-white rounded-lg max-w-4xl w-full max-h-[90vh] overflow-y-auto">
        {/* Header */}
        <div className="flex items-center justify-between p-6 border-b border-gray-200">
          <div className="flex items-center space-x-3">
            <div className="text-3xl">{getIndustryIcon(displayDRO.industry)}</div>
            <div>
              <h2 className="text-2xl font-semibold text-gray-900">{displayDRO.title}</h2>
              <div className="flex items-center space-x-4 mt-1">
                <p className="text-gray-600 flex items-center">
                  <Building2 className="w-4 h-4 mr-1" />
                  {displayDRO.msme_name || 'Unknown Company'}
                </p>
                <span className={`px-3 py-1 rounded-full text-xs font-medium ${getStatusColor(displayDRO.status)}`}>
                  {displayDRO.status?.charAt(0).toUpperCase() + displayDRO.status?.slice(1)}
                </span>
              </div>
            </div>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="p-6">
          {/* Description */}
          {displayDRO.description && (
            <div className="mb-8">
              <h3 className="text-lg font-semibold text-gray-900 mb-3">About This Opportunity</h3>
              <p className="text-gray-700 leading-relaxed">{displayDRO.description}</p>
            </div>
          )}

          {/* Key Metrics Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-sirex-blue bg-opacity-5 border border-sirex-blue border-opacity-20 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <TrendingUp className="w-6 h-6 text-sirex-blue" />
                <span className="text-2xl font-bold text-sirex-blue">
                  {displayDRO.revenue_percentage}%
                </span>
              </div>
              <div className="text-sm text-gray-600">Revenue Share</div>
            </div>

            <div className="bg-green-50 border border-green-200 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <DollarSign className="w-6 h-6 text-green-600" />
                <span className="text-lg font-bold text-green-600">
                  {formatCurrency(displayDRO.minimum_investment)}
                </span>
              </div>
              <div className="text-sm text-gray-600">Min Investment</div>
            </div>

            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <Users className="w-6 h-6 text-blue-600" />
                <span className="text-2xl font-bold text-blue-600">
                  {displayDRO.investor_count || 0}
                </span>
              </div>
              <div className="text-sm text-gray-600">Investors</div>
            </div>

            <div className="bg-purple-50 border border-purple-200 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <Calendar className="w-6 h-6 text-purple-600" />
                <span className="text-2xl font-bold text-purple-600">
                  {displayDRO.duration_months}
                </span>
              </div>
              <div className="text-sm text-gray-600">Months Duration</div>
            </div>
          </div>

          {/* Funding Progress */}
          <div className="mb-8">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                <Target className="w-5 h-5 mr-2" />
                Funding Progress
              </h3>
              <span className="text-lg font-bold text-sirex-blue">
                {fundingPercentage.toFixed(1)}%
              </span>
            </div>

            <div className="bg-gray-200 rounded-full h-4 mb-4">
              <div 
                className="sirex-gradient h-4 rounded-full transition-all duration-500"
                style={{ width: `${Math.min(fundingPercentage, 100)}%` }}
              ></div>
            </div>

            <div className="grid grid-cols-3 gap-4 text-center">
              <div>
                <div className="text-2xl font-bold text-gray-900">
                  {formatCurrency(displayDRO.raised_amount || 0)}
                </div>
                <div className="text-sm text-gray-600">Raised</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-sirex-blue">
                  {formatCurrency(displayDRO.target_amount)}
                </div>
                <div className="text-sm text-gray-600">Target</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-green-600">
                  {formatCurrency(displayDRO.target_amount - (displayDRO.raised_amount || 0))}
                </div>
                <div className="text-sm text-gray-600">Remaining</div>
              </div>
            </div>
          </div>

          {/* Investment Details */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-8 mb-8">
            {/* Investment Terms */}
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <Info className="w-5 h-5 mr-2" />
                Investment Terms
              </h3>
              <div className="space-y-3">
                <div className="flex justify-between py-2 border-b border-gray-100">
                  <span className="text-gray-600">Industry</span>
                  <span className="font-medium">{displayDRO.industry}</span>
                </div>
                <div className="flex justify-between py-2 border-b border-gray-100">
                  <span className="text-gray-600">Revenue Share</span>
                  <span className="font-medium text-sirex-blue">{displayDRO.revenue_percentage}%</span>
                </div>
                <div className="flex justify-between py-2 border-b border-gray-100">
                  <span className="text-gray-600">Minimum Investment</span>
                  <span className="font-medium">{formatCurrency(displayDRO.minimum_investment)}</span>
                </div>
                <div className="flex justify-between py-2 border-b border-gray-100">
                  <span className="text-gray-600">Duration</span>
                  <span className="font-medium">{displayDRO.duration_months} months</span>
                </div>
                <div className="flex justify-between py-2 border-b border-gray-100">
                  <span className="text-gray-600">Created</span>
                  <span className="font-medium">{formatDate(displayDRO.created_at)}</span>
                </div>
              </div>
            </div>

            {/* Recent Investors */}
            <div>
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <BarChart3 className="w-5 h-5 mr-2" />
                Recent Investors
              </h3>
              {loading ? (
                <div className="space-y-3">
                  {[1, 2, 3].map((i) => (
                    <div key={i} className="animate-pulse">
                      <div className="h-4 bg-gray-200 rounded mb-2"></div>
                      <div className="h-3 bg-gray-200 rounded w-3/4"></div>
                    </div>
                  ))}
                </div>
              ) : investments.length > 0 ? (
                <div className="space-y-3">
                  {investments.slice(0, 5).map((investment, index) => (
                    <div key={index} className="flex justify-between items-center py-2 border-b border-gray-100">
                      <div>
                        <div className="font-medium text-gray-900">
                          {investment.investor_name || 'Anonymous Investor'}
                        </div>
                        <div className="text-sm text-gray-500">
                          {formatDate(investment.investment_date)}
                        </div>
                      </div>
                      <div className="text-right">
                        <div className="font-medium text-sirex-blue">
                          {formatCurrency(investment.amount)}
                        </div>
                        <div className="text-xs text-gray-500 capitalize">
                          {investment.status}
                        </div>
                      </div>
                    </div>
                  ))}
                  {investments.length > 5 && (
                    <div className="text-center text-sm text-gray-500 pt-2">
                      +{investments.length - 5} more investors
                    </div>
                  )}
                </div>
              ) : (
                <div className="text-center py-8 text-gray-500">
                  <Users className="w-12 h-12 mx-auto mb-2 text-gray-300" />
                  <p>No investors yet</p>
                  <p className="text-sm">Be the first to invest!</p>
                </div>
              )}
            </div>
          </div>

          {/* Risk Notice */}
          <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4 mb-6">
            <div className="flex items-start">
              <div className="text-yellow-600 mr-3 mt-1">âš ï¸</div>
              <div>
                <h4 className="font-medium text-yellow-800 mb-1">Investment Risk Notice</h4>
                <p className="text-sm text-yellow-700">
                  Revenue-based investments carry inherent risks. Returns are not guaranteed and depend on the business's 
                  actual revenue performance. You should carefully consider your risk tolerance and investment objectives 
                  before investing.
                </p>
              </div>
            </div>
          </div>

          {/* Action Buttons */}
          <div className="flex space-x-4">
            <button
              onClick={onClose}
              className="flex-1 btn-secondary"
            >
              Close
            </button>

            {displayDRO.status === 'active' && (
              <button
                onClick={() => onInvest(displayDRO)}
                className="flex-1 btn-primary flex items-center justify-center"
              >
                <Target className="w-5 h-5 mr-2" />
                Invest Now
              </button>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default DRODetailModal;

