          <div className="flex items-center space-x-3">
            <div className="text-3xl">{getIndustryIcon(displayDRO.industry)}</div>
            <div>
              <h2 className="text-2xl font-semibold text-gray-900">{displayDRO.title}</h2>
              <div className="flex items-center space-x-4 mt-1">
                <p className="text-gray-600 flex items-center">
                  <Building2 className="w-4 h-4 mr-1" />
                  {displayDRO.msme_name || 'Unknown Company'}
                </p>
                <span className={`px-3 py-1 rounded-full text-xs font-medium ${getStatusColor(displayDRO.status)}`}>
                  {displayDRO.status?.charAt(0).toUpperCase() + displayDRO.status?.slice(1)}
                </span>
              </div>
            </div>
          </div>
          <button
            onClick={onClose}
            className="text-gray-400 hover:text-gray-600"
          >
            <X className="w-6 h-6" />
          </button>
        </div>

        <div className="p-6">
          {/* Description */}
          {displayDRO.description && (
            <div className="mb-8">
              <h3 className="text-lg font-semibold text-gray-900 mb-3">About This Opportunity</h3>
              <p className="text-gray-700 leading-relaxed">{displayDRO.description}</p>
            </div>
          )}

          {/* Key Metrics Grid */}
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
            <div className="bg-sirex-blue bg-opacity-5 border border-sirex-blue border-opacity-20 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <TrendingUp className="w-6 h-6 text-sirex-blue" />
                <span className="text-2xl font-bold text-sirex-blue">
                  {displayDRO.revenue_percentage}%
                </span>
              </div>
              <div className="text-sm text-gray-600">Revenue Share</div>
            </div>

            <div className="bg-green-50 border border-green-200 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <DollarSign className="w-6 h-6 text-green-600" />
                <span className="text-lg font-bold text-green-600">
                  {formatCurrency(displayDRO.minimum_investment)}
                </span>
              </div>
              <div className="text-sm text-gray-600">Min Investment</div>
            </div>

            <div className="bg-blue-50 border border-blue-200 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <Users className="w-6 h-6 text-blue-600" />
                <span className="text-2xl font-bold text-blue-600">
                  {displayDRO.investor_count || 0}
                </span>
              </div>
              <div className="text-sm text-gray-600">Investors</div>
            </div>

            <div className="bg-purple-50 border border-purple-200 rounded-lg p-4">
              <div className="flex items-center justify-between mb-2">
                <Calendar className="w-6 h-6 text-purple-600" />
                <span className="text-2xl font-bold text-purple-600">
                  {displayDRO.duration_months}
                </span>
              </div>
              <div className="text-sm text-gray-600">Months Duration</div>
            </div>
          </div>

          {/* Funding Progress */}
          <div className="mb-8">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-gray-900 flex items-center">
                <Target className="w-5 h-5 mr-2" />
                Funding Progress
              </h3>
              <span className="text-lg font-bold text-sirex-blue">
                {fundingPercentage.toFixed(1)}%
              </span>
            </div>

            <div className="bg-gray-200 rounded-full h-4 mb-4">
              <div 
                className="sirex-gradient h-4 rounded-full transition-all duration-500"
                style={{ width: `${Math.min(fundingPercentage, 100)}%` }}
              ></div>
            </div>

            <div className="grid grid-cols-3 gap-4 text-center">
              <div>
                <div className="text-2xl font-bold text-gray-900">
                  {formatCurrency(displayDRO.raised_amount || 0)}
                </div>
                <div className="text-sm text-gray-600">Raised</div>
              </div>
              <div>
                <div className="text-2xl font-bold text-sirex-blue">
                  {formatCurrency(displayDRO.target_amount)}
                </div>
