import React, { useState, useEffect } from 'react';
import type { DRO } from '../services/api';
import { droAPI } from '../services/api';
import DROCard from './DROCard';
import DROFilters from './DROFilters';
import InvestmentModal from './InvestmentModal';
import DRODetailModal from './DRODetailModal';
import { Loader2, AlertCircle, ChevronLeft, ChevronRight } from 'lucide-react';

interface FilterValues {
  search: string;
  industry: string;
  min_investment: string;
  max_investment: string;
  min_return: string;
  max_return: string;
  status: string;
}

const DROList: React.FC = () => {
  const [dros, setDros] = useState<DRO[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [selectedDRO, setSelectedDRO] = useState<DRO | null>(null);
  const [showInvestmentModal, setShowInvestmentModal] = useState(false);
  const [showDetailModal, setShowDetailModal] = useState(false);
  const [filters, setFilters] = useState<FilterValues>({
    search: '',
    industry: '',
    min_investment: '',
    max_investment: '',
    min_return: '',
    max_return: '',
    status: 'active',
  });
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 12,
    total: 0,
  });

  // Fetch DROs
  const fetchDROs = async () => {
    try {
      setLoading(true);
      setError(null);

      // Convert filter values to API format
      const apiFilters: any = {
        page: pagination.page,
        limit: pagination.limit,
      };

      if (filters.search) apiFilters.search = filters.search;
      if (filters.industry) apiFilters.industry = filters.industry;
      if (filters.status) apiFilters.status = filters.status;
      if (filters.min_investment) apiFilters.min_investment = parseFloat(filters.min_investment);
      if (filters.max_investment) apiFilters.max_investment = parseFloat(filters.max_investment);
      if (filters.min_return) apiFilters.min_return = parseFloat(filters.min_return);
      if (filters.max_return) apiFilters.max_return = parseFloat(filters.max_return);

      const response = await droAPI.getDROs(apiFilters);
      setDros(response.dros);
      setPagination(prev => ({ ...prev, total: response.dros.length }));
    } catch (err) {
      console.error('Error fetching DROs:', err);
      setError('Failed to load DROs. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  // Effect to fetch DROs when filters or pagination change
  useEffect(() => {
    const debounceTimer = setTimeout(() => {
      fetchDROs();
    }, 500); // Debounce search

    return () => clearTimeout(debounceTimer);
  }, [filters, pagination.page]);

  // Handle filter changes
  const handleFiltersChange = (newFilters: FilterValues) => {
    setFilters(newFilters);
    setPagination(prev => ({ ...prev, page: 1 })); // Reset to first page
  };

  // Handle investment
  const handleInvest = (dro: DRO) => {
    setSelectedDRO(dro);
    setShowInvestmentModal(true);
  };

  // Handle view details
  const handleViewDetails = (dro: DRO) => {
    setSelectedDRO(dro);
    setShowDetailModal(true);
  };

  // Handle successful investment
  const handleInvestmentSuccess = () => {
    setShowInvestmentModal(false);
    setSelectedDRO(null);
    fetchDROs(); // Refresh the list
  };

  // Handle pagination
  const handlePageChange = (newPage: number) => {
    if (newPage >= 1 && newPage <= Math.ceil(pagination.total / pagination.limit)) {
      setPagination(prev => ({ ...prev, page: newPage }));
      window.scrollTo({ top: 0, behavior: 'smooth' });
    }
  };

  // Calculate pagination info
  const totalPages = Math.ceil(pagination.total / pagination.limit);
  const startItem = (pagination.page - 1) * pagination.limit + 1;
  const endItem = Math.min(pagination.page * pagination.limit, pagination.total);

  if (loading && dros.length === 0) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center">
          <Loader2 className="w-12 h-12 text-sirex-blue animate-spin mx-auto mb-4" />
          <p className="text-gray-600 text-lg">Loading investment opportunities...</p>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="min-h-screen bg-gray-50 flex items-center justify-center">
        <div className="text-center max-w-md mx-auto">
          <AlertCircle className="w-12 h-12 text-red-500 mx-auto mb-4" />
          <h2 className="text-xl font-semibold text-gray-900 mb-2">Error Loading DROs</h2>
          <p className="text-gray-600 mb-4">{error}</p>
          <button
            onClick={fetchDROs}
            className="btn-primary"
          >
            Try Again
          </button>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            Investment Opportunities
          </h1>
          <p className="text-gray-600">
            Discover and invest in Daily Revenue Obligations from growing businesses
          </p>
        </div>

        {/* Filters */}
        <div className="mb-8">
          <DROFilters onFiltersChange={handleFiltersChange} isLoading={loading} />
        </div>

        {/* Results Header */}
        <div className="flex items-center justify-between mb-6">
          <div className="text-sm text-gray-600">
            {dros.length > 0 ? (
              <>
                Showing {startItem}-{endItem} of {pagination.total} opportunities
                {filters.search && ` for "${filters.search}"`}
              </>
            ) : (
              'No opportunities found'
            )}
          </div>

          {loading && (
            <div className="flex items-center text-sm text-gray-500">
              <Loader2 className="w-4 h-4 animate-spin mr-2" />
              Updating...
            </div>
          )}
        </div>

        {/* DRO Grid */}
        {dros.length > 0 ? (
          <>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
              {dros.map((dro) => (
                <DROCard
                  key={dro.id}
                  dro={dro}
                  onInvest={handleInvest}
                  onViewDetails={handleViewDetails}
                />
              ))}
            </div>

            {/* Pagination */}
            {totalPages > 1 && (
              <div className="flex items-center justify-between border-t border-gray-200 bg-white px-4 py-3 sm:px-6 rounded-lg">
                <div className="flex flex-1 justify-between sm:hidden">
                  <button
                    onClick={() => handlePageChange(pagination.page - 1)}
                    disabled={pagination.page === 1}
                    className="relative inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Previous
                  </button>
                  <button
                    onClick={() => handlePageChange(pagination.page + 1)}
                    disabled={pagination.page === totalPages}
                    className="relative ml-3 inline-flex items-center rounded-md border border-gray-300 bg-white px-4 py-2 text-sm font-medium text-gray-700 hover:bg-gray-50 disabled:opacity-50 disabled:cursor-not-allowed"
                  >
                    Next
                  </button>
                </div>

                <div className="hidden sm:flex sm:flex-1 sm:items-center sm:justify-between">
                  <div>
                    <p className="text-sm text-gray-700">
                      Showing page <span className="font-medium">{pagination.page}</span> of{' '}
                      <span className="font-medium">{totalPages}</span>
                    </p>
                  </div>
                  <div>
                    <nav className="isolate inline-flex -space-x-px rounded-md shadow-sm" aria-label="Pagination">
                      <button
                        onClick={() => handlePageChange(pagination.page - 1)}
                        disabled={pagination.page === 1}
                        className="relative inline-flex items-center rounded-l-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0 disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        <ChevronLeft className="h-5 w-5" />
                      </button>

                      {/* Page numbers */}
                      {Array.from({ length: Math.min(5, totalPages) }, (_, i) => {
                        const pageNum = Math.max(1, Math.min(totalPages - 4, pagination.page - 2)) + i;
                        if (pageNum <= totalPages) {
                          return (
                            <button
                              key={pageNum}
                              onClick={() => handlePageChange(pageNum)}
                              className={`relative inline-flex items-center px-4 py-2 text-sm font-semibold ${
                                pageNum === pagination.page
                                  ? 'z-10 bg-sirex-blue text-white focus-visible:outline focus-visible:outline-2 focus-visible:outline-offset-2 focus-visible:outline-sirex-blue'
                                  : 'text-gray-900 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0'
                              }`}
                            >
                              {pageNum}
                            </button>
                          );
                        }
                        return null;
                      })}

                      <button
                        onClick={() => handlePageChange(pagination.page + 1)}
                        disabled={pagination.page === totalPages}
                        className="relative inline-flex items-center rounded-r-md px-2 py-2 text-gray-400 ring-1 ring-inset ring-gray-300 hover:bg-gray-50 focus:z-20 focus:outline-offset-0 disabled:opacity-50 disabled:cursor-not-allowed"
                      >
                        <ChevronRight className="h-5 w-5" />
                      </button>
                    </nav>
                  </div>
                </div>
              </div>
            )}
          </>
        ) : (
          !loading && (
            <div className="text-center py-16">
              <div className="text-6xl mb-4">ğŸ”</div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">No opportunities found</h3>
              <p className="text-gray-600 mb-6">
                Try adjusting your search criteria or filters to find more investment opportunities.
              </p>
              <button
                onClick={() => handleFiltersChange({
                  search: '',
                  industry: '',
                  min_investment: '',
                  max_investment: '',
                  min_return: '',
                  max_return: '',
                  status: 'active',
                })}
                className="btn-secondary"
              >
                Clear All Filters
              </button>
            </div>
          )
        )}
      </div>

      {/* Modals */}
      {showInvestmentModal && selectedDRO && (
        <InvestmentModal
          dro={selectedDRO}
          onClose={() => {
            setShowInvestmentModal(false);
            setSelectedDRO(null);
          }}
          onSuccess={handleInvestmentSuccess}
        />
      )}

      {showDetailModal && selectedDRO && (
        <DRODetailModal
          dro={selectedDRO}
          onClose={() => {
            setShowDetailModal(false);
            setSelectedDRO(null);
          }}
          onInvest={handleInvest}
        />
      )}
    </div>
  );
};

export default DROList;

