toolUseResult.originalFileContents:
import React, { useState } from 'react';
import { Search, Filter, X } from 'lucide-react';

interface FilterValues {
  search: string;
  industry: string;
  min_investment: string;
  max_investment: string;
  min_return: string;
  max_return: string;
  status: string;
}

interface DROFiltersProps {
  onFiltersChange: (filters: FilterValues) => void;
  isLoading?: boolean;
}

const DROFilters: React.FC<DROFiltersProps> = ({ onFiltersChange, isLoading = false }) => {
  const [filters, setFilters] = useState<FilterValues>({
    search: '',
    industry: '',
    min_investment: '',
    max_investment: '',
    min_return: '',
    max_return: '',
    status: 'active',
  });

  const [showAdvanced, setShowAdvanced] = useState(false);

  const industries = [
    'Technology',
    'Food & Beverage',
    'Retail',
    'Healthcare',
    'Education',
    'Manufacturing',
    'Services',
    'Agriculture',
    'Real Estate',
    'Entertainment',
    'Other',
  ];

  const statuses = [
    { value: '', label: 'All Status' },
    { value: 'active', label: 'Active' },
    { value: 'pending', label: 'Pending' },
    { value: 'completed', label: 'Completed' },
  ];

  const handleFilterChange = (key: keyof FilterValues, value: string) => {
    const newFilters = { ...filters, [key]: value };
    setFilters(newFilters);
    onFiltersChange(newFilters);
  };

  const clearFilters = () => {
    const clearedFilters: FilterValues = {
      search: '',
      industry: '',
      min_investment: '',
      max_investment: '',
      min_return: '',
      max_return: '',
      status: 'active',
    };
    setFilters(clearedFilters);
    onFiltersChange(clearedFilters);
  };

  const hasActiveFilters = () => {
    return filters.search || 
           filters.industry || 
           filters.min_investment || 
           filters.max_investment || 
           filters.min_return || 
           filters.max_return;
  };

  return (
    <div className="bg-white rounded-lg shadow-lg border border-gray-200">
      {/* Search Bar */}
      <div className="p-6 border-b border-gray-200">
        <div className="relative">
          <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
          <input
            type="text"
            placeholder="Search DROs by title, description, or company..."
            value={filters.search}
            onChange={(e) => handleFilterChange('search', e.target.value)}
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-sirex-blue focus:border-transparent"
            disabled={isLoading}
          />
        </div>
      </div>

      {/* Quick Filters */}
      <div className="p-6">
        <div className="flex items-center justify-between mb-4">
          <h3 className="text-lg font-semibold text-gray-900">Filters</h3>
          <div className="flex items-center space-x-2">
            {hasActiveFilters() && (
              <button
                onClick={clearFilters}
                className="flex items-center text-sm text-gray-500 hover:text-gray-700"
                disabled={isLoading}
              >
                <X className="w-4 h-4 mr-1" />
                Clear
              </button>
            )}
            <button
              onClick={() => setShowAdvanced(!showAdvanced)}
              className="flex items-center text-sm text-sirex-blue hover:text-sirex-light-blue"
              disabled={isLoading}
            >
              <Filter className="w-4 h-4 mr-1" />
              {showAdvanced ? 'Simple' : 'Advanced'}
            </button>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {/* Industry Filter */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Industry
            </label>
            <select
              value={filters.industry}
              onChange={(e) => handleFilterChange('industry', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-sirex-blue focus:border-transparent"
              disabled={isLoading}
            >
              <option value="">All Industries</option>
              {industries.map((industry) => (
                <option key={industry} value={industry}>
                  {industry}
                </option>
              ))}
            </select>
          </div>

          {/* Status Filter */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Status
            </label>
            <select
              value={filters.status}
              onChange={(e) => handleFilterChange('status', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-sirex-blue focus:border-transparent"
              disabled={isLoading}
            >
              {statuses.map((status) => (
                <option key={status.value} value={status.value}>
                  {status.label}
                </option>
              ))}
            </select>
          </div>

          {/* Investment Range */}
          <div>
            <label className="block text-sm font-medium text-gray-700 mb-2">
              Min Investment
            </label>
            <input
              type="number"
              placeholder="e.g., 1000"
              value={filters.min_investment}
              onChange={(e) => handleFilterChange('min_investment', e.target.value)}
              className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-sirex-blue focus:border-transparent"
              disabled={isLoading}
            />
          </div>
        </div>

        {/* Advanced Filters */}
        {showAdvanced && (
          <div className="mt-6 pt-6 border-t border-gray-200">
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Max Investment
                </label>
                <input
                  type="number"
                  placeholder="e.g., 50000"
                  value={filters.max_investment}
                  onChange={(e) => handleFilterChange('max_investment', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-sirex-blue focus:border-transparent"
                  disabled={isLoading}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Min Return %
                </label>
                <input
                  type="number"
                  placeholder="e.g., 5"
                  value={filters.min_return}
                  onChange={(e) => handleFilterChange('min_return', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-sirex-blue focus:border-transparent"
                  disabled={isLoading}
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-gray-700 mb-2">
                  Max Return %
                </label>
                <input
                  type="number"
                  placeholder="e.g., 20"
                  value={filters.max_return}
                  onChange={(e) => handleFilterChange('max_return', e.target.value)}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-sirex-blue focus:border-transparent"
                  disabled={isLoading}
                />
              </div>
            </div>
          </div>
        )}

        {/* Active Filters Display */}
        {hasActiveFilters() && (
          <div className="mt-4 pt-4 border-t border-gray-200">
            <div className="flex flex-wrap gap-2">
              {filters.search && (
                <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-sirex-blue text-white">
                  Search: "{filters.search}"
                  <button
                    onClick={() => handleFilterChange('search', '')}
                    className="ml-2 hover:text-gray-200"
                  >
                    <X className="w-3 h-3" />
                  </button>
                </span>
              )}

              {filters.industry && (
                <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-sirex-blue text-white">
                  Industry: {filters.industry}
                  <button
                    onClick={() => handleFilterChange('industry', '')}
                    className="ml-2 hover:text-gray-200"
                  >
                    <X className="w-3 h-3" />
                  </button>
                </span>
              )}

              {filters.min_investment && (
                <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-sirex-blue text-white">
                  Min: ${parseInt(filters.min_investment).toLocaleString()}
                  <button
                    onClick={() => handleFilterChange('min_investment', '')}
                    className="ml-2 hover:text-gray-200"
                  >
                    <X className="w-3 h-3" />
                  </button>
                </span>
              )}

              {filters.max_investment && (
                <span className="inline-flex items-center px-3 py-1 rounded-full text-xs font-medium bg-sirex-blue text-white">
                  Max: ${parseInt(filters.max_investment).toLocaleString()}
                  <button
                    onClick={() => handleFilterChange('max_investment', '')}
                    className="ml-2 hover:text-gray-200"
                  >
                    <X className="w-3 h-3" />
                  </button>
                </span>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default DROFilters;

