  onFiltersChange: (filters: FilterValues) => void;
  isLoading?: boolean;
}

const DROFilters: React.FC<DROFiltersProps> = ({ onFiltersChange, isLoading = false }) => {
  const [filters, setFilters] = useState<FilterValues>({
    search: '',
    industry: '',
    min_investment: '1000',
    max_investment: '50000',
    min_return: '5',
    max_return: '20',
    status: 'active',
  });


  const industries = [
    'Technology',
    'Food & Beverage',
    'Retail',
    'Healthcare',
    'Education',
    'Manufacturing',
    'Services',
    'Agriculture',
    'Real Estate',
    'Entertainment',
    'Other',
  ];

  const statuses = [
    { value: '', label: 'All Status' },
    { value: 'active', label: 'Active' },
    { value: 'pending', label: 'Pending' },
    { value: 'completed', label: 'Completed' },
  ];

  const handleFilterChange = (key: keyof FilterValues, value: string) => {
    const newFilters = { ...filters, [key]: value };
    setFilters(newFilters);
    onFiltersChange(newFilters);
  };

  const clearFilters = () => {
    const clearedFilters: FilterValues = {
      search: '',
      industry: '',
      min_investment: '1000',
      max_investment: '50000',
      min_return: '5',
      max_return: '20',
      status: 'active',
    };
    setFilters(clearedFilters);
    onFiltersChange(clearedFilters);
  };

  const hasActiveFilters = () => {
    return filters.search || 
           filters.industry || 
           filters.min_investment || 
           filters.max_investment || 
           filters.min_return || 
           filters.max_return;
  };

  const getIndustryIcon = (industry: string) => {
    switch (industry) {
      case 'Technology': return <Zap className="w-5 h-5" />;
      case 'Food & Beverage': return <span className="text-lg">ğŸ½ï¸</span>;
      case 'Retail': return <span className="text-lg">ğŸ›ï¸</span>;
      case 'Healthcare': return <Shield className="w-5 h-5" />;
      case 'Education': return <span className="text-lg">ğŸ“š</span>;
      case 'Manufacturing': return <span className="text-lg">ğŸ­</span>;
      case 'Services': return <span className="text-lg">âš™ï¸</span>;
      case 'Agriculture': return <span className="text-lg">ğŸŒ¾</span>;
      case 'Real Estate': return <span className="text-lg">ğŸ¢</span>;
      case 'Entertainment': return <span className="text-lg">ğŸ­</span>;
      default: return <Building2 className="w-5 h-5" />;
    }
  };

  return (
    <div className="space-y-8">
      {/* Search Bar */}
      <div className="glass-card p-6">
        <div className="relative">
          <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-slate-400 w-6 h-6" />
          <input
            type="text"
            placeholder="Search investment opportunities by company, industry, or description..."
            value={filters.search}
            onChange={(e) => handleFilterChange('search', e.target.value)}
            className="w-full pl-12 pr-6 py-4 bg-white/10 border border-white/20 rounded-xl text-white placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 text-lg backdrop-blur-sm"
            disabled={isLoading}
          />
        </div>
      </div>

      {/* Filter Header */}
      <div className="flex items-center justify-between">
        <div>
          <h2 className="text-2xl font-bold text-white mb-2">Filter Opportunities</h2>
          <p className="text-slate-300">Find investments that match your criteria</p>
        </div>
        <div className="flex items-center space-x-4">
          {hasActiveFilters() && (
            <button
              onClick={clearFilters}
              className="btn-crypto-secondary flex items-center space-x-2"
              disabled={isLoading}
            >
              <X className="w-4 h-4" />
              <span>Clear All</span>
            </button>
          )}
        </div>
      </div>

      {/* Industry Cards */}
      <div className="glass-card p-6">
        <h3 className="text-xl font-bold text-white mb-6 flex items-center">
          <Building2 className="w-6 h-6 mr-3 text-primary-400" />
          Industry Sectors
        </h3>
        <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 xl:grid-cols-6 gap-3">
          <button
            onClick={() => handleFilterChange('industry', '')}
            className={`p-4 rounded-xl transition-all duration-200 border ${
              !filters.industry
                ? 'bg-gradient-primary text-white border-primary-500 shadow-crypto'
                : 'bg-white/5 text-slate-300 border-white/20 hover:bg-white/10 hover:text-white'
            }`}
            disabled={isLoading}
          >
            <div className="flex flex-col items-center space-y-2">
              <span className="text-lg">ğŸŒ</span>
              <span className="text-sm font-medium">All</span>
            </div>
          </button>
          {industries.map((industry) => (
            <button
              key={industry}
              onClick={() => handleFilterChange('industry', industry)}
              className={`p-4 rounded-xl transition-all duration-200 border ${
                filters.industry === industry
                  ? 'bg-gradient-primary text-white border-primary-500 shadow-crypto'
                  : 'bg-white/5 text-slate-300 border-white/20 hover:bg-white/10 hover:text-white'
              }`}
              disabled={isLoading}
            >
              <div className="flex flex-col items-center space-y-2">
                <div className="text-primary-400">{getIndustryIcon(industry)}</div>
                <span className="text-sm font-medium text-center leading-tight">{industry}</span>
              </div>
            </button>
          ))}
        </div>
      </div>

      {/* Status and Investment Range */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Status Cards */}
        <div className="glass-card p-6">
          <h3 className="text-xl font-bold text-white mb-6 flex items-center">
            <TrendingUp className="w-6 h-6 mr-3 text-accent-green" />
            Investment Status
          </h3>
          <div className="grid grid-cols-2 gap-3">
            {statuses.map((status) => (
              <button
                key={status.value}
                onClick={() => handleFilterChange('status', status.value)}
                className={`p-4 rounded-xl transition-all duration-200 border ${
                  filters.status === status.value
                    ? 'bg-gradient-primary text-white border-primary-500 shadow-crypto'
                    : 'bg-white/5 text-slate-300 border-white/20 hover:bg-white/10 hover:text-white'
                }`}
                disabled={isLoading}
              >
                <div className="flex flex-col items-center space-y-2">
                  <div className={`w-3 h-3 rounded-full ${
                    status.value === 'active' ? 'bg-green-500' :
                    status.value === 'pending' ? 'bg-yellow-500' :
                    status.value === 'completed' ? 'bg-blue-500' : 'bg-gray-500'
                  }`}></div>
                  <span className="text-sm font-medium">{status.label}</span>
                </div>
              </button>
            ))}
          </div>
        </div>

        {/* Investment Range */}
        <div className="glass-card p-6">
          <h3 className="text-xl font-bold text-white mb-6 flex items-center">
            <DollarSign className="w-6 h-6 mr-3 text-accent-gold" />
            Investment Range
          </h3>
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">
                Minimum Investment
              </label>
              <input
                type="number"
                placeholder="e.g., 1,000"
                value={filters.min_investment}
                onChange={(e) => handleFilterChange('min_investment', e.target.value)}
                className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-xl text-white placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm"
                disabled={isLoading}
              />
            </div>
            <div>
              <label className="block text-sm font-medium text-slate-300 mb-2">
                Maximum Investment
              </label>
              <input
                type="number"
                placeholder="e.g., 50,000"
                value={filters.max_investment}
                onChange={(e) => handleFilterChange('max_investment', e.target.value)}
                className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-xl text-white placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm"
                disabled={isLoading}
              />
            </div>
          </div>
        </div>
      </div>

      {/* Return Range */}
      <div className="glass-card p-6">
        <h3 className="text-xl font-bold text-white mb-6 flex items-center">
          <TrendingUp className="w-6 h-6 mr-3 text-accent-green" />
          Expected Returns
        </h3>
        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-slate-300 mb-2">
              Minimum Return %
            </label>
            <input
              type="number"
              placeholder="e.g., 5"
              value={filters.min_return}
              onChange={(e) => handleFilterChange('min_return', e.target.value)}
              className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-xl text-white placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm"
              disabled={isLoading}
            />
          </div>
          <div>
            <label className="block text-sm font-medium text-slate-300 mb-2">
              Maximum Return %
            </label>
            <input
              type="number"
              placeholder="e.g., 20"
              value={filters.max_return}
              onChange={(e) => handleFilterChange('max_return', e.target.value)}
              className="w-full px-4 py-3 bg-white/10 border border-white/20 rounded-xl text-white placeholder-slate-400 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-primary-500 backdrop-blur-sm"
              disabled={isLoading}
            />
          </div>
        </div>
      </div>

      {/* Active Filters Display */}
      {hasActiveFilters() && (
        <div className="glass-card p-6">
          <h3 className="text-lg font-bold text-white mb-4">Active Filters</h3>
          <div className="flex flex-wrap gap-3">
            {filters.search && (
              <span className="inline-flex items-center px-4 py-2 rounded-full text-sm font-medium bg-gradient-primary text-white shadow-crypto">
                Search: "{filters.search}"
                <button
                  onClick={() => handleFilterChange('search', '')}
                  className="ml-3 hover:text-gray-200 transition-colors"
                >
                  <X className="w-4 h-4" />
                </button>
              </span>
            )}

            {filters.industry && (
              <span className="inline-flex items-center px-4 py-2 rounded-full text-sm font-medium bg-gradient-primary text-white shadow-crypto">
                Industry: {filters.industry}
                <button
                  onClick={() => handleFilterChange('industry', '')}
                  className="ml-3 hover:text-gray-200 transition-colors"
                >
                  <X className="w-4 h-4" />
                </button>
              </span>
            )}

            {filters.min_investment && (
              <span className="inline-flex items-center px-4 py-2 rounded-full text-sm font-medium bg-gradient-primary text-white shadow-crypto">
                Min: ${parseInt(filters.min_investment).toLocaleString()}
                <button
                  onClick={() => handleFilterChange('min_investment', '')}
                  className="ml-3 hover:text-gray-200 transition-colors"
                >
                  <X className="w-4 h-4" />
                </button>
              </span>
            )}

            {filters.max_investment && (
              <span className="inline-flex items-center px-4 py-2 rounded-full text-sm font-medium bg-gradient-primary text-white shadow-crypto">
                Max: ${parseInt(filters.max_investment).toLocaleString()}
                <button
                  onClick={() => handleFilterChange('max_investment', '')}
                  className="ml-3 hover:text-gray-200 transition-colors"
                >
                  <X className="w-4 h-4" />
                </button>
              </span>
            )}

            {filters.min_return && (
              <span className="inline-flex items-center px-4 py-2 rounded-full text-sm font-medium bg-gradient-primary text-white shadow-crypto">
                Min Return: {filters.min_return}%
                <button
                  onClick={() => handleFilterChange('min_return', '')}
                  className="ml-3 hover:text-gray-200 transition-colors"
                >
                  <X className="w-4 h-4" />
                </button>
              </span>
            )}

            {filters.max_return && (
              <span className="inline-flex items-center px-4 py-2 rounded-full text-sm font-medium bg-gradient-primary text-white shadow-crypto">
                Max Return: {filters.max_return}%
                <button
                  onClick={() => handleFilterChange('max_return', '')}
                  className="ml-3 hover:text-gray-200 transition-colors"
                >
                  <X className="w-4 h-4" />
                </button>
              </span>
            )}
          </div>
        </div>
      )}
    </div>
  );
};

export default DROFilters;

