import { 
  mockDROs, 
  mockInvestments, 
  mockPayouts, 
  mockUser, 
  mockPlatformStats,
  useMockData 
} from '../data/mockData';
import type { DRO, Investment, User } from './api';

// Demo service that provides mock data when in demo mode
export class DemoService {
  static isDemoMode(): boolean {
    return useMockData() || localStorage.getItem('sirex_demo_mode') === 'true';
  }

  static enableDemoMode(): void {
    localStorage.setItem('sirex_demo_mode', 'true');
  }

  static disableDemoMode(): void {
    localStorage.removeItem('sirex_demo_mode');
  }

  // DRO-related demo methods
  static async getDROs(filters?: {
    industry?: string;
    min_investment?: number;
    max_investment?: number;
    min_return?: number;
    max_return?: number;
    status?: string;
    search?: string;
    page?: number;
    limit?: number;
  }): Promise<{
    dros: DRO[];
    pagination: { page: number; limit: number; total: number };
    filters: any;
  }> {
    // Simulate API delay
    await new Promise(resolve => setTimeout(resolve, 300));

    let filteredDROs = [...mockDROs];

    // Apply filters
    if (filters) {
      if (filters.industry) {
        filteredDROs = filteredDROs.filter(dro => 
          dro.industry.toLowerCase().includes(filters.industry!.toLowerCase())
        );
      }
      if (filters.status) {
        filteredDROs = filteredDROs.filter(dro => dro.status === filters.status);
      }
      if (filters.search) {
        const searchTerm = filters.search.toLowerCase();
        filteredDROs = filteredDROs.filter(dro => 
          dro.title.toLowerCase().includes(searchTerm) ||
          dro.description.toLowerCase().includes(searchTerm) ||
          dro.msme_name.toLowerCase().includes(searchTerm)
        );
      }
      if (filters.min_investment) {
        filteredDROs = filteredDROs.filter(dro => 
          dro.minimum_investment >= filters.min_investment!
        );
      }
      if (filters.max_investment) {
        filteredDROs = filteredDROs.filter(dro => 
          dro.minimum_investment <= filters.max_investment!
        );
      }
      if (filters.min_return) {
        filteredDROs = filteredDROs.filter(dro => 
          dro.revenue_percentage >= filters.min_return!
        );
      }
      if (filters.max_return) {
        filteredDROs = filteredDROs.filter(dro => 
          dro.revenue_percentage <= filters.max_return!
        );
      }
    }

    const page = filters?.page || 1;
    const limit = filters?.limit || 12;
    const startIndex = (page - 1) * limit;
    const endIndex = startIndex + limit;
    const paginatedDROs = filteredDROs.slice(startIndex, endIndex);

    return {
      dros: paginatedDROs,
      pagination: {
        page,
        limit,
        total: filteredDROs.length
      },
      filters: filters || {}
    };
  }

  static async getDRO(id: number): Promise<{
    dro: DRO;
    investments: Investment[];
  }> {
    await new Promise(resolve => setTimeout(resolve, 200));

    const dro = mockDROs.find(d => d.id === id);
    if (!dro) {
      throw new Error('DRO not found');
    }

    const investments = mockInvestments.filter(inv => inv.dro_id === id);

    return { dro, investments };
  }

  static async getMyInvestments(): Promise<{
    total_invested: number;
    active_investments: number;
    total_return_earned: number;
    investments: Investment[];
  }> {
    await new Promise(resolve => setTimeout(resolve, 400));

    const totalInvested = mockInvestments.reduce((sum, inv) => sum + inv.amount, 0);
    const totalReturns = mockInvestments.reduce((sum, inv) => sum + inv.total_returns, 0);

    return {
      total_invested: totalInvested,
      active_investments: mockInvestments.filter(inv => inv.status === 'active').length,
      total_return_earned: totalReturns,
      investments: mockInvestments
    };
  }

  static async getMyPayouts(page?: number, limit?: number): Promise<{
    payouts: any[];
    pagination: { page: number; limit: number; total: number };
    summary: {
      total_payouts: number;
      total_amount: number;
      pending_amount: number;
      completed_amount: number;
    };
  }> {
    await new Promise(resolve => setTimeout(resolve, 300));

    const currentPage = page || 1;
    const pageLimit = limit || 10;
    const startIndex = (currentPage - 1) * pageLimit;
    const endIndex = startIndex + pageLimit;
    const paginatedPayouts = mockPayouts.slice(startIndex, endIndex);

    const totalAmount = mockPayouts.reduce((sum, payout) => sum + payout.payout_amount, 0);
    const completedAmount = mockPayouts
      .filter(p => p.status === 'completed')
      .reduce((sum, payout) => sum + payout.payout_amount, 0);
    const pendingAmount = mockPayouts
      .filter(p => p.status === 'pending')
      .reduce((sum, payout) => sum + payout.payout_amount, 0);

    return {
      payouts: paginatedPayouts,
      pagination: {
        page: currentPage,
        limit: pageLimit,
        total: mockPayouts.length
      },
      summary: {
        total_payouts: mockPayouts.length,
        total_amount: totalAmount,
        pending_amount: pendingAmount,
        completed_amount: completedAmount
      }
    };
  }

  static async getPerformance(): Promise<{
    performance: {
      total_invested: number;
      total_returns: number;
      net_profit: number;
      roi_percentage: number;
      active_investments: number;
      completed_payouts: number;
      pending_payouts: number;
      average_monthly_return: number;
    };
  }> {
    await new Promise(resolve => setTimeout(resolve, 250));

    const totalInvested = mockUser.total_invested;
    const totalReturns = mockUser.total_returns;
    const netProfit = totalReturns;
    const roiPercentage = (totalReturns / totalInvested) * 100;
    const completedPayouts = mockPayouts.filter(p => p.status === 'completed').length;
    const pendingPayouts = mockPayouts.filter(p => p.status === 'pending').length;
    const averageMonthlyReturn = totalReturns / 4; // Assuming 4 months of activity

    return {
      performance: {
        total_invested: totalInvested,
        total_returns: totalReturns,
        net_profit: netProfit,
        roi_percentage: roiPercentage,
        active_investments: mockUser.active_investments,
        completed_payouts: completedPayouts,
        pending_payouts: pendingPayouts,
        average_monthly_return: averageMonthlyReturn
      }
    };
  }

  static getCurrentUser(): User | null {
    if (this.isDemoMode()) {
      return mockUser;
    }
    return null;
  }

  static async investInDRO(droId: number, amount: number): Promise<{
    message: string;
    investment: Investment;
  }> {
    await new Promise(resolve => setTimeout(resolve, 1000)); // Simulate processing

    const dro = mockDROs.find(d => d.id === droId);
    if (!dro) {
      throw new Error('DRO not found');
    }

    const newInvestment: Investment = {
      id: mockInvestments.length + 1,
      investor_id: mockUser.id,
      dro_id: droId,
      amount: amount,
      investment_date: new Date().toISOString(),
      status: 'active',
      dro_title: dro.title,
      dro_industry: dro.industry,
      dro_revenue_percentage: dro.revenue_percentage
    };

    // Update mock data
    mockInvestments.push(newInvestment);
    dro.raised_amount += amount;
    dro.investor_count = (dro.investor_count || 0) + 1;
    dro.funding_percentage = (dro.raised_amount / dro.target_amount) * 100;

    return {
      message: 'Investment successful!',
      investment: newInvestment
    };
  }

  static getPlatformStats() {
    return mockPlatformStats;
  }

  // Authentication demo methods
  static async demoLogin(email: string, password: string): Promise<{
    message: string;
    user: User;
    tokens: {
      access_token: string;
      refresh_token: string;
      token_type: string;
    };
  }> {
    await new Promise(resolve => setTimeout(resolve, 800));

    // For demo, accept any email/password
    this.enableDemoMode();

    return {
      message: 'Login successful',
      user: mockUser,
      tokens: {
        access_token: 'demo_access_token_' + Date.now(),
        refresh_token: 'demo_refresh_token_' + Date.now(),
        token_type: 'Bearer'
      }
    };
  }

  static async demoRegister(userData: any): Promise<{
    message: string;
    user: User;
    tokens: {
      access_token: string;
      refresh_token: string;
      token_type: string;
    };
  }> {
    await new Promise(resolve => setTimeout(resolve, 1200));

    this.enableDemoMode();

    const newUser: User = {
      id: Date.now(),
      email: userData.email,
      name: userData.name || `${userData.first_name} ${userData.last_name}`,
      first_name: userData.first_name,
      last_name: userData.last_name,
      user_type: userData.user_type,
      kyc_status: 'approved',
      is_verified: true
    };

    return {
      message: 'Registration successful',
      user: newUser,
      tokens: {
        access_token: 'demo_access_token_' + Date.now(),
        refresh_token: 'demo_refresh_token_' + Date.now(),
        token_type: 'Bearer'
      }
    };
  }
}

// Export demo mode checker
export const isDemoMode = () => DemoService.isDemoMode();

