import React, { useState, useEffect } from 'react';
import { DemoService } from '../services/demoService';
import { Play, Pause, Database, Users } from 'lucide-react';

const DemoModeToggle: React.FC = () => {
  const [isDemoMode, setIsDemoMode] = useState(DemoService.isDemoMode());
  const [isVisible, setIsVisible] = useState(false);

  useEffect(() => {
    // Show toggle in development or when demo mode is enabled
    const shouldShow = process.env.NODE_ENV === 'development' || 
                       window.location.hostname.includes('demo') ||
                       window.location.search.includes('demo=true') ||
                       isDemoMode;
    setIsVisible(shouldShow);
  }, [isDemoMode]);

  const toggleDemoMode = () => {
    if (isDemoMode) {
      DemoService.disableDemoMode();
      setIsDemoMode(false);
      window.location.reload(); // Reload to clear demo data
    } else {
      DemoService.enableDemoMode();
      setIsDemoMode(true);
      window.location.reload(); // Reload to load demo data
    }
  };

  if (!isVisible) return null;

  return (
    <div className="fixed top-20 right-4 z-50">
      <div className="glass-card p-4 border-2 border-yellow-400/50 bg-yellow-50/10">
        <div className="flex items-center space-x-3 mb-3">
          <div className="p-2 rounded-lg bg-yellow-400/20">
            {isDemoMode ? (
              <Database className="w-5 h-5 text-yellow-400" />
            ) : (
              <Users className="w-5 h-5 text-yellow-400" />
            )}
          </div>
          <div>
            <div className="text-sm font-bold text-white">
              {isDemoMode ? 'Demo Mode' : 'Live Mode'}
            </div>
            <div className="text-xs text-yellow-200">
              {isDemoMode ? 'Using mock data' : 'Using real API'}
            </div>
          </div>
        </div>

        <button
          onClick={toggleDemoMode}
          className={`w-full flex items-center justify-center space-x-2 py-2 px-3 rounded-lg font-medium text-sm transition-all duration-200 ${
            isDemoMode
              ? 'bg-yellow-500 hover:bg-yellow-600 text-black'
              : 'bg-green-500 hover:bg-green-600 text-white'
          }`}
        >
          {isDemoMode ? (
            <>
              <Pause className="w-4 h-4" />
              <span>Exit Demo</span>
            </>
          ) : (
            <>
              <Play className="w-4 h-4" />
              <span>Start Demo</span>
            </>
          )}
        </button>

        {isDemoMode && (
          <div className="mt-3 text-xs text-yellow-200 text-center">
            Perfect for VC presentations!
          </div>
        )}
      </div>
    </div>
  );
};

export default DemoModeToggle;

