        />

        <div className="transition-all duration-500 ease-in-out">
          {renderCurrentView()}
        </div>
      </div>

      {/* Modals */}
      {showInvestmentModal && selectedDRO && (
        <InvestmentModal
          dro={selectedDRO}
          onClose={() => {
            setShowInvestmentModal(false);
            setSelectedDRO(null);
          }}
          onSuccess={handleInvestmentSuccess}
        />
      )}

      {showDetailModal && selectedDRO && (
        <DRODetailModal
          dro={selectedDRO}
          onClose={() => {
            setShowDetailModal(false);
            setSelectedDRO(null);
          }}
          onInvest={handleInvest}
        />
      )}
    </div>
  );
}

export default App

